    pos.x *= aspect+iMusic[1].z;

    float radius = length(pos)+iMusic[1].y;

    float x = radius*1.0 - iTime/(4.0+iMusic[0].y);
    float ringColor = mod(x, 1.0) * 10.0;

    vec3 finalColor;
    if (ringColor < 1.0) {
        finalColor = vec3(230,0,63)*(1.-iMusic[3].w)  / 255.0;
    } else if (ringColor < 2.0) {
        finalColor = vec3(255,0,0)*iMusic[3].z  / 255.0;
    } else if (ringColor < 3.0) {
        finalColor = vec3(255,81,0)*iMusic[3].y  / 255.0;
    } else if (ringColor < 4.0) {
        finalColor = vec3(255,138,0)*iMusic[3].x / 255.0;
    } else if (ringColor < 5.0) {
        finalColor = vec3(255,189,0)*iMusic[1].x / 255.0;
    } else if (ringColor < 6.0) {
        finalColor = vec3(241,236,0)*iMusic[0].x / 255.0;
    } else if (ringColor < 7.0) {
        finalColor = vec3(0,245,138)*iMusic[2].x / 255.0;
    } else if (ringColor < 8.0) {
        finalColor = vec3(0,206,190)*iMusic[2].y / 255.0;
    } else if (ringColor < 9.0) {
        finalColor = vec3(0,118,252)*iMusic[2].z / 255.0;
    } else if (ringColor < 10.0) {
        finalColor = vec3(140,24,195)*iMusic[2].w / 255.0;
    }

    float gradient = mod(ringColor, 1.0+0.5*iMusic[1].w);
