//if uv is in Circle or not
float IsInCircle(vec2 uv,vec2 center,float radius) {
	float r = distance(uv,center);
	if (r < radius) {
		return r / radius;
	}
	return -1.0;
}

float CaculateAngle(vec2 v1, vec2 v2) {
	float magnitude1 = sqrt(v1.x*v1.x + v1.y*v1.y);
	float magnitude2 = sqrt(v2.x*v2.x + v2.y*v2.y);
	float cosValue = dot(v1, v2) / (magnitude1*magnitude2);
	float finalAngle = acos(cosValue);
	finalAngle = degrees(finalAngle);

	vec3 dir = cross(vec3(v1, 0), vec3(v2, 0));
	if (dir.z >= 0.0) {
		return finalAngle;
	}
	else{
		return 360.0 - finalAngle;
	}
}

//计算点到直线的距离，offset用于平行线
float CaculatePointToAxis(vec2 uv, vec2 center, float slope, float offset) {
	float val = abs(slope*(uv.x - center.x) + offset - (uv.y - center.y)) / sqrt(1.0 + slope * slope);
	return val;
}

float IsInRect(vec2 uv, vec2 center, float angle, float width, float height) {

		if (angle == 90.0 || angle == 270.0) {
			float val1 = abs(uv.x + width * 0.5 - center.x);
			float val2 = abs(uv.x - width * 0.5 - center.x);
			float val3 = abs(uv.y + height * 0.5 - center.x);
			float val4 = abs(uv.y - height * 0.5 - center.x);

			if (val1 + val2 <= width + 0.001 && val3 + val4 <= height + 0.001) {
				return 1.0;
			}
			return 0.0;
		}
		if (angle == 0.0 || angle == 180.0) {
			float val1 = abs(uv.x + height * 0.5 - center.x);
			float val2 = abs(uv.x - height * 0.5 - center.x);
			float val3 = abs(uv.y + width * 0.5 - center.x);
			float val4 = abs(uv.y - width * 0.5 - center.x);

			if (val1 + val2 <= height + 0.001 && val3 + val4 <= width + 0.001) {
				return 1.0;
			}
			return 0.0;
		}
		angle = radians(angle);
		float slope = tan(angle);
		float offset1 = width * 0.5 * sqrt(1.0 + slope * slope);
		float val1 = CaculatePointToAxis(uv, center, slope, offset1);
		float val2 = CaculatePointToAxis(uv, center, slope, -offset1);

		slope = -1.0 / slope;
		float offset2 = height * 0.5 * sqrt(1.0 + slope * slope);
		float val3 = CaculatePointToAxis(uv, center, slope, offset2);
		float val4 = CaculatePointToAxis(uv, center, slope, -offset2);

		if (val1 + val2 <= width + 0.001 && val3 + val4 <= height + 0.001) {
			return 1.0;
		}
		return 0.0;
	}

float CalculateRadius(float r1,float r2,float r4) {
	float k1 = 1.0/ r1;
	float k2 = 1.0/ r2;
	float k4 = 1.0/ r4;

	float t1 = k1 + k2 - k4;
	float t2 = k1 * k1 + k2 * k2 + k4 * k4;

	float a = 1.0;
	float b = -2.0*t1;
	float c = 2.0 * t2 - t1 * t1;
	float delta = b * b - 4.0 * a*c;
	if (delta < 0.0) {
		return -1.0;
	}
	float d1 =( -b + sqrt(delta))/(2.0*a);
	//float d2 =（ -b - sqrt(delta)）/(2.0*a);
	return 1.0/d1;
}

vec4 CaculateFourthCircleCenter(vec2 center1,vec2 center2,float r1,float r2) {
	float m = -(center2.x-center1.x)/(center2.y-center1.y);
	float n = 0.5*((r2*r2-r1*r1)/(center1.y-center2.y)+center2.y+center1.y+(center1.x*center1.x-center2.x*center2.x)/(center1.y-center2.y));
	float a = 1.0 + m * m;
	float b = 2.0 * m*(n - center1.y) - 2.0*center1.x;
	float c = center1.x*center1.x + (n-center1.y)*(n-center1.y) - r1 * r1;
	float x1 = (-b + sqrt(b*b-4.0*a*c))/(2.0*a);
	float y1 = m * x1 + n;
	float x2 = (-b - sqrt(b*b - 4.0 * a*c)) / (2.0 * a);
	float y2 = m * x2 + n;

	vec3 v0 = vec3(center1.x-center2.x,center1.y-center2.y,0.0);
	vec3 v1 = vec3(center1.x-x1,center1.y-y1,0.0);
	vec3 t = cross(v0, v1);
	if ( t.z>= 0.0) {
		return vec4(x1,y1,x2,y2);
	}
	return vec4(x2,y2,x1,y1);

}

vec4 DrawCircle(vec2 uv,vec2 center,float radius,vec4 centerColor,vec4 contourCol) {
    float aspect = distance(uv,center)/radius;
    float alpha = aspect;
	float angle = CaculateAngle(vec2(1,0),uv-center);
    float ColorRotateSpeed=10.0;
	angle = mod(angle+ColorRotateSpeed*iTime,360.0);
	float index =floor(angle/20.0);
	//alpha = index * alpha;
	alpha *= abs(0.5-index / 18.0);
	return mix(centerColor,contourCol,alpha);
}

