    float radius2=0.35;
	R[1] = radius2+scale;
	//CircleCenter[1].x = CircleCenter[0].x - (R[0] - R[1]);
	//CircleCenter[1].y = CircleCenter[0].y;
	CircleCenter[1].x = CircleCenter[0].x - (R[0] - R[1])*cos(angle);
	CircleCenter[1].y = CircleCenter[0].y + (R[0] - R[1])*sin(angle);

	R[2] = R[0] - R[1]-0.0001;
	float l = (R[0]-R[1]) / (R[1]+R[2]);
	CircleCenter[2] = CircleCenter[1]-(1.0/l)*(CircleCenter[1]-CircleCenter[0]);

	vec4 CenterColor = vec4(0.7,0.9,0.9,1.0);
	vec4 ContourColor = vec4(0.8,0,0.4,1.0);

	vec4 centerColor;
    vec4 contourColor;




	//第一个内圆
	//float flag = IsInCircle(uv, CircleCenter[1], R[1]);
	if (IsInGear(uv, CircleCenter[1], R[1], centerColor, contourColor, ToothWidth, ToothDepth,  -rearSpeed) >= 0.5) {
		centerColor = mix(CenterColor, ContourColor, 0.7);
		contourColor = mix(ContourColor, CenterColor, 0.7);
		return DrawCircle(uv, CircleCenter[1],R[1], centerColor, contourColor);
	}

	//第二个内圆
	//flag = IsInCircle(uv, CircleCenter[2], R[2]);
	if (IsInGear(uv, CircleCenter[2], R[2], centerColor, contourColor, ToothWidth, ToothDepth,  -rearSpeed) >= 0.5) {
		centerColor = mix(CenterColor, ContourColor, 0.3);
		contourColor = mix(ContourColor, CenterColor, 0.3);
		return DrawCircle(uv, CircleCenter[2],R[2], centerColor, contourColor);
	}

	//第三个内圆
	float r = CalculateRadius(R[1], R[2], R[0]);

	if (r < 0.0) {
		return vec4(0, 0, 0, 1);
	}

	vec4 center4 = CaculateFourthCircleCenter(CircleCenter[1], CircleCenter[2], R[1] + r, R[2] + r);
	//center4.x*=aspect;
    //center4.z*=aspect;
    //float2 center4 = CaculateFourthCircleCenter(CircleCenter[0],float2(0.35,0.5), float2(0.85, 0.5), R[0], R[1] + r, R[2] + r);
	//flag = IsInCircle(uv, center4.xy, r);
	if (IsInGear(uv, center4.xy, r, centerColor, contourColor, ToothWidth, ToothDepth, -rearSpeed) >= 0.5) {
		//return fixed4(0,0,0,1);
		centerColor = mix(CenterColor, ContourColor, 0.4);
		contourColor = mix(ContourColor, CenterColor, 0.4);
		return DrawCircle(uv, center4.xy,r, centerColor, contourColor);
	}
    //return vec4(1,0,0.5,1);
		//flag = IsInCircle(uv, center4.zw, r);
		if (IsInGear(uv, center4.zw, r, centerColor, contourColor, ToothWidth, ToothDepth, -rearSpeed) >= 0.5) {
			//return fixed4(0,0,0,1);
			centerColor = mix(CenterColor, ContourColor, 0.4);
			contourColor = mix(ContourColor, CenterColor, 0.4);
			return DrawCircle(uv, center4.zw,r, centerColor, contourColor);
		}

		CircleCenter[3] = center4.xy;
		R[3] = r;

		CircleCenter[4] = center4.zw;
		R[4] = r;

		int amount = 6;
		for (int j = 0; j < amount; j++) {
			r = CalculateRadius(R[1],R[3],R[0]);
			center4 = CaculateFourthCircleCenter(CircleCenter[1], CircleCenter[3], R[1] + r, R[3] + r);
			//flag = IsInCircle(uv, center4.xy, r);
			if (IsInGear(uv, center4.xy, r, centerColor, contourColor, ToothWidth, ToothDepth, -rearSpeed) >= 0.5) {
				//return fixed4(1,1,1,1);
				centerColor = mix(CenterColor, ContourColor, 0.5+float(j)*0.1);
				contourColor = mix(ContourColor, CenterColor, 0.5+float(j)*0.1);
				return DrawCircle(uv, center4.xy,r, centerColor, contourColor);
			}
			CircleCenter[3] = center4.xy;
			R[3] = r;
		}

		for (int j = 0; j < amount; j++) {
			r = CalculateRadius(R[1], R[4], R[0]);
			center4 = CaculateFourthCircleCenter(CircleCenter[1], CircleCenter[4], R[1] + r, R[4] + r);
			//flag = IsInCircle(uv, center4.zw, r);
			if (IsInGear(uv, center4.zw, r, centerColor, contourColor, ToothWidth, ToothDepth, -rearSpeed) >= 0.5) {
				//return fixed4(0,0,0,1);
				centerColor = mix(CenterColor, ContourColor, 0.5 + float(j) * 0.1);
				contourColor = mix(ContourColor, CenterColor, 0.5 + float(j) * 0.1);
				return DrawCircle(uv, center4.zw,r, centerColor, contourColor);
			}
			CircleCenter[4] = center4.zw;
			R[4] = r;
		}

		//contourColor=vec4(0.7,0.9,0.9,1);
		//flag = IsInCircle(uv, CircleCenter[0], R[0]);
		if (IsInGearContour(uv, CircleCenter[0], R[0], centerColor, contourColor, ContourToothWidth, ContourToothDepth,ContourDepth, -rearSpeed) >= 0.5) {
			centerColor = mix(CenterColor, ContourColor, 0.0);
			contourColor = mix(ContourColor, CenterColor, 0.0);
			return DrawCircle(uv, CircleCenter[0],R[0], centerColor, contourColor);
		}

		return DrawBg(uv);
}


