void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    float r = length(vec2(.0)-uv);
    float a = atan(uv.x,uv.y);

    a += smoothstep(r*.1,r+.9,sin((r-a)*6.+iTime))*sin((r+a)*3.+iTime);

    vec3 col = vec3(0.0);

    col.r += smoothstep(.1,.9,abs(sin(a+iTime)));
    col.g += smoothstep(r,r+.1,abs(sin((a+r)*3.+iTime)));
    col.b += smoothstep(.1,r-.1,sin((a+r)*12.+iTime))+smoothstep(.1,r-.1,sin((a-r)*12.+iTime));
	col.rgb += sin((a-r)*12.-iTime-mix(col.r, col.g, (sin(r*10.-iTime))));
    // Output to screen
    fragColor = vec4(col,1.0);
}

