float raySphere(vec3 rayPos, vec3 rayDir, vec3 spherePos, float sphereRadius, out vec3 intersecS, out vec3 normalS)
{    
    vec3 diff = rayPos - spherePos;

    float a = dot(rayDir,rayDir);
    float b = 2.0 * dot(diff,rayDir);
    float c = dot(diff,diff) - sphereRadius * sphereRadius;

    float di = b*b - 4.0*a*c;

    if(di >= 0.0)
    {
        float sdi = sqrt(di);
        float den = 1.0 / (2.0 * a);
        float t1 = (-b - sdi) * den;
        float t2 = (-b + sdi) * den;

        intersecS = rayPos + t1 * rayDir;
        normalS = normalize(intersecS - spherePos);

        return t1;
    }

    return -1.;
}

float rayPlane(vec3 rayPos, vec3 rayDir, vec3 planePos, vec3 planeNormal, out vec3 intersecPt, out vec3 normal)
{
    if(dot(rayDir,planeNormal)!=0.0)
    {
        float t = dot(planePos - rayPos,planeNormal)/dot(rayDir,planeNormal);

        intersecPt = rayPos + t * rayDir;
        normal = planeNormal;

        return t;
    }
    return -1.0;
}

float rayCSG(vec3 rayPos, vec3 rayDir, out vec3 intersecPt, out vec3 normal, out int subObjectId)
{
    //Our example CSG object is define like this : ((sphere1 AND sphere2) OR sphere3) - sphere4)

    float distIn, distOut;
    distIn = -1.0;
    distOut = -1.0;

    //Intersection sphere 1
    vec3 diff1 = rayPos - csgSphere1Pos;

    float a1 = dot(rayDir,rayDir);
    float b1 = 2.0 * dot(diff1,rayDir);
    float c1 = dot(diff1,diff1) - csgSphere1Radius * csgSphere1Radius;

    float di1 = b1*b1 - 4.0*a1*c1;

    float dist1IN, dist1OUT;

    if(di1 >= 0.0)
    {
        float sdi1 = sqrt(di1);
        float den1 = 1.0 / (2.0 * a1);
        float t11 = (-b1 - sdi1) * den1;
        float t21 = (-b1 + sdi1) * den1;

        //sphere1IN = rayPos + t11 * rayDir;
        //sphere1OUT = rayPos + t21 * rayDir;
        dist1IN = t11;
        dist1OUT = t21;
        //normalS1 = normalize(intersecS1 - csgSphere1Pos);
    }
    else
    {
        dist1IN = -1.0;
        dist1OUT = -1.0;
    }


    //Intersection Sphere 2
    vec3 diff2 = rayPos - csgSphere2Pos;

    float a2 = dot(rayDir,rayDir);
    float b2 = 2.0 * dot(diff2,rayDir);
    float c2 = dot(diff2,diff2) - csgSphere2Radius * csgSphere2Radius;

    float di2 = b2*b2 - 4.0*a2*c2;

    float dist2IN, dist2OUT;

    if(di2 >= 0.0)
    {
        float sdi2 = sqrt(di2);
        float den2 = 1.0 / (2.0 * a2);
        float t12 = (-b2 - sdi2) * den2;
        float t22 = (-b2 + sdi2) * den2;

        //sphere1IN = rayPos + t12 * rayDir;
        //sphere1OUT = rayPos + t22 * rayDir;
        dist2IN = t12;
        dist2OUT = t22;
        //normalS2 = normalize(intersecS2 - csgSphere2Pos);
    }
    else
    {
        dist2IN = -1.0;
        dist2OUT = -1.0;
    }

    //Intersection Sphere 3
    vec3 diff3 = rayPos - csgSphere3Pos;

    float a3 = dot(rayDir,rayDir);
    float b3 = 2.0 * dot(diff3,rayDir);
    float c3 = dot(diff3,diff3) - csgSphere3Radius * csgSphere3Radius;

    float di3 = b3*b3 - 4.0*a3*c3;

    float dist3IN, dist3OUT;

    if(di3 >= 0.0)
    {
        float sdi3 = sqrt(di3);
        float den3 = 1.0 / (2.0 * a3);
        float t13 = (-b3 - sdi3) * den3;
        float t23 = (-b3 + sdi3) * den3;

        //sphere3IN = rayPos + t13 * rayDir;
        //sphere3OUT = rayPos + t23 * rayDir;
        dist3IN = t13;
        dist3OUT = t23;
        //normalS3 = normalize(intersecS3 - csgSphere3Pos);
    }
    else
    {
        dist3IN = -1.0;
        dist3OUT = -1.0;
    }

    //Intersection Sphere 4
    vec3 diff4 = rayPos - csgSphere4Pos;

    float a4 = dot(rayDir,rayDir);
    float b4 = 2.0 * dot(diff4,rayDir);
    float c4 = dot(diff4,diff4) - csgSphere4Radius * csgSphere4Radius;

    float di4 = b4*b4 - 4.0*a4*c4;

    float dist4IN, dist4OUT;

    if(di4 >= 0.0)
    {
        float sdi4 = sqrt(di4);
        float den4 = 1.0 / (2.0 * a4);
        float t14 = (-b4 - sdi4) * den4;
        float t24 = (-b4 + sdi4) * den4;

        //sphere4IN = rayPos + t14 * rayDir;
        //sphere4OUT = rayPos + t24 * rayDir;
        dist4IN = t14;
        dist4OUT = t24;
        //normalS4 = normalize(intersecS4 - csgSphere4Pos);
    }
    else
    {
        dist4IN = -1.0;
        dist4OUT = -1.0;
    }

    //Sphere1 AND Sphere2

    if((dist1OUT < dist2IN) || (dist2OUT < dist1IN))
    {
        distIn = -1.0;
    	distOut = -1.0;
    }
    else
    {
        distIn = max(dist1IN,dist2IN);
        distOut = min(dist1OUT,dist2OUT);
        if(distIn == dist1IN)
        {
        	subObjectId = 1;
            intersecPt = rayPos + distIn * rayDir;
        	normal = normalize(intersecPt - csgSphere1Pos);
        }
        else
        {
            subObjectId = 2;
            intersecPt = rayPos + distIn * rayDir;
        	normal = normalize(intersecPt - csgSphere2Pos);
        }
    }

    //(Sphere1 AND Sphere2) OR Sphere3
    if(distIn > 0.0 && dist3IN > 0.0)
    {
        distIn = min(distIn,dist3IN);
        distOut = max(distOut, dist3OUT);
        if(distIn == dist3IN)
        {
        	subObjectId = 3;
            intersecPt = rayPos + distIn * rayDir;
        	normal = normalize(intersecPt - csgSphere3Pos);
        }
    }
    else
    {
        distIn = max(distIn,dist3IN);
        distOut = max(distOut, dist3OUT);
        if(distIn > 0.0)
        {
        	if(distIn == dist3IN)
        	{
        		subObjectId = 3;
        	    intersecPt = rayPos + distIn * rayDir;
        		normal = normalize(intersecPt - csgSphere3Pos);
       		}
        }
        else
        {
            distIn = -1.0;
    		distOut = -1.0;
            subObjectId = 0;
        }
    }

    //((Sphere1 AND Sphere2) OR Sphere3) - Sphere4
    if(distIn > 0.0 && dist4IN > 0.0)
    {
        if(distIn < dist4IN)
        {
            distOut = dist4IN;
        }
        else
        {
            if(distOut > dist4OUT)
            {
            	distIn = dist4OUT;
            	subObjectId = 4;
            	intersecPt = rayPos + distIn * rayDir;
        		normal = normalize(intersecPt - csgSphere4Pos);
            }
            else
            {
                distIn = -1.0;
            }
        }
    }
    else
    {
        if(distIn < 0.0)
        {
            distIn = -1.0;
    		distOut = -1.0;
            subObjectId = 0;
        }
        else
        {
            distOut = dist4IN;
        }
    }

    if(distIn >= 0.0)
        return distIn;

    subObjectId = 0;

    return -1.0;
}

void computeCameraRayFromPixel(in vec2 pixCoord, out vec3 rayPos, out vec3 rayDir)
{
    rayPos = cameraPos;

    float f = 1./tan(radians(cameraFovY)/2.);

    vec3 PT = cameraTarget - cameraPos;
    vec3 cz = normalize(PT);

    vec3 PO = f*cz;

    vec3 cyT = vec3(0,-1.,0);
    vec3 cxT = cross(cyT,cz);

    vec3 cx = normalize(cxT);

    vec3 cy = cross(cz,cx);

    vec3 OS = pixCoord.x * cx - pixCoord.y * cy;

    rayDir = normalize(PO+OS);
}

vec3 computePhongShading(vec3 sphereCol, Material sphereMat,float shadowFactor, vec3 normal, vec3 L, vec3 R, vec3 V, LightInfo light)
{
    vec3 A = sphereMat.Ka * ambiantCol / float(NB_LIGHTS);
    vec3 D = sphereMat.Kd * sphereCol * light.col * light.power * max(dot(L,normal),0.0);
    vec3 S = sphereMat.Ks * light.col * light.power  * pow(max(dot(R,V),0.0),sphereMat.Kn);
    return A+shadowFactor*D+shadowFactor*S;
}



//The aim of this routine is to find the nearest intersection the ray has with all objects
float computeNearestIntersection(vec3 rayPos, vec3 rayDir, out int objectId, out vec3 intersecI, out vec3 normalI)
{
    // Méthode pas adaptée pour l'ajout d'objets, 
    // pour le CSG je m'inspire de la méthode ComputeNearestIntersection de https://www.shadertoy.com/view/XslcDM

    /*vec3 intersecS, normalS;
    float distSphere = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersecS, normalS);
    vec3 intersecP, normalP;
    float distPlane = rayPlane(rayPos, rayDir, planePos, planeNormal, intersecP, normalP);
    if(distSphere > 0.0 && distPlane > 0.0)
    {
        float distMin = min(distSphere,distPlane);
        objectId = (distSphere == distMin) ? sphereId : planeId; 
        intersecI = (distSphere == distMin) ? intersecS : intersecP; ;
        normalI = (distSphere == distMin) ? normalS : normalP; ;
        return distMin;
    }
    else
    {
        if(distSphere > 0.0)
        {
            objectId = sphereId;
            intersecI = intersecS;
            normalI = normalS;
            return distSphere;
        }
        else if (distPlane > 0.0)
        {
            objectId = planeId;
            intersecI = intersecP;
            normalI = normalP;
            return distPlane;
        }
        else
        {
           	objectId = skyId;
            return -1.0;
        }
    }*/

    // Set the default value when no intersection is found: we hit the 'sky'
    float minDist  = 1000000.0;
          objectId = skyId;

    // Test the sphere
    vec3 intersecS, normalS;
    float distS = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersecS, normalS);
    if ((distS > 0.0) && (distS < minDist))
    {
        objectId  =  sphereId;
        minDist   =     distS;
        intersecI = intersecS;
          normalI =   normalS;
    }

    // Test the plane
    vec3 intersecP, normalP;
    float distP =  rayPlane(rayPos, rayDir,  planePos,  planeNormal, intersecP, normalP);
    if ((distP > 0.0) && (distP < minDist))
    {
        objectId  =   planeId;
        minDist   =     distP;
	    intersecI = intersecP;
    	  normalI =   normalP;
    }

    // Test the CSG object
    vec3 intersecC, normalC;
    float distC =  rayCSG(rayPos, rayDir,  intersecC,  normalC, subObjectIdC);
    if ((distC > 0.0) && (distC < minDist))
    {
        objectId  =   csgId;
        minDist   =     distC;
	    intersecI = intersecC;
    	  normalI =   normalC;
    }

    // To remain coherent with the raySphere & rayPlane function that returns -1 when no
    // intersetion is found, we add the following two lines:
    if (objectId == skyId)
        minDist = -1.0;

    return minDist;
}

