const float phi = (1.+sqrt(5.))*.5;

float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 eye() {
    return vec3(.2, .5, 2.);
}

vec3 lookAt (vec3 from, vec3 target, vec2 uv) {
    vec3 forward = normalize(target - from);
    vec3 right = normalize(cross(forward, vec3(0,1,0)));
    vec3 up = normalize(cross(forward, right));
    return normalize(forward * .5 + uv.x * right + uv.y * up);
}

mat2 rotation(float angle) {
    float c = cos(angle), s = sin(angle);
    return mat2(c, s, -s, c);
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdDodecahedron(vec3 p, float r)
{
    const vec3 n = normalize(vec3(phi,1,0));

    p = abs(p/r);
    float a = dot(p,n.xyz);
    float b = dot(p,n.zxy);
    float c = dot(p,n.yzx);
    return (max(max(a,b),c)-n.x)*r;
}

float sdIcosahedron(vec3 p, float r)
{
    const float q = (sqrt(5.)+3.)/2.;

    const vec3 n1 = normalize(vec3(q,1,0));
    const vec3 n2 = vec3(sqrt(3.)/3.);

    p = abs(p/r);
    float a = dot(p, n1.xyz);
    float b = dot(p, n1.zxy);
    float c = dot(p, n1.yzx);
    float d = dot(p, n2.xyz)-n1.x;
    return max(max(max(a,b),c)-n1.x,d)*r; // turn into (...)/r  for weird refractive effects when you subtract this shape
}

float displace(float shape, vec3 p, float freq, float scale) {
    float d = sin(freq*p.x)*sin(freq*p.y)*sin(freq*p.z);
    return shape + scale*d;
}

float sdf(vec3 eye) {
    mat2 rot = rotation(iTime*.1);
    vec3 p = eye;
    p.xy *= rot;
    p.xz *= rot;
    p.yz *= rot;

    float shape = min(
            sdDodecahedron(p,.75),
            sdIcosahedron(p.zyx,.75)
        );
    vec3 pos = eye;
    float displacedShape = displace(shape, p, mix(20., 1., (sin(iTime)*.5 + .5)), .1);

    return mix(displacedShape, shape, (sin(iTime)*.5 + .5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = (uv-.5) / 2.;
    uv.x *= iResolution.x/iResolution.y;

    vec4 color = vec4(0.);

    float shade = 0.;
    float distTotal = 0.;

    vec3 eye = eye();
    vec3 target = vec3(0);
    vec3 ray = lookAt(eye, target, uv);
    const float count = 50.;

    for (float i = count; i > 0.; --i) {

        float dist = sdf(eye);

        if (dist < .0001) {
            shade = i/count;
            break;
        }

        eye += ray * dist;
        distTotal += dist;
    }

    vec3 shadeColor = mix(vec3(0., 1., 0.), vec3(0., 0., 1.), (sin(iTime)*.5 + .5));

	color = vec4(vec3(pow(shade, 2.)) * shadeColor, 1.);

    color.rgb += rand(uv+vec2(iTime*.00001))*.15;

    fragColor = color;
}

const float PI = 3.14159265;
const float angle = 60.0;
const float fov = angle * 0.5 * PI / 180.0;

vec3 cPos = vec3(0,0,3);

const float sSize = 0.5;

float sphereDF(vec3 p){
    //レイ座標の乗除を取ることでレイをループ
    vec3 loopP = mod(p,4.0) - 2.0;
	return length(loopP) - sSize;
}

vec3 getNormal(vec3 p){
    float delta = 0.001;
	vec3 dx = vec3(delta,0.0,0.0);
	vec3 dy = vec3(0.0,delta,0.0);
	vec3 dz = vec3(0.0,0.0,delta);
    vec3 result;
    result.x = sphereDF(p + dx) - sphereDF(p - dx);
    result.y = sphereDF(p + dy) - sphereDF(p - dy);
    result.z = sphereDF(p + dz) - sphereDF(p - dz);

    return normalize(result);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //uvを取得
    vec2 pos = (fragCoord * 2.0  - iResolution.xy ) / min(iResolution.x,iResolution.y);

    //レイを作成
	vec3 ray = normalize(vec3(sin(fov) * pos.x,sin(fov) * pos.y,-1.0));

    //レイからオブジェクトの距離
    float rDistance = 0.0;
    //レイの進んだ距離
    float rLen = 0.0;

    //カメラの位置を移動
    cPos.z -= iTime * 10.0;
    cPos.y = sin(iTime) * 5.0;
    cPos.x = cos(iTime * 0.5) * 2.0;
    vec3 rPos = cPos;

    //レイマーチング
    for(int i = 0; i < 64; i++){
    	rDistance = sphereDF(rPos);
        rLen += rDistance;
        rPos = cPos + ray * rLen;
    }

    vec3 col = vec3(0,0,0);
    //最短距離が十分短ければ
    if(abs(rDistance) <= 0.01){
        //オブジェクトの砲戦を取得
        vec3 normal = getNormal(rPos);
        float diff = clamp(dot(vec3(0.5,0.5,0.5), normal), 0.1, 1.0);
        //ピクセルの描画
        col = vec3(diff);
    }else{
        //レイがオブジェクトに到達していなければ描画しない
        col = vec3(0,0,0);
    }

    fragColor = vec4(col,1.0);
}

