#define angle_percent(uv) ((angle(uv)/PI+1.0)/2.0)

#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)
#define circle(x) (vec2(cos((x)*2.0*PI),sin(2.0*PI*(x))))

#define rotatePoint(p,n,theta) (p*cos(theta)+cross(n,p)*sin(theta)+n*dot(p,n) *(1.0-cos(theta)))


float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}

vec4 saw(vec4 x)
{
    return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w));
}


//-----------------ITERATED FUNCTION SYSTEM-----------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float gradient = uv.y;

        float scale = E;
    uv = uv*scale-scale/2.0;

    float aspect = iResolution.x/iResolution.y;

    uv.x *= aspect;

    vec2 uv0 = uv;

    int max_iterations = 4;//-int(saw(spounge)*float(max_iterations)/2.0);

    float antispeckle = 1.0; 
    float magnification = 1.0;

	vec4 color = vec4(0.0);
    float map = 0.0;
    float border = 1.0;
    vec4 final = vec4(0.0);
    float c1 = 0.0;

    for(int i = 0; i < max_iterations; i++)
    {
        float iteration = float(i)/float(max_iterations);

        float d = -cos(float(i)*PI);

        vec2 o = circle(time*d)*(1.0-iteration)/2.0;


        float c = smoothstep(0.0, 1.0/E/E, 1.0-length(uv));
        if(i == 0) c1 = c;



        vec2 uv2 = (uv+o)*(E+iteration);
        float c2 = smoothstep(1.0/E, 1.0/GR, 1.0-length(uv2)*2.0/PI)*c;
        vec2 uv3 = (uv-o)*(E+iteration);
        float c3 = smoothstep(1.0/E, 1.0/GR, 1.0-length(uv3)*2.0/PI)*c;

        uv = ((uv)*(1.0-c2)*(1.0-c3)+
            (uv2)*(c2)*(1.0-c3)+
            (uv3)*(1.0-c2)*(c3))*(antispeckle)+uv*(1.0-antispeckle)*c;


        border *= saw(1.0+c2*2.0)*saw(1.0+c3*2.0);

        float b = pow(border, iteration);
        float a = atan(uv.y, uv.x)/PI*.5+.5;
        float l = d*length(uv)*.5;
        map += smoothstep( 0.0, border*GR, GR*saw(c+c2+c3)*saw(border*saw(saw(2.0*(a+l))*saw(b*(d-c2)*(d-c3))*antispeckle)*(iteration+1.0)));

        antispeckle *= (clamp((c2+c3), 0.0, 1.0))*(1.0-saw(c2*2.0-c3)*saw(c3*2.0-c2));
        final += vec4(flux(map*PI+time*GR*E), 1.0);
    }

    float w = smoothstep(.8, 1.0, saw(map));

    map = smoothstep(0.0, 1.0, map);
    fragColor = ((w+final*(1.0-w)))*map*c1+(1.0-c1)*gradient;
}

#define PI2 6.28318530718

bool equals(float val, float check, float tolerance) {
    if (val + tolerance > check && val - tolerance < check) {
        return true;
    } else {
        return false;
    }
}

bool equalsMod(float val, float check, float tolerance, float modulo) {
    if (mod(mod(val, modulo) - check, modulo) < tolerance) {
        return true;
    } else {
        return false;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspect = iResolution.x / iResolution.y;

    const vec2 centerOffset = vec2(0,0);
    const float speed = 5.0;
    const float lineThickness = 0.5 * PI2;

    vec2 pos = (uv * 2.0) - vec2(1, 1);
    pos -= centerOffset;

    pos.x *= aspect;

    float radius = length(pos);
    float angle = acos(pos.y / radius);
    if (pos.x <= 0.0) {
        angle = (PI2) - angle;
    }

    angle = mod(angle + iTime * speed, PI2);

    float r = radius * 10.0 * PI2;

    if (equalsMod(r, angle, lineThickness, PI2)) {
        fragColor = vec4(1,1,1,1);
    } else {
        fragColor = vec4(0,0,0,1);
    }
}

#define STEP_SIZE .1
#define NUMBER_OF_STEPS 32
#define MINIMUM_HIT_DISTANCE .001
#define MAXIMUM_TRACE_DISTANCE 1000.
#define SMALL_STEP vec3(.001, 0., 0.)
#define SQRT2 1.41421356237
#define SQRT13 3.60555127546
#define NB_THRESHOLDS 100.
#define PI 3.14159265359
#define AA_SAMPLES_X 4.

#define sph(p, c, r) length(p-c)-r


// params:
// p: arbitrary point in 3D space
// c: the center of our sphere
// r: the radius of our sphere

// polynomial smooth min (k = 0.1);
float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 );
    return min( a, b ) - h*h*h/(6.0*k*k);
}

float SDF(in vec3 p){
    //float displacement = 0.;//sin(5. * p.x) * sin(5. * p.y) * sin(5. * p.z) * 0.25;

 	float sphere_0 = sph(p, vec3(0.), 1.);

    float sphere_1 = sph(p, vec3(1.5*sin(iTime),1.5*sin(iTime),-1.), 0.5);

    //Later we might have sphere_1, sphere_2, cube_3, etc...

    return sminCubic(sphere_0, sphere_1, 1.5);
}

vec3 calculate_normal(in vec3 p){
    return normalize(vec3(
    SDF(p + SMALL_STEP.xyy) - SDF(p - SMALL_STEP.xyy),
    SDF(p + SMALL_STEP.yxy) - SDF(p - SMALL_STEP.yxy),
    SDF(p + SMALL_STEP.yyx) - SDF(p - SMALL_STEP.yyx)));
}

vec3 ray_march(in vec3 ro, in vec3 rd){
	float total_distance_traveled = 0.;
    // Ray Marching
    for (int i = 0; i < NUMBER_OF_STEPS ; ++i){
        // Calculate our current posiiton along the ray
        vec3 current_position = ro + total_distance_traveled * rd;

        // Sphere centered at the origin and has unit radius
        float distance_to_closest = SDF(current_position);

        if (distance_to_closest < MINIMUM_HIT_DISTANCE)
        {
            vec3 normal = calculate_normal(current_position);

            // for now hard-code the ligth's position in our scene
            vec3 light_position = vec3(SQRT13*cos(2.*iTime), -5.0, SQRT13*sin(2.*iTime));
            vec3 light_color = vec3(1., 0., 0.);

            //Calculate the unit direction vector that point from
            // the point of intersection to the light source
            vec3 direction_to_light = normalize(current_position - light_position);

            float diffuse_intensity = max(0., floor(dot(normal, direction_to_light)*NB_THRESHOLDS)/NB_THRESHOLDS);

            // for now hard-code the ligth's position in our scene
            vec3 light_position2 = vec3(SQRT13*cos(2.*iTime + 2.*PI/3.), -5.0, SQRT13*sin(2.*iTime + 2.*PI/3.));
            vec3 light_color2 = vec3(0., 0., 1.);

            //Calculate the unit direction vector that point from
            // the point of intersection to the light source
            vec3 direction_to_light2 = normalize(current_position - light_position2);

            float diffuse_intensity2 = max(0., floor(dot(normal, direction_to_light2)*NB_THRESHOLDS)/NB_THRESHOLDS);

            // for now hard-code the ligth's position in our scene
            vec3 light_position3 = vec3(SQRT13*cos(2.*iTime + 4.*PI/3.), -5.0, SQRT13*sin(2.*iTime+ 4.*PI/3.));
            vec3 light_color3 = vec3(0., 1., 0.);

            //Calculate the unit direction vector that point from
            // the point of intersection to the light source
            vec3 direction_to_light3 = normalize(current_position - light_position3);

            float diffuse_intensity3 = max(0., floor(dot(normal, direction_to_light3)*NB_THRESHOLDS)/NB_THRESHOLDS);

         	// we hit something ! Return red for now
            return clamp(vec3(1.,1.,1.)*(diffuse_intensity * light_color + diffuse_intensity2 * light_color2 + diffuse_intensity3 * light_color3), vec3(0.), vec3(1.));
        }

        if (total_distance_traveled > MAXIMUM_TRACE_DISTANCE) //miss
        {
         	break;   
        }

        // accumulate the distance traveled thus far
        total_distance_traveled += distance_to_closest;
    }

    // If we get here, we didn't hit anything so just
    // rerturn a background color (black)
    return vec3(0.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from -1 to 1) and normalize
    vec2 uv = 2.*fragCoord.xy/iResolution.x - vec2(1., iResolution.y/iResolution.x);
    float delta = 2./iResolution.x;

    vec3 camera_position  = vec3(0., 0., -5. - 0.*sin(iTime));
    vec3 ro = camera_position;
    vec3 rd;


	// ANTI ALIASING ALGORITHM
    vec3 shaded_color = vec3(0);
    for (float i=0. ; i < AA_SAMPLES_X * AA_SAMPLES_X ; i++){
        //rd = vec3(uv, 1.0);
        rd = vec3(uv.x + delta/2. * (floor(i/AA_SAMPLES_X) - 0.5), uv.y + delta/2. * (mod(i,AA_SAMPLES_X) - 0.5),1.0);
        shaded_color += ray_march(ro, rd);
    }
    shaded_color /= AA_SAMPLES_X * AA_SAMPLES_X;


    // Output to screen
    fragColor = vec4(shaded_color,1.0);
}

// simple plane march
mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}
#define	TAU 6.28318

float map(vec3 p)
{
    float d =  length(p.xz);

    float t2 = fract(iTime*0.5) * TAU;

	float y = 0.5+sin(t2 + d)*0.5;
	y = y*=abs(p.x*p.z)*0.125;
	y = smoothstep(0.0,4.0,y);

	return p.y - y*y;
}

vec3 normal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}
vec3 render(vec3 ro, vec3 rd)
{
	// march	
	float tmin = 0.1;
	float tmax = 120.;
	vec3 p;
	float t = tmin;
	for (int i = 0; i < 80; i++)
	{
		p = ro + t * rd;
		float d = map(p);
		t += d*0.5;
		if (t > tmax)
			break;		
	}

    // light
	if (t < tmax)
	{
	   	vec3 lightDir = normalize(vec3(1.5, 1.0, 0.5));
		vec3 nor = normal(p);
		vec3 c = vec3(0.3, 0.1, 0.5);

		float dif = max(dot(nor, lightDir), 0.0);
		c += vec3(0.2) * dif;

		vec3 ref = reflect(rd, nor);
		float spe = max(dot(ref, lightDir), 0.0);
		c += vec3(3.0) * pow(spe, 16.);

		return c;
	}

	return vec3(0.2,0.2,0.6);
}

mat3 camera(vec3 ro, vec3 ta, vec3 up)
{
	vec3 nz = normalize(ta - ro);
	vec3 nx = cross(nz, normalize(up));
	vec3 ny = cross(nx, nz);
	return mat3(nx, ny, nz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.xy;
	p.x *= iResolution.x / iResolution.y;


	vec3 ro = vec3(0.0, 10.0, 0.0);

    float ang = radians(-90.0);
    ro.z = sin(ang)*14.0;
    ro.x = cos(ang)*14.0;

	vec3 ta = vec3(0.0, -2.0, 0.0);

	vec3 rd = camera(ro, ta, vec3(0.0, 1.0, 0.0)) * normalize(vec3(p.xy, 1.0));

	vec3 c = render(ro, rd);

    // vignette
    c *= 0.4 + 0.6*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.1 );

	fragColor = vec4(c, 1.0);
}

// lots of inspiration from IQ and hg_sdf

const float PI = 3.14159265359;
const int MAX_MARCHING_STEPS = 50;
const float EPSILON = 0.0001;

vec2 rotate2d(vec2 v, float a) {
	return vec2(v.x * cos(a) - v.y * sin(a), v.y * cos(a) + v.x * sin(a)); 
}

// Rotate around a coordinate axis (i.e. in a plane perpendicular to that axis) by angle <a>.
// Read like this: R(p.xz, a) rotates "x towards z".
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sdTorus( vec3 p, vec2 t ) {
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float opTwist( vec3 p, float fftValue, float time ) {
    float c = cos((fftValue*1.5) *p.y);
    float s = sin((fftValue+0.5)*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m * p.xz, p.y);

    return sdTorus(q, vec2(abs(sin(iTime*0.1))+0.5*(fftValue*0.2), fftValue*0.0001));

}

float opRep( vec3 p, vec3 c ) {

    float idx = mod((floor((p.x)/c.x)), 32.0);
    float idy = mod((floor((p.y)/c.y)), 32.0);
    float idz = mod((floor((p.z)/c.z)), 32.0);

	float id = length(vec3(idx, idy, idz));

    float fftValue = (((texture( iChannel0, vec2(id+1.0, 0.0) ).x)) * 10.0);

    vec3 q = mod(p, c) - 0.5 * c;

    vec3 r = q;  

    float rotationAmount = (id * 5.0) + (iTime * 2.0);

    bool xmod2 = mod(idx, 2.0) == 0.0;

    // offset even rows
    if (xmod2) {
    	q.y += 1.5;
        r.y -= 1.5;
    }

	pR(q.xy, rotationAmount);
    pR(q.xz, rotationAmount * 0.1);

    float shape1 = opTwist(q, fftValue, iTime);

    if (xmod2) {

        pR(r.xy, rotationAmount);
        pR(r.xz, rotationAmount * 0.1);

        float shape2 = opTwist(r, fftValue, iTime);

    	return min(shape1, shape2);

    } else {

        return shape1;

    }
}


float sceneSDF(vec3 samplePoint) {
	return opRep(samplePoint, vec3(3.0, 3.0, 3.0));
}

vec3 castRay(vec3 pos, vec3 dir) {
	for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
		float dist = sceneSDF(pos);
        if (dist < EPSILON) {
			return pos;
        }
		pos += dist * dir;
	}    
	return pos;
}


float lightPointDiffuse(vec3 pos, vec3 lightPos) {
	float lightDist = length(lightPos - pos);
	float color = 3.0 / (lightDist * lightDist);
	return max(0.0, color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float fft = ( iChannel0, vec2(fragCoord.x,fragCoord.y) ).x; 
	fft = 0.9*fft;

	vec4 mousePos = (iMouse / iResolution.xyxy) * 2.0 - 1.0;

	mousePos *= vec2(PI / 2.0, PI / 2.0).xyxy;

    if (iMouse.zw == vec2(0.0)) {
        mousePos.xy = vec2(0.5, -0.2); 
    }

	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

	vec3 cameraPos = vec3(0.0, 0.0, -8.0);

	vec3 cameraDir = vec3(0.0, 0.0, 1.0);
	vec3 planeU = vec3(2.0, 0.0, 0.0);
	vec3 planeV = vec3(0.0, iResolution.y / iResolution.x * 2.0, 0.0);
	vec3 rayDir = normalize(cameraDir + screenPos.x * planeU + screenPos.y * planeV);

	cameraPos.yz = rotate2d(cameraPos.yz, mousePos.y);
	rayDir.yz = rotate2d(rayDir.yz, mousePos.y);

	cameraPos.xz = rotate2d(cameraPos.xz, mousePos.x);
	rayDir.xz = rotate2d(rayDir.xz, mousePos.x);

    cameraPos.zy += iTime;

	vec3 rayPos = castRay(cameraPos, rayDir);

    // base color
	vec3 color = vec3(0.01, 0.23, 0.43);

    color += (rayDir*0.02);

    vec3 lightPos = cameraPos;

    color *= 10.0 * lightPointDiffuse(rayPos, lightPos) * 10.0;

	color = pow(color, vec3(0.4));	

	fragColor = vec4(color, 1.0);
}

