vec2 	cmult(vec2 a, vec2 b)
{
    return (vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x));
}

vec2	cadd(vec2 a, vec2 b)
{
	return (vec2(a.x + b.x, a.y + b.y));
}

vec2 g(vec2 n) {
    return vec2(
        n.x*n.x*n.x - 3.*n.x*n.y*n.y - 100.,
        -n.y*n.y*n.y + 3.*n.x*n.x*n.y
    );
}

vec2 dg(vec2 n) {
    return 2. * vec2(
        n.x*n.x - n.y*n.y,
        2. * n.x * n.y
    );
}

vec2 cdiv(vec2 a, vec2 b) {
    float d = dot(b, b);
    if(d == 0.) return a;
    else return vec2(
        (a.x*b.x + a.y*b.y),
        (a.y*b.x - a.x*b.y)) / d;;
}

const vec3 light = vec3(5,2.6,3.2) * 5.;

const float epsilon = 0.001;
const float screen_width = 2.;
const float screen_height = 2.;
const float screen_depth= 2.;

float sdBox3( vec3 p, vec3 b ) {
    p = abs(p) - b;
    return min(max(p.x, max(p.y, p.z)), length(max(vec3(0), p)));
}

float distSponge(vec3 p) {
    p = abs(p);
    float d = sdBox3(p, vec3(1));
    float s = 1.;
    for (int i = 0; i < 3; i++) {
        vec3 a = mod( p*s, 2.0 )-1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0*abs(a));
        float da = max(r.x,r.y);
        float db = max(r.y,r.z);
        float dc = max(r.z,r.x);
        float c = (min(da,min(db,dc))-1.0)/s;

        if( c>d ) {
          d = c;
        }
    }
    return d;
}

float dist(vec3 p) {
    vec3 xvec = normalize(vec3(1,2,4));
    vec3 yvec = vec3(0,1,0);
    vec3 zvec = normalize(cross(xvec, yvec));
    yvec = normalize(cross(zvec, xvec));
    mat3 r = mat3(xvec, yvec, zvec);
    float d = distSponge(r * p);
    d = min(d, distSponge(r * r * (p - vec3(3,1,0))));
    d = min(d, p.y + 1.5);
    return d;
}

vec3 getNormal(vec3 p) {
	vec3 result;
	result.x = dist(p + vec3(epsilon, 0, 0)) - dist(p - vec3(epsilon, 0, 0));
	result.y = dist(p + vec3(0, epsilon, 0)) - dist(p - vec3(0, epsilon, 0));
	result.z = dist(p + vec3(0, 0, epsilon)) - dist(p - vec3(0, 0, epsilon));
	return normalize(result);
}

vec3 rayMarch(vec3 cv, vec3 p) {
	for (int j = 0; j < 64; j++) {
		float d = dist(p);
		if (abs(d) < epsilon)
			break;
		p += d * cv;
	}
    return p;
}

float shadow(vec3 p) {
    const float k = 20.;
    float res = 1.;
    vec3 v = normalize(light-p);
    float t = 0.01;
    float ph = 1e20;
    for (int i = 0; i < 32; i++) {
		float h = dist(p + t * v);
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;

        if (res < epsilon) break;
        if (t > 100.) break;
	}
    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 tc = fragCoord - iResolution.xy * .5;
    tc /= iResolution.y;
    vec3 eye = vec3(1, 3, 5.);
    vec3 yvec = vec3(0, 1, 0);
    vec3 n = normalize(vec3(0, 1., 0));
    float angle = 1. * iTime;
    float c = cos(angle);
    float s = sin(angle);
    mat4 matrix;
	matrix[0][0] = n[0]*n[0]*(1.-c)+c;
	matrix[0][1] = n[0]*n[1]*(1.-c)-n[2]*s;
	matrix[0][2] = n[2]*n[0]*(1.-c)+n[1]*s;
	matrix[1][0] = n[0]*n[1]*(1.-c)+n[2]*s;
	matrix[1][1] = n[1]*n[1]*(1.-c)+c;
	matrix[1][2] = n[1]*n[2]*(1.-c)-n[0]*s;
	matrix[2][0] = n[2]*n[0]*(1.-c)-n[1]*s;
	matrix[2][1] = n[1]*n[2]*(1.-c)+n[0]*s;
	matrix[2][2] = n[2]*n[2]*(1.-c)+c;
    eye = (matrix * vec4(eye, 1.)).xyz;
    yvec = (matrix * vec4(yvec, 1.)).xyz;
    vec3 zvec = normalize(eye);
    vec3 xvec = normalize(cross(yvec, zvec));
    yvec = normalize(cross(zvec, xvec));
	vec3 cv = normalize(screen_width * tc.x * xvec + screen_height * tc.y * yvec - zvec * screen_depth);
    vec3 p = rayMarch(cv, eye);
	float d = dist(p);
	if (d < epsilon) {
        float intensity = texture(iChannel0, p.xz * 0.1).r;
        vec3 color = vec3(intensity);


		vec3 normal = getNormal(p);
        float dif = max(0., dot(normal, normalize(light - p)));
        dif *= shadow(p);


        float len = length(p - eye);
        float fog = len * 0.02;

        color = color * dif + fog;

        fragColor = vec4(color, 1.);
	} else {
		fragColor = vec4(1);
	}
   // fragColor += sin(iTime);
}

