//射线
struct Ray
{
    vec3 origin;
    vec3 direction;
};
//材质
struct Material
{
    vec4 BaseColor;
};
//检测结果
struct HitResult
{
    float t;
    vec3 worldNormal;
    Material material;
};

//球体检测函数
struct Sphere
{
    float radius;
    vec3 center;
    Material material;
};
//相机
