//球体射线检测
bool trace(in Ray r,in Sphere sp,out HitResult res){
    //射线原点到球心向量
    vec3 oc=sp.center-r.origin;
    //将原点到球心向量投影到射线方向求长度
    float l=dot(r.direction,oc);
    //射线原点到球心的平方距离
    float d=dot(oc,oc);
    //两个直角三角形平方差。其值为一条直角边的平方
    float det=pow(sp.radius,2.0)-(d-pow(l,2.0));//平方差
    if(det<0.0) return false;//无解
    res.t=l-sqrt(det);//最近点长度
    if (res.t<0.0) res.t=l+sqrt(det);//最近点在相反方向
    if(res.t<0.0) return false;
    //满足条件，填充检测结果
    res.worldNormal=normalize(r.origin+r.direction*res.t-sp.center);
    res.material=sp.material;
    return true;
}
