/////追踪场景内的物体
bool traceSence(in Ray r,out HitResult res)
{   
    bool re=false;
    float maxDistance=9e10;//最大光线追踪深度
    for(int i=0;i<count;i++)
    {
        HitResult tempRes;
        if(trace(r,sps[i],tempRes))
        {
            if(tempRes.t<maxDistance)
            {
                re=true;
                res=tempRes;
                maxDistance=tempRes.t;
            }
        }
    }
    return re;
}
//////取得世界空间射线
Ray getRay(in vec2 fragCoord,in Camera ca,in mat4 caT)
{
    fragCoord+=random(vec2(fragCoord.x,iTime));
    //对焦平面半高度
    float halfH=tan(radians(ca.FOV/2.0))*ca.focusDistance;
    //射线在对焦平面上的位置
    vec2 uv=fragCoord-iResolution.xy/2.0;//平移
    float rate=halfH/(iResolution.y/2.0);//绽放比例
    uv*=rate;
    vec3 rayTarget=vec3(uv,ca.focusDistance);
    //局部射线方向
    vec3 dir=normalize(rayTarget);//局部方向
    vec3 camerOrigin=vec3(0.0,0.0,0.0);//局部位置
    //转换到世界空间射线
    camerOrigin=vec3(caT*vec4(camerOrigin,1.0));//世界位置
    dir=mat3(caT)*dir;//世界方向
    //构建射线对象
    Ray r=Ray(camerOrigin,dir);
    return r;
}

