/////渲染背景
vec4 getBackground(vec3 dir)
{
    dir=normalize(dir);
    vec3 lightDir=mat3(rotatY(iTime*50.0))*normalize(vec3(1.0,1.0,-1.0));
    float alpha=pow(dot(dir,lightDir)*0.5+0.5,16.0);
    vec4 sunColor=vec4(1.0,0.6,0.2,1.0)*2.0;
    vec4 up=vec4(0.2,0.2,1.0,1.0)*1.0;
    vec4 down=vec4(1.0,0.9,0.8,1.0)*0.5;
    return mix(mix(down,up,dir.y),sunColor,alpha);
}
/////渲染
vec4 render(in Ray r)
{   
    const int maxDepth=32;
    vec4 finalColor=vec4(1.0);
    HitResult tempRes;
    for(int i=0;i<maxDepth;i++)
    {   r.origin+=r.direction*0.01;
        if(traceSence(r,tempRes))
        {
            //辐射贡献
            finalColor*=tempRes.material.BaseColor;
            //用于下一次追踪的射线
            r=Ray(r.origin+r.direction*tempRes.t,reflect(r.direction,tempRes.worldNormal));

            /*
            vec3 normal=(dot(r.direction,tempRes.worldNormal)<0.0)? tempRes.worldNormal:-tempRes.worldNormal;
            float rr=(dot(r.direction,tempRes.worldNormal)<0.0)? 1.3:1.0/1.3;
            r=Ray(r.origin+r.direction*tempRes.t,refract(r.direction,normal,rr));
			*/

            //追踪次数结束
            if(i+1==count){
                finalColor*=getBackground(r.direction);
                //finalColor*=vec4(1.0);//追踪尽头颜色
            }
        }
        else
        {
            finalColor*=getBackground(r.direction);
            break;
        }
    }
    return finalColor;
}
