const vec2 k = vec2(1.,0.);
const float pi = acos(-1.);

vec2 B2(float t, vec2 P0, vec2 P1, vec2 P2)
{
    float m1 = 1.-t;
    return m1*m1*P0 + 2.*m1*t*P1 + t*t*P2;
}

vec2 B2Prime(float t, vec2 P0, vec2 P1, vec2 P2)
{
    return 2.*((1.-t)*(P1-P0)+t*(P2-P1));
}

float D2(vec2 x, float t, vec2 P0, vec2 P1, vec2 P2)
{
    return length(x-B2(t, P0, P1, P2));
}

float D2Prime(vec2 x, float  t, vec2 P0, vec2 P1, vec2 P2)
{
    return dot(x-B2(t,P0,P1,P2), B2Prime(t, P0,P1,P2));
}

vec2 B3(float t, vec2 P0, vec2 P1, vec2 P2, vec2 P3)
{
    float m1 = 1.-t;
    return m1*m1*m1*P0 + 3.*m1*t*(m1*P1+t*P2) + t*t*t*P3;
}

vec2 B3Prime(float t, vec2 P0, vec2 P1, vec2 P2, vec2 P3)
{
    float m1 = 1.-t;
    return 3.*(m1*m1*(P1-P0)+2.*m1*t*(P2-P1)+t*t*(P3-P2));
}

float D3(vec2 x, float t, vec2 P0, vec2 P1, vec2 P2, vec2 P3)
{
    return length(x-B3(t, P0, P1, P2, P3));
}

float D3Prime(vec2 x, float  t, vec2 P0, vec2 P1, vec2 P2, vec2 P3)
{
    return dot(x-B3(t,P0,P1,P2,P3), B3Prime(t,P0,P1,P2,P3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 1.*(fragCoord/iResolution.yy-.5);

    // Control points.
    vec2 p_0 = vec2(-.25,-.25)+.15*vec2(sin(iTime), cos(3.*iTime)), 
        p_1 = vec2(.25,-.25)+.5*vec2(sin(iTime), .5-sin(iTime)),
        p_2 = vec2(.25,.25)+.15*vec2(cos(iTime), sin(iTime)),
    	p_3 = vec2(.65,.35)+.15*vec2(sin(iTime), cos(iTime));

    float tmin = 0., tmax = 1., tnew, 
#ifdef QUADRATIC
        dmin = D2Prime(uv,tmin,p_0,p_1,p_2), 
        dmax = D2Prime(uv,tmax,p_0,p_1,p_2),
#else
        dmin = D3Prime(uv,tmin,p_0,p_1,p_2,p_3),
        dmax = D3Prime(uv,tmax,p_0,p_1,p_2,p_3),
#endif
        dnew;

    // Solve for polynomial roots with interval approximation.
    for(int i=0; i<10; ++i)
    {
        // Compute t and distance for the new test parameter tnew
        tnew = mix(tmin, tmax, .5);
#ifdef QUADRATIC
        dnew = D2Prime(uv,tnew,p_0,p_1,p_2);
#else
        dnew = D3Prime(uv,tnew,p_0,p_1,p_2,p_3);
#endif

        if(dnew>0.)
        {
            tmin = tnew;
            dmin = dnew;
        }
        else 
        {
            tmax = tnew;
            dmax = dnew;
        }
    }

    // Cap at the ends of the spline
    tmin = clamp(tmin, 0., 1.);

    // Compute distance
#ifdef QUADRATIC
    dmin = D2(uv, tmin, p_0, p_1, p_2);
#else
    dmin = D3(uv, tmin, p_0, p_1, p_2, p_3);
#endif

    vec3 col = k.yyy;

    // Draw distance isolines
    col += (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))*mod(dmin,0.1)*10.+vec3(1.,0.,0.)*step(dmin,.05);
    // Draw control points
    col += k.yxx*step(length(uv-p_0), .05)+k.xyx*step(length(uv-p_1), .05)+k.yxx*step(length(uv-p_2), .05);

#ifdef CUBIC
    col += k.yxy*step(length(uv-p_3), .05);
#endif

    fragColor = vec4(col,1.0);
}

