    int res = int(pow(2.0, float(bitdepth)));

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float zoom = 0.5 + (pow(iTime, 3.0) / 8.0);
    //zoom = 0.5;
    float xoffset = -(zoom / 1.0) * (iResolution.x / 2.85);
    float yoffset = -(zoom / 4.0) * (iResolution.x / 18.0);
    yoffset = 1.0;

    float c_re = ((fragCoord.x + xoffset) - iResolution.x/2.0)*4.0/(iResolution.x * zoom);
    float c_im = ((fragCoord.y + yoffset) - iResolution.y/2.0)*4.0/(iResolution.x * zoom);
    float x = 0.0;
    float y = 0.0;

    int max = int(pow(2.0, float(bitdepth * 3))) - 1;

    int iteration = 0;
    while (((x*x+y*y) <= 4.0) && (iteration < max)) {
        float x_new = x*x - y*y + c_re;
        y = 2.0*x*y + c_im;
        x = x_new;
        iteration++;
    }

    float b = float(iteration % int(res)) / float(res);
    float g = float((iteration >> bitdepth) % int(res)) / float(res);
    float r = float((iteration >> (bitdepth * 2)) % int(res)) / float(res);

    vec3 col = vec3(r,g,b);

    //col = vec3(1.0,1.0,1.0) - col;

    fragColor = vec4(col,1.0);    

    // Output to screen

}

// based on https://www.shadertoy.com/view/XdByD1

vec2 csqr( vec2 a )  { return vec2( a.x*a.x - a.y*a.y, 2.*a.x*a.y  ); }

vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph )//from iq
{
	vec3 oc = ro;// - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = 1.0; //b*b - c;
	return vec2(-b-h, -b+h );
}

float map(in vec3 p, vec2 sctime) {

	float res = 0.;

    vec3 c = p;
    c.xy = c.xy * sctime.x + vec2(c.y, c.x) * sctime.y;
	for (int i = 0; i < 10; ++i) 
    {
        p =.7*abs(p)/dot(p,p) -.7;
        p.yz= csqr(p.yz);
        p=p.zxy;
        res += exp(-19. * abs(dot(p,c)));        
	}
	return res/2.;
}

vec3 raymarch( in vec3 ro, vec3 rd, vec2 tminmax , vec2 sctime)
{
    //tminmax += vec2(1.,1.) * sin( iTime * 1.3)*3.0;
   	vec3 one3 = vec3(1.,1.,1.);
    vec3 t = one3 * tminmax.x;

    vec3 dt = vec3(.07, 0.02, 0.05);
    vec3 col= vec3(0.);
    vec3 c = one3 * 0.;
    for( int i=0; i<64; i++ )
	{
     	vec3 s = vec3(2.0, 3.0, 4.0);   
        t+=dt*exp(-s*c);
        vec3 a = step(t,one3*tminmax.y);
        vec3 pos = ro+t*rd;

        c.x = map(ro+t.x*rd, sctime);
        c.y = map(ro+t.y*rd, sctime);
        c.z = map(ro+t.z*rd, sctime);               

        col = mix(col, .99*col+ .08*c*c*c, a);
    }

    vec3 c0 = vec3(0.4,0.3,0.99);
    vec3 c1 = vec3(0.9,0.7,0.0);
    vec3 c2 = vec3(0.9,0.1,0.2);
    return c0 * col.x + c1 * col.y + c2 * col.z;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;
    vec2 m = vec2(0.);

    vec3 ro = vec3(6.);
    vec3 ta = vec3( 0.0 , 0.0, 0.0 );
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 4.0*ww );


    vec2 tmm = iSphere( ro, rd, vec4(0.,0.,0.,2.) );

	// raymarch
    vec3 col = raymarch(ro,rd,tmm, vec2(sin(iTime), cos(iTime)));

	// shade    
    col =  .5 *(log(1.+col));
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );

}

