	for (int i = 0; i < 6; i++)
	{

		p.xyz = clamp(p.xyz, -1.0, 1.0) * 2.0 - p.xyz;
		float r2 = dot(p.xyz, p.xyz);
		p *= clamp(max(minRad2/r2, minRad2), 0.0, 1.0);

		p = p*scale.xyz + p0.xyz;
		trap = min(trap, r2);
	}
	// |c.x|: log final distance (fractional iteration count)
	// |c.y|: spherical orbit trap at (0,0,0)
	vec2 c = clamp(vec2( 0.3333*log(dot(p,p))-1.0, sqrt(trap) ), 0.0, 1.0);

    float t = mod(length(pos) - gTime*150., 16.0);
    surfaceColour1 = mix( surfaceColour1, vec3(.4, 3.0, 5.), pow(smoothstep(0.0, .3, t) * smoothstep(0.6, .3, t), 10.0));
	return mix(mix(surfaceColour1, surfaceColour2, c.y), surfaceColour3, c.x);
}


//----------------------------------------------------------------------------------------
vec3 GetNormal(vec3 pos, float distance)
{
    distance *= 0.001+.0001;
	vec2 eps = vec2(distance, 0.0);
	vec3 nor = vec3(
	    Map(pos+eps.xyy) - Map(pos-eps.xyy),
	    Map(pos+eps.yxy) - Map(pos-eps.yxy),
	    Map(pos+eps.yyx) - Map(pos-eps.yyx));
	return normalize(nor);
}

//----------------------------------------------------------------------------------------
float GetSky(vec3 pos)
{
    pos *= 2.3;
	float t = Noise(pos);
    t += Noise(pos * 2.1) * .5;
    t += Noise(pos * 4.3) * .25;
    t += Noise(pos * 7.9) * .125;
	return t;
}

//----------------------------------------------------------------------------------------
float BinarySubdivision(in vec3 rO, in vec3 rD, vec2 t)
{
    float halfwayT;

    for (int i = 0; i < 6; i++)
    {

        halfwayT = dot(t, vec2(.5));
        float d = Map(rO + halfwayT*rD); 
        //if (abs(d) < 0.001) break;
        t = mix(vec2(t.x, halfwayT), vec2(halfwayT, t.y), step(0.0005, d));

    }

	return halfwayT;
}

//----------------------------------------------------------------------------------------
vec2 Scene(in vec3 rO, in vec3 rD, in vec2 fragCoord)
{
	float t = .05 + 0.05 * texture(iChannel0, fragCoord.xy / iChannelResolution[0].xy).y;
	vec3 p = vec3(0.0);
    float oldT = 0.0;
    bool hit = false;
    float glow = 0.0;
    vec2 dist;
	for( int j=0; j < 100; j++ )
	{
		if (t > 12.0) break;
        p = rO + t*rD;

		float h = Map(p);

		if(h  <0.0005)
		{
            dist = vec2(oldT, t);
            hit = true;
            break;
        }
       	glow += clamp(.05-h, 0.0, .4);
        oldT = t;
      	t +=  h + t*0.001;
 	}
    if (!hit)
        t = 1000.0;
    else       t = BinarySubdivision(rO, rD, dist);
    return vec2(t, clamp(glow*.25, 0.0, 1.0));

}

//----------------------------------------------------------------------------------------
float Hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 33758.5453)-.5;
} 

//----------------------------------------------------------------------------------------
vec3 PostEffects(vec3 rgb, vec2 xy)
{
	// Gamma first...


	// Then...
	#define CONTRAST 1.08
	#define SATURATION 1.5
	#define BRIGHTNESS 1.5
	rgb = mix(vec3(.5), mix(vec3(dot(vec3(.2125, .7154, .0721), rgb*BRIGHTNESS)), rgb*BRIGHTNESS, SATURATION), CONTRAST);
	// Noise...
	//rgb = clamp(rgb+Hash(xy*iTime)*.1, 0.0, 1.0);
	// Vignette...
	rgb *= .5 + 0.5*pow(20.0*xy.x*xy.y*(1.0-xy.x)*(1.0-xy.y), 0.2);	

    rgb = pow(rgb, vec3(0.47 ));
	return rgb;
}

//----------------------------------------------------------------------------------------
float Shadow( in vec3 ro, in vec3 rd)
{
	float res = 1.0;
    float t = 0.05;
	float h;

    for (int i = 0; i < 8; i++)
	{
		h = Map( ro + rd*t );
		res = min(6.0*h / t, res);
		t += h;
	}
    return max(res, 0.0);
}

//----------------------------------------------------------------------------------------
mat3 RotationMatrix(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

//----------------------------------------------------------------------------------------
vec3 LightSource(vec3 spotLight, vec3 dir, float dis)
{
    float g = 0.0;
    if (length(spotLight) < dis)
    {
		g = pow(max(dot(normalize(spotLight), dir), 0.0), 500.0);
    }

    return vec3(.6) * g;
}

//----------------------------------------------------------------------------------------
vec3 CameraPath( float t )
{
    vec3 p = vec3(-.78 + 3. * sin(2.14*t),.05+2.5 * sin(.942*t+1.3),.05 + 3.5 * cos(3.594*t) );
	return p;
} 

//----------------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float m = (iMouse.x/iResolution.x)*300.0;
	gTime = (iTime+m)*.01 + 15.00;
    vec2 xy = fragCoord.xy / iResolution.xy;
	vec2 uv = (-1.0 + 2.0 * xy) * vec2(iResolution.x/iResolution.y, 1.0);


	#ifdef STEREO
	float isRed = mod(fragCoord.x + mod(fragCoord.y, 2.0),2.0);
	#endif

	vec3 cameraPos	= CameraPath(gTime);
    vec3 camTar		= CameraPath(gTime + .01);

	float roll = 13.0*sin(gTime*.5+.4);
	vec3 cw = normalize(camTar-cameraPos);

	vec3 cp = vec3(sin(roll), cos(roll),0.0);
	vec3 cu = normalize(cross(cw,cp));

	vec3 cv = normalize(cross(cu,cw));
    cw = RotationMatrix(cv, sin(-gTime*20.0)*.7) * cw;
	vec3 dir = normalize(uv.x*cu + uv.y*cv + 1.3*cw);

	#ifdef STEREO
	cameraPos += .008*cu*isRed; // move camera to the right
	#endif

    vec3 spotLight = CameraPath(gTime + .03) + vec3(sin(gTime*18.4), cos(gTime*17.98), sin(gTime * 22.53))*.2;
	vec3 col = vec3(0.0);
    vec3 sky = vec3(0.03, .04, .05) * GetSky(dir);
	vec2 ret = Scene(cameraPos, dir,fragCoord);

    if (ret.x < 900.0)
    {
		vec3 p = cameraPos + ret.x*dir; 
		vec3 nor = GetNormal(p, ret.x);

       	vec3 spot = spotLight - p;
		float atten = length(spot);

        spot /= atten;

        float shaSpot = Shadow(p, spot);
        float shaSun = Shadow(p, sunDir);

       	float bri = max(dot(spot, nor), 0.0) / pow(atten, 1.5) * .15;
        float briSun = max(dot(sunDir, nor), 0.0) * .3;

       col = Colour(p, ret.x);
       col = (col * bri * shaSpot) + (col * briSun* shaSun);

       vec3 ref = reflect(dir, nor);
       col += pow(max(dot(spot,  ref), 0.0), 10.0) * 2.0 * shaSpot * bri;
       col += pow(max(dot(sunDir, ref), 0.0), 10.0) * 2.0 * shaSun  * bri;
    }

    col = mix(sky, col, min(exp(-ret.x+1.5), 1.0));
    col += vec3(pow(abs(ret.y), 2.)) * vec3(.02, .04, .1);

    col += LightSource(spotLight-cameraPos, dir, ret.x);
	col = PostEffects(col, xy);	


	#ifdef STEREO	
	col *= vec3( isRed, 1.0-isRed, 1.0-isRed );	
	#endif

	fragColor=vec4(col,1.0);
}

//--------------------------------------------------------------------------

mat2 getMat( float angle ){
    return mat2( cos( angle ), -sin( angle ), sin( angle ), cos( angle ) );
}

//YI-Wen Lin:
//https://gist.github.com/yiwenl/745bfea7f04c456e0101
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - 3.);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
	vec2 uv = ( fragCoord.xy / iResolution.xy ) * 2.0 - 1.0;

    //preserve aspect ratio
	uv.x *= iResolution.x / iResolution.y;

    //radius
      float r = length(uv) * 1.618033988749894848; //1.618... = Golden ration

      //grid pattern
      vec2 chk = floor( uv * 8. );
      vec3 col = vec3( 0.65 ) + mod( chk.x + chk.y, 2. ) * vec3(.25);

      //sway
      uv *= getMat( sin( iTime ) * .2 );

      //bulge
      uv *= ( ( 1. - sqrt( 1. - r ) ) / r );

      //vertical shift
      uv.y += iTime * .1;

      //stripe
      float stripes = 7.;
      float hue = fract( floor( uv.y * stripes ) / stripes  );

      	//smooth-ish blend S > 1. / bloom-ish with S = 0.5
    	///*
        float S = 0.5;
        float prev = fract(  floor( uv.y * stripes ) / stripes ) - 1. / stripes;
        float hueMix = clamp( fract( uv.y * stripes ) * S, 0.,1.);
        hue = mix( prev, hue, hueMix );
		//*/

      //convert Hue to RGB
      vec3 hsv = hsv2rgb( vec3( hue, .75, 1. ) );

      //blend
      float alpha = smoothstep(  0.99, 1.,r );
      fragColor.rgb = mix( hsv, col, alpha );
      fragColor.a = 1.;
}

#define MAX_MARCHING_STEPS 200
#define EPSILON 0.001
//#define AXIS_RADIUS 0.01;
#define AMBIENT_LIGHT 0.01

#define ROTATE_EYE 0
#define ANIMATE_FIGURE 0
#define ROTATE_LIGHT 1
#define VIEW_AXIS 0

#define EYE_DIST 18.0
#define WALL_DIST 20.0

float AXIS_RADIUS = (2.0) * EYE_DIST / 1000.0;

mat2 cubeRot;
vec3 cubeTra;

vec3 light1Pos_base = vec3(0.0,2.0,3.0);
vec3 light1Pos;

int lightInScene = 1;

mat2 rot2d( float angle ) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2( 
        		c, -s,
                s,  c
               );
}

float unionSDF(float sdf1, float sdf2) {
    return min(sdf1,sdf2);
}

float intersectSDF(float sdf1, float sdf2) {
    return max(sdf1,sdf2);
}

float excludeSDF(float sdf1, float sdf2) {
    return intersectSDF(sdf1, -sdf2);
}

float sphereSDF(vec3 p, float radio) {
    // SPHERE
    return length(p) - radio;
}
float leftHSpaceSDF(float coord, float limit) {
    return coord - limit;
}
float rightHSpaceSDF(float coord, float limit) {
    return limit - coord;
}
float cubeSDF(vec3 p, float lado)
{
    lado *= 0.5;
    // CUBE
    float dx = max( leftHSpaceSDF(p.x,lado) , rightHSpaceSDF(p.x,-lado) );
    float dy = max( leftHSpaceSDF(p.y,lado) , rightHSpaceSDF(p.y,-lado) );
    float dz = max( leftHSpaceSDF(p.z,lado) , rightHSpaceSDF(p.z,-lado) );

    float ret;
    ret = max(max(dx,dy),dz);

    return ret;
}

float ejeXSDF(vec3 p) { return length(p.yz) - AXIS_RADIUS; }
float ejeYSDF(vec3 p) { return length(p.xz) - AXIS_RADIUS; }
float ejeZSDF(vec3 p) { return length(p.xy) - AXIS_RADIUS; }

float sceneSDF(vec3 p, out int mat) {

    mat = 1;

    float ret;
    float sphere1, sphere2, sphere3, sphere4;
    float cube;

    float commonRadius = 1.3;

    vec3 sphereCentre1 = vec3(0.0,0.0,0.0);
    float sphereRadius1 = commonRadius; //(1.0 + sin(iTime)) * 0.5;

	vec3 sphereCentre2 = vec3(0.0,0.0,0.0);
    float sphereRadius2 = 0.5; // (1.0 + sin(iTime)) * 0.5;
	/*
    vec3 sphereCentre3 = vec3(0.0,1.0,0.0);
    float sphereRadius3 = commonRadius; // (1.0 + sin(iTime)) * 0.5;

    vec3 sphereCentre4 = vec3(0.0,-1.0,0.0);
    float sphereRadius4 = commonRadius; // (1.0 + sin(iTime)) * 0.5;
	*/

    // CUBE TRANSFORMATIONS
    vec3 pp = p;
    pp.xyz += cubeTra;
    //pp.yz = cubeRot * pp.yz;
    //pp.xz = cubeRot * pp.xz;
    pp.xy = cubeRot * pp.xy;

    sphere1 = sphereSDF(pp - sphereCentre1, sphereRadius1);
    sphere2 = sphereSDF(p - sphereCentre2, sphereRadius2);
    //sphere3 = sphereSDF(pp - sphereCentre3, sphereRadius3);
    //sphere4 = sphereSDF(pp - sphereCentre4, sphereRadius4);

    cube = cubeSDF(pp , 2.0);

    ret = 999.0;

    ret = unionSDF(ret,cube);

    // INTERSECTION
    //ret = intersectSDF(ret,sphere);

    /* UNION 
    ret = unionSDF(ret, sphere1);
    ret = unionSDF(ret, sphere2);
    ret = unionSDF(ret, sphere3);
    ret = unionSDF(ret, sphere4);
    //*/

    ///* EXCLUSION
	ret = excludeSDF(ret,sphere1);
    //ret = excludeSDF(ret,sphere2);
    //ret = excludeSDF(ret,sphere3);
    //ret = excludeSDF(ret,sphere4);
	//*/

	ret = unionSDF(ret, sphere2);

    /* INTERSECTION
	float fig2;
    fig2 = 999.0;
	fig2 = unionSDF(fig2,sphere1);
    fig2 = unionSDF(fig2,sphere2);
    fig2 = unionSDF(fig2,sphere3);
    fig2 = unionSDF(fig2,sphere4);

    ret = intersectSDF(ret,fig2);
	//*/


    // UNION CON LOS EJES
    if ( ret > 0.0 
        	&& lightInScene > 0 
        	&& VIEW_AXIS > 0
       ) {
        float eje;

        eje = ejeXSDF(p);
        ret = unionSDF(ret, eje);
        if ( eje < EPSILON ) mat = 2;

        eje = ejeYSDF(p);
        ret = unionSDF(ret, eje);
        if ( eje < EPSILON ) mat = 3;

        eje = ejeZSDF(p);
        ret = unionSDF(ret, eje);
        if ( eje < EPSILON ) mat = 4;
    }

    if ( lightInScene > 0 )
    {
        float lightDist = sphereSDF(p - light1Pos, 0.15);
        if ( lightDist < EPSILON ) mat = 5;

        ret = unionSDF(ret, lightDist);
    }

    ret = unionSDF(ret,leftHSpaceSDF(p.y, -3.0));
    ret = unionSDF(ret,leftHSpaceSDF(p.z, -WALL_DIST));
    ret = unionSDF(ret,leftHSpaceSDF(p.x, -WALL_DIST));
    ret = unionSDF(ret,rightHSpaceSDF(p.z, WALL_DIST));
    ret = unionSDF(ret,rightHSpaceSDF(p.x, WALL_DIST));

    return ret;
}

float lightSDF(vec3 p, vec3 l, out vec3 col) 
{
    float ret = sphereSDF(l, 0.1);
    if ( ret < EPSILON )
    {
        col = vec3(1.0,1.0,0.0);
    }
    return ret;
}

float rayMarch(vec3 eye, vec3 viewRayDirection, out int mat) {
    float start = 0.0;
    float end = 100.0;
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        //vec3 col;
        float dist = sceneSDF(eye + depth * viewRayDirection, mat);
        if (dist < EPSILON) {
            // We're inside the scene surface!
            return depth;
        }
        // Move along the view ray
        depth += dist;

        if (depth >= end) {
            // Gone too far; give up
            return end;
        }
    }
    return end;
}

vec3 estimateNormal(vec3 p) {
    int col;
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z),col) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z),col),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z),col) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z),col),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON),col) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON),col)
    ));
}

vec4 image( in vec2 fragCoord, in vec2 resolution )
{
    vec4 fragColor = vec4(0, 0, 0, 1);

    // MOELO TRANFORMATIONS
    if ( ANIMATE_FIGURE > 0 )
    {
		cubeRot = rot2d(0.5*iTime);
		//cubeTra = vec3(0.0, 0.0, 5.0*sin(1.7*iTime));
        //cubeTra.x = 1.5 * sin(3.0*iTime);
    }
    else
    {
        cubeRot = rot2d(0.0);
        cubeTra = vec3(3.0,0.0,0.0);
    }
    //cubeTra = vec3(-2.0,0.0,0.0);

    // LIGHT ROTATION
	light1Pos = light1Pos_base;
    if ( ROTATE_LIGHT > 0 )
    {
        mat2 rot = rot2d(iTime);
        light1Pos.xz *= rot;  
    }

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / resolution.xy - 0.5;
    uv.y *= resolution.y / resolution.x;

	vec3 eye;
    if ( ROTATE_EYE == 0 )
    {
        eye = vec3(EYE_DIST*cos(1.1),3.0,EYE_DIST * sin(1.1));
    }
    else
    {
    	float eyeAngle = 0.5 * iTime;
        eye.xz = EYE_DIST * vec2 ( sin(eyeAngle), cos(eyeAngle) );
        eye.y = 3.0;
    }

    vec3 lookAt = vec3 ( 0.0, 0.0, 0.0 );
    vec3 eyeDir = normalize(lookAt - eye);
    vec3 up = vec3 ( 0.0, 1.0, 0.0 );
    vec3 right = normalize(cross(eyeDir,up));
    up = cross(right,eyeDir);

    vec3 screen = eye + eyeDir + uv.x * right + uv.y * up;
    vec3 rayDir = normalize(screen - eye);

    int material;
    float dist = rayMarch(eye, rayDir, material);

	vec3 col;

	if ( material == 1 ) col = vec3(1.0,1.0,1.0);

    if ( material >= 2 && material <= 5 ) {
        if ( material == 2 ) col = vec3(1.0,0.0,0.0);
        if ( material == 3 ) col = vec3(0.0,1.0,0.0);
        if ( material == 4 ) col = vec3(0.0,0.0,1.0);
        if ( material == 5 ) col = vec3(1.0,1.0,0.0);
        fragColor = vec4(col,1.0);
    }
    else 
    {

        vec3 p = eye + dist * rayDir;

        if ( dist < 100.0 ) {

            vec3 viewDir = - rayDir;

            vec3 normal = estimateNormal(p);

            // DIRECTIONAL LIGHT
            //vec3 light = -light1Dir;
            //float ligthPower = 1.0;

            // POINT LIGHT
            vec3 light = light1Pos - p;
            float lightDist = length(light);
            float ligthPower = pow(3.0 / lightDist,1.0);

            light = normalize(light);

            // DIFFUSE LIGHT
            float light1Dif = max(dot(light, normal),0.0);

            // SPECULAR LIGHT
            vec3 reflectDir = reflect(-light, normal);
            float angle = max(dot(reflectDir, viewDir),0.0);
            float light1Spe = pow(angle,64.0);

            // VIEW NORMALS
            //fragColor = vec4(normal,1.0);

            //
            fragColor = vec4(AMBIENT_LIGHT);

            // VIEW DIFF LIGHT
            fragColor += 1.0 * ligthPower * vec4(light1Dif);

            // VIEW SPEC LIGHT
            fragColor += 1.0 * ligthPower * vec4(light1Spe);

            //fragColor = vec4(abs(normal),1.0);
            //fragColor = vec4(light,1.0);
            //fragColor = vec4(pow(4.0/lightDist,10.0));

            fragColor.rgb *= col;

            // VERIFY LIGHT OCCLUSION
            ///*
            lightInScene = 0;
            rayDir = normalize(p-light1Pos);
            float distFromLight = rayMarch(light1Pos, rayDir, material);
            float diff = length(p-light1Pos) -distFromLight ;
            if ( diff > 10.0 * EPSILON )
            {
                fragColor = vec4(AMBIENT_LIGHT);
            }
			//*/
        }
    }

    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float subSample = 3.0;


    if ( subSample < 2.0 )
    {
    	fragColor += image(fragCoord, iResolution.xy);
    }
    else
    {
        vec2 subResolution = iResolution.xy * subSample;

        float samples = subSample * subSample;
        float sampleWeight = 1.0 / samples;

        for ( float i = 0.0; i < subSample ; i ++ )
        {
            for ( float j = 0.0; j < subSample ; j ++ )
            {
                vec2 newCoord = fragCoord * subSample + vec2(i,j);
                fragColor += image(newCoord, subResolution) * sampleWeight;
            }
        }
    }
}

precision highp float;
float PI = 3.14159265359;

vec2 kaleidoscope(vec2 uv, vec2 offset, float splits)
{
    // XY coord to angle
    float angle = atan(uv.y, uv.x);
    // Normalize angle (0 - 1)
    angle = ((angle / PI) + 1.0) * 0.5;
    // Rotate by 90°
    angle = angle + 0.25 * iTime * 0.05;
    // Split angle 
    angle = mod(angle, 1.0 / splits) * splits;

    // Warp angle
    angle = -abs(2.0*angle - 1.0) + 1.0;

    angle = angle*0.1;

    // y is just dist from center
    float y = length(uv);

    angle = angle * (y*3.0);

    return vec2(angle, y) + offset;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    vec2 offset = vec2(iTime   *0.05,  iTime   *0.5);
    vec2 uv  = kaleidoscope(p, offset, 10.);
    // pattern: cosines
    float f = cos(8.*uv.x)*cos(6.0*uv.y);

    // color fetch: palette
    vec3 col =0.+  sin( 3.1416*f + vec3(1.,0.5,1.0) );

    // output:
    fragColor = vec4( col, 1.0 );


    // Output to screen
    fragColor = vec4(col,1.0);
}

