	float reflective_fract;
};


const int obj_count = 10;
const int light_count = 3;
shape shapes[obj_count];
light lights[light_count];


//ray must be normalized!
float ray_sphere_isect(ray r, const int id) {
	shape s = shapes[id];

	float a = dot(r.d, r.d);
	vec3 s0_r0 = r.o - s.pos;
	float b = 2.0*dot(r.d, s0_r0);
	float c = dot(s0_r0, s0_r0) - s.offset.x*s.offset.x;
	float b2 = b*b;
	if (b2 - 4.0*a*c < 0.0) {
		return -1.0;
	}
	return (-b - sqrt(b2 - 4.0*a*c)) / (2.0*a); 
}

vec3 diffuse_texture(vec3 p, int t) {
	if(t==0)
		return vec3(1.0,0.,0.0);
	if(t==1)
		return vec3(1.0 - (
			smoothstep(0.3, 0.5, fract(p.z)) -
			smoothstep(0.5, 0.7, fract(p.z)) +
			smoothstep(0.3, 0.5, fract(p.y)) -
			smoothstep(0.5, 0.7, fract(p.y))
			));
	if(t==2)
		return vec3(1.0 - (
			smoothstep(0.3, 0.5, fract(p.x)) -
			smoothstep(0.5, 0.7, fract(p.x)) +
			smoothstep(0.3, 0.5, fract(p.z)) -
			smoothstep(0.5, 0.7, fract(p.z))
			));
	if(t==3)
		return vec3(1.0 - (
			smoothstep(0.3, 0.5, fract(p.x)) -
			smoothstep(0.5, 0.7, fract(p.x)) +
			smoothstep(0.3, 0.5, fract(p.y)) -
			smoothstep(0.5, 0.7, fract(p.y))
			));
	if(t==4)
		return vec3(0.0,1.0,0.0);
	if(t==5)
		return vec3(0.0,0.0,1.0);
	return vec3(0.2,0.2,0.2);

}

float ray_plane_isect(ray r, const int id) {
	shape s = shapes[id];

	float denom = dot(s.offset, r.d);
	if(denom > 1.0e-6) {
		float t = dot((s.pos - r.o), s.offset) / denom;
		if(t > 1.0e-6) {
			return t;
		}
	}
	return -1.0;
}

//Should isect_dist return dist*dist?
float isect_dist(ray r, int id) {
	if (shapes[id].type == 0) {
		return ray_sphere_isect(r, id);
	} else {
		return ray_plane_isect(r, id);
	}
	return -1.;
}

vec3 isect_normal(vec3 position, int id) {
	if (shapes[id].type == 0) {
		return(normalize(position - shapes[id].pos));
	} else {
		return(-shapes[id].offset);
	}
}

//TODO: 
// * Light attenuation is broken. Gets brighter with distance.
// * visible light sources.
vec3 get_color_for_ray(ray r) {
	int ignore = -1;
	vec3 retval = vec3(0.0);
	/* visible light sources...
	for(lid=0; lid<light_count; lid++) {
		vec3
	}
	*/
	float attenuation = 1.0;
	int depth = 0;
	vec3 position, normal, reflection, to_v;
	while(depth < 4 && attenuation > 0.05) {
		depth += 1;
		float min_dist = 100000.;
		int nearest_obj = -1;
		for (int obj_id=0; obj_id<obj_count; obj_id++) {
			if (obj_id == ignore) {
				continue;
			}
			float t = isect_dist(r, obj_id);
			if (t > 0.0 && t < min_dist) {
				nearest_obj = obj_id;
				min_dist = t;
			}
		}
		if (nearest_obj < 0) {
			return retval;
		}
		position = r.o + r.d*min_dist;
		normal = isect_normal(position, nearest_obj);
		if (shapes[nearest_obj].diffuse_fract > 0.0 || shapes[nearest_obj].reflective_fract > 0.0) {
			to_v = normalize(r.o - position);
			reflection = reflect(to_v, normal);
		}
		if (shapes[nearest_obj].diffuse_fract > 0.0) {
			for (int lid=0; lid<light_count; lid++) {
				vec3 to_l = lights[lid].pos - position;
				ray shadow_test = ray(position, to_l);  //NOT NORMALIZED
				bool shadowed = false;
				if (dot(normal, to_l) < 0.0) {
					shadowed = true;
				} else {
					for (int obj_id=0; obj_id<obj_count; obj_id++) {
						float dist = isect_dist(shadow_test, obj_id);
						if (dist > 0.0 && dist < 1.0) { //1.0 because shadow_test isn't normalized
							shadowed = true;
							break;
						}
					}
				}
				if (!shadowed) {
					to_l = normalize(to_l);
					retval += 
						attenuation * (
							diffuse_texture(position, shapes[nearest_obj].diffuse_texture)
							* dot(to_l, normal) 
							* shapes[nearest_obj].diffuse_fract
							/ sqrt(dot(to_l, to_l))
							//SPECULAR:
							+ lights[lid].col
								* pow(dot(to_l, reflection), 50.0)
								* shapes[nearest_obj].specular_fract
						);
				}
			}
		}
		if (shapes[nearest_obj].reflective_fract <= 0.0) {
			return retval;
		}
		retval *= (1.0 - shapes[nearest_obj].reflective_fract);
		attenuation *= shapes[nearest_obj].reflective_fract;
		r.o = position;
		r.d = -reflection;

		ignore = nearest_obj;
	}
	return retval;
}

