vec2 f(vec2 z){
    vec2 result = vec2(1, 0);

    for(int i = 1; i <= numeratorDegree; i++){
        vec4 r = rand(i);
        vec2 p = r.xy + r.z*sin(r.w*iTime + pi.xy);
        result = cmul(result, z - p);
    }
    for(int i = 1; i <= denominatorDegree; i++){
        vec4 r = rand(-i);
