const float ffd = 3.1415927 * .25;
camera makeIsometric(camera cam) {
    cam.cam.yz = rotate(cam.cam.yz, ffd);
    cam.cam.xz = rotate(cam.cam.xz, ffd);
    cam.ray.yz = rotate(cam.ray.yz, ffd);
    cam.ray.xz = rotate(cam.ray.xz, ffd);
    return cam;
}

camera mixCameras(camera a, camera b, float x) {
    x = smoothstep(0., 1., x);
	return camera(
        mix(a.cam, b.cam, x),
        mix(a.ray, b.ray, x));
}

camera getCamera(int n, vec2 uv) {
    switch(n) {
        // case 1: // a perspective camera
        case 2: // an isometric orthographic camera
            return makeIsometric(camera(
                vec3(uv * 6., 0.),
                vec3(0., 0., 1.)));
        case 3: // a balls-tripping reverse perspective camera
            return makeIsometric(camera(
                vec3(uv * 12., 0.),
                normalize(vec3(-uv, 3.))));
    }
    // fall back to camera 1:
    // an isometric perspective camera
    return makeIsometric(camera(
    	vec3(0.),
    	normalize(vec3(uv, 3.))));
}

camera mixedCamera(int a, int b, float x, vec2 uv) {
    return mixCameras(getCamera(a, uv), getCamera(b, uv), x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -(fragCoord - iResolution.xy * 0.5) / iResolution.y;

 	float bigTime = mod(iTime / 10., 3.),
        smallTime = fract(bigTime);

    camera cam;

    if (bigTime < 1.)
        cam = mixedCamera(2, 1, smallTime, uv);
    else if (bigTime < 2.)
        cam = mixedCamera(1, 3, smallTime, uv);
    else
        cam = mixedCamera(3, 2, smallTime, uv);
    //cam = getCamera(3, uv);
    //cam = getCamera(3, uv);

    cam.cam.x += sin(iTime * 0.1);
    cam.cam.y += cos(iTime * 0.682);

	// so we have to march the ray through the cubes
    // nothing interesting happens within 16 units of the camera
    vec3 p = cam.cam + cam.ray * 16., ray = cam.ray;
    float face;
    vec3 cube;

    for (int i = 0; i < 70; ++i) {
        vec3 distToNextPixel = 1. - fract(p * sign(ray));
        vec3 timeToNextPixel = abs(distToNextPixel / ray);

        if (timeToNextPixel.x < timeToNextPixel.y
            && timeToNextPixel.x < timeToNextPixel.z)
        {
            face = .3;
            p += ray * timeToNextPixel.x;
        }
        else if (timeToNextPixel.y < timeToNextPixel.x
                 && timeToNextPixel.y < timeToNextPixel.z)
        {
            face = .6;
            p += ray * timeToNextPixel.y;
        }
        else {
            face = .9;
            p += ray * timeToNextPixel.z;
        }
        cube = floor(p);
        if (abs(cube.x) + abs(cube.y) + abs(cube.z) >= 30.)
            break;
    }

    vec3 col;

    #ifdef COLOURS
    switch (int(mod(cube.x + cube.y - cube.z, 3.))) {
        case 0: switch(int(mod(cube.x, 3.))) {
            case 1: col = vec3(.5, .9, .2); break;
            case 2: col = vec3(.9, .4, .2); break;
            case 0: col = vec3(.2, .3, .9); break;
        } break;
    	case 1: switch(int(mod(cube.x, 3.))) {
            case 2: col = vec3(.5, .9, .2); break;
            case 0: col = vec3(.9, .4, .2); break;
            case 1: col = vec3(.2, .3, .9); break;
        } break;
    	case 2: switch(int(mod(cube.x, 3.))) {
            case 0: col = vec3(.5, .9, .2); break;
            case 1: col = vec3(.9, .4, .2); break;
            case 2: col = vec3(.2, .3, .9); break;
        } break;
    }
    #else
    col = vec3(.9);
    #endif

    fragColor = vec4(col * face, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float res = 4.0;
    int max = int(pow(2.0, res * 3.0));

    float div = pow(2.0, res);

    vec2 c = vec2(-0.7, 0.27015);
    vec2 move = vec2(- 0.8,0.0);
    float zoom = 0.5 + (pow(iTime, 3.0) / 8.0);

    int iteration = max;

    vec2 z;
    z.x = 1.5*(fragCoord.x - iResolution.x/2.0)/(0.5*zoom*iResolution.x) + move.x;
    z.y = 1.0*(fragCoord.y - iResolution.y/2.0)/(0.5*zoom*iResolution.y) + move.y;

    while (((z.x*z.x+z.y*z.y) < 4.0) && (iteration > 1)) {
        float tmp = (z.x*z.x - z.y*z.y) + c.x;
        z.y = 2.0*z.x*z.y + c.y;
        z.x = tmp;
        iteration--;
    }

    float r = float(iteration % int(div)) / div;
    float g = float((iteration >> int(res)) % int(div)) / div;
    float b = float((iteration >> (int(res) * 2)) % int(div)) / div;

    fragColor = vec4(r,g,b,1.0);        
}

vec3 v;
vec3 lightSource = vec3(20.0, 0.0, 6.0);
float bLightSource = 0.6;
float ba = 0.4;

vec2 random2(vec2 st, float n){
    //computed values have to be n-periodic in y-direction
    //on both poles of the sphere gradients are identically
    //set to zero to avoid discontinuity
    vec2 stPeriodic = vec2(st.x, mod(st.y, n));
    if(abs(st.x)<=1.01 || abs(st.x-n)<=1.01){
        return vec2(0.0);
    }
    else{
    //hash function to generate noise
    stPeriodic = vec2( dot(stPeriodic,vec2(127.1,311.7)),
              dot(stPeriodic,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(stPeriodic)*43758.177);
    }

    }





float gNoise(vec2 st, float n) {
    //gradient noise
	vec2 i = floor(st);
	vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0), n), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0), n), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0), n), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0), n), f - vec2(1.0,1.0) ), u.x), u.y);
    }

float pi(){
 	return radians(180.0);   
}


vec2 intersectCircle(vec2 cp, vec2 rView){
    //compute p and q from pq-formula
            float cp_projected = dot(rView, cp);
            return vec2(cp_projected, pow(cp_projected, 2.0)-dot(cp, cp));
    }

vec2 intersectBall(vec3 cp, vec3 rView){
    //compute p and q from pq-formula
            float cp_projected = dot(rView, cp);
            return vec2(cp_projected, pow(cp_projected, 2.0)-dot(cp, cp));
    }

float diffuse(vec3 p, vec3 normal){
            vec3 dirLight = normalize(lightSource-p);
            return bLightSource*max(0.0, dot(normal, dirLight));
    }


float specular(vec3 normal, vec3 p, vec3 rCamera){
            vec3 dir_light = normalize(p-lightSource);
            vec3 dir_reflect = reflect(dir_light, normal);
            float spec = pow(max(0.0, dot(rCamera, dir_reflect)), 200.0);
            return spec;

    }

vec3 chess(vec3 p, vec3 pFrag){
    //computes the chess pattern with diffuse lightning
    vec3 cSand = vec3(0.76, 0.7, 0.5);
    vec3 cSandStone = vec3(0.76, 0.57, 0.5);
    vec3 cRock = vec3(0.71, 0.7, 0.65);

    float tileSize = 4.0;
    float pixSize = 3.0*2.0*length(v-p)
        							/(min(iResolution.x, iResolution.y)
                                    *length(v-pFrag)
                                    *tileSize);

    vec2 ij = floor(p.xy/tileSize);
   	vec3 cChess;
    if(mod(ij.x, 2.0)==mod(ij.y, 2.0)){
        cChess = cSand;
    }
    else{
     	cChess = cSandStone;   
    }

    vec2 pLocal = abs(fract(p.xy/tileSize)-0.5);
    float maxLocal = max(pLocal.x, pLocal.y);

    float bd = diffuse(p, vec3(0.0, 0.0, 1.0));
    return (ba+bd)*mix(cRock, cChess, smoothstep(0.0, pixSize, 0.49-maxLocal));  
}

vec3 rostrum(vec3 p, vec3 rView, float radius){
    //essentially intersect ray with cylinder
    vec3 cRock = vec3(0.71, 0.7, 0.65);
    float h = 2.0;

    vec2 sol = intersectCircle(-p.xy, normalize(rView.xy));

    vec3 pCylinder = p + rView*
        				(sol.x+sqrt(sol.y+pow(radius, 2.0)))
                         /length(rView.xy);

    vec3 nCylinder = normalize(vec3(-pCylinder.xy, 0.0));
    vec3 pPlane = p - (p.z-h) * rView / rView.z;

    float bd = mix(diffuse(pCylinder, nCylinder),
             diffuse(pPlane, vec3(0.0, 0.0, 1.0)),
             smoothstep(h-0.1, h, pCylinder.z));
    return (ba+bd)*cRock;
}

vec3 computeBackground(vec3 p, vec3 rView){
 	vec3 cSand = vec3(0.76, 0.7, 0.5);
    vec3 cSandStone = vec3(0.76, 0.57, 0.5);
    vec3 cSky = vec3(0.5, 0.76, 0.7);

    //check if XY-plane is visible
    if(dot(rView, vec3(0.0, 0.0, 1.0))>-0.01*3.14){
     	return cSky;   
    }

    //calculate intersection with XY-plane
    vec3 pXY = p - p.z * rView / rView.z;
    float l0 = length(pXY);
    float l1 = length(p - (p.z-2.0) * rView / rView.z);


    if(l1>30.0){

     	return cSky;  
    }
    else if(l0>25.0){
        vec3 cRostrum = rostrum(p, rView, 25.0);
        vec3 mix0 = mix(chess(pXY, p), cRostrum,
                   smoothstep(25.0, 25.4, l0));  
     	return mix(mix0, cSky,
                   smoothstep(29.3, 30.0, l1));   
    }
    else{
        return chess(pXY, p); 

    }
}




mat3 rotationMatrix(const float angle, const vec3 axis){
 	float alpha = radians(angle);
    float c = cos(alpha);
    float s = sin(alpha);
    mat3 R;
    R[0] = axis * axis.x * (1.0-c) + vec3(c, axis.z*s, -axis.y*s);
    R[1] = axis * axis.y * (1.0-c) + vec3(-axis.z*s, c, axis.x*s);
    R[2] = axis * axis.z * (1.0-c) + vec3(axis.y*s, -axis.x*s, c);
    return R;
}

vec3 getCameraPosition(vec4 mouse){
    //computes position of camera from difference between
    //clicked and current pixel-coordinates
    vec3 v0 = 20.0*vec3(1.0, 0.0, 1.0);

    if(iMouse.w<0.){
        return v0;
    }
    else{
        vec3 v;
    	vec2 angle = 200.0*(mouse.xy-mouse.zw);
        angle.y = min(abs(angle.y), 40.0)*sign(angle.y);

        vec3 axVert = vec3(0.0, 0.0, 1.0);
        mat3 rVert = rotationMatrix(angle.x, axVert);
        v = rVert*v0;

        vec3 axFlat = normalize(cross(-v, axVert));
		mat3 rFlat = rotationMatrix(angle.y, axFlat);
        return rFlat*v;
    }
}

float f(vec2 polar, float n){
 	//deviation from radius


    return 0.5*(1.0+sin(iTime+5.0*gNoise(polar, n)));
}

vec3 polarToSpace(vec2 polar, float n){
    //convert polar coordinates to euclidean
    polar = vec2(polar.x*pi()/n, 2.0*pi()*polar.y/n); 
 	return vec3(cos(polar.x), 
               	sin(polar.x)*-sin(polar.y),
               	sin(polar.x)*-cos(polar.y));

}

vec3 reflectionBall(vec3 pSurface, vec3 normal, vec3 pFrag, vec3 rView){
    //compute the reflection in the balls surface
    vec3 rReflected = reflect(rView, normal);
    vec3 pFragReflected = pSurface - rReflected * length(pSurface-pFrag);
    return computeBackground(pFragReflected, rReflected);

}

vec3 warpedSurface(vec3 p, vec3 center, vec3 rView, float radius){
    //compute the perturbed normals of the 
    //warped surface (bump map, no true normals or positions)

    vec3 cLemon = vec3(1.0, 0.88, 0.0);
    vec3 pNormalized = normalize(p-center);
   	float n = 13.0;

   	vec2 pz = normalize(pNormalized.zy);

   	vec2 polar = vec2(n*acos(pNormalized.x)/pi(),
                      0.5*n*(acos(pz.x)*sign(pz.y)/pi()+1.0));

    float h = 0.05;	   

    float factor = 0.01;
    float fVal = factor*f(polar, n);

  	vec3 dx = ((1.0-factor*f(vec2(polar.x+h, polar.y), n))*
               polarToSpace(vec2(polar.x+h, polar.y), n)-
               (1.0-fVal)*
               pNormalized)/h;
    vec3 dy = ((1.0-factor*f(vec2(polar.x, polar.y+h), n))*
               polarToSpace(vec2(polar.x, polar.y+h), n)-
               (1.0-fVal)*
               pNormalized)/h;

    vec3 normal = -normalize(cross(dx, dy));

    //add lightning and reflection
    float bd = diffuse(p, normal); 
    float spec = specular(normal, p, -rView);

    vec3 reflection = reflectionBall(p+pNormalized*fVal,
                                     normal,
                                     v,
                                     rView);
   	return mix(mix((ba+bd)*cLemon, reflection, 0.3),
              vec3(1.0), spec);



   }


vec4 computeObject(vec3 p, vec3 rView){
    //compute intersection of viewing ray with sphere
    //calculate color
    vec3 center = vec3(0.0, 0.0, 6.0);
    float radius =  5.0;

    vec2 sol = intersectBall(center-p, rView);

    if(pow(radius, 2.0)+sol.y>0.0){
           float temp = sol.x+sqrt(sol.y+pow(radius, 2.0));
           vec3 p1 = p+(-sol.y-pow(radius, 2.0)+pow(sol.x, 2.0))*rView/temp;

           vec3 color = warpedSurface(p1, center, rView, radius);
           return vec4(color , smoothstep(0.0, 0.3, pow(radius, 2.0)+sol.y));
           }
           else{
           return vec4(0.0);
            }        

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0*(fragCoord-0.5*iResolution.xy)/min(iResolution.x, iResolution.y);
    vec4 mouse = iMouse/min(iResolution.x, iResolution.y);

    // set up camera

    v = getCameraPosition(mouse);
    vec3 vDir = normalize(-v);
    vec3 vLeft = normalize(cross(vDir, vec3(0.0, 0.0, 1.0)));
    vec3 vUp = cross(vLeft, vDir);
    float dScreen = 2.5;

    //calculate fragment position in space
    vec3 pFrag = v + dScreen * vDir + uv.x*vLeft + uv.y * vUp;

    vec3 rView = normalize(pFrag-v);

    vec4 colorBall = computeObject(pFrag, rView);

    vec3 color = computeBackground(pFrag, rView);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(mix(color, colorBall.xyz, colorBall.w),1.0);
}

// Truchet tiling using the semi-regular tiling made of squares, hexagons, and dodecagons.
// This is based on the idea that any regular polygon with an even number of sides can be used
// as a Truchet tile.
//
// Inspired by this JavaScript canvas version here: http://somethingorotherwhatever.com/truchet-polygons/
//
// The side connection tables were pre-generated using my own rather brute-force program.
//
// 4.8^2 version is here: https://www.shadertoy.com/view/MlyBRG
//
// Weaved version is here: https://www.shadertoy.com/view/WsyXWR
//

float pi = 3.141592653589;

float hash(vec2 p)
{
    return texelFetch(iChannel0, ivec2(p) & 255, 0).r;
}

/*
// Returns the origin and radius of a circle intersecting a and b, with tangents
// at a and b pointing towards vec2(0). This is for drawing the circular arcs.
void solveCircle(vec2 a, vec2 b, out vec2 o, out float r)
{
    float th = acos(dot(normalize(a), normalize(b))) / 2.;
    float adj = length(a);
    r = tan(th) *adj;
    o = normalize((a + b) / 2.) * sqrt(r * r + adj * adj);
}
*/

// (via comments on https://www.shadertoy.com/view/wdBSRm):
// adx's considerably more concise version of the method above.
// On a side note, if you haven't seen it before, his "Quake / Introduction" 
// shader is well worth the look: https://www.shadertoy.com/view/lsKfWd
void solveCircle(vec2 a, vec2 b, out vec2 o, out float r){

    vec2 m = a + b;
    o = dot(a, a)/dot(m, a)*m;
    r = length(o - a);

}

// Pixel width for anti-aliasing.
float w;

// Draws a line or arc connecting a and b inside a polygon.
float l(vec2 p, vec2 a, vec2 b)
{
    if(dot(normalize(a), normalize(b)) < -.99999)
    {
        float d = distance(p, mix(a, b, clamp(dot(p - a, b - a) / dot(b - a,b - a), 0., 1.)));
        return smoothstep(0.03, 0.03 - w * 2., d - .02);
    }

    vec2 o;
    float r;
    solveCircle(a, b, o, r);
    return smoothstep(0.03, 0.03 - w * 2., abs(distance(p, o) - r) - .02);
}

