}

// you can literally raymarch anything :)
float Turb(vec2 p)
{
	p*=0.25;
	p.x *= 0.7;
	float ti = iTime*0.125;
	p.y += ti;
	vec2 i = p;

	float c = -0.1;
	float inten = 0.15;
	float r = length(p+vec2(sin(ti),sin(ti*0.433))*2.);

	for (float n = 0.0; n < 4.0; n+=1.0)
	{
		float t = r-ti * (1.0 - (1.9 / (n+1.)));
		      t = r-ti/(n+0.6);
		i -= p.yx + vec2(
			cos(t*0.1 - i.x-r) + sin(t*0.1 + i.y), 
			sin(t*0.7 - i.y) + cos(t*0.4 + i.x)+r
		);
		c += 1.0/length(vec2(
			(sin(i.x+t)/inten),
			(cos(i.y+t)/inten)
			)
		);
	}
	c = smoothstep(0.0,1.0,c*0.75);
	return c * 0.3;
}


float map(vec3 p)
{
	p.x + sin(p.z+iTime)*0.5;
	p.x *= sign(p.y);	// mirror top->bottom
	float k = Turb(p.xz);
	return 3.5 -abs(p.y) - k;
}

vec3 normal(vec3 p) {
	vec2 e = vec2(.001, 0.);
	vec3 n;
	n.x = map(p + e.xyy) - map(p - e.xyy);
	n.y = map(p + e.yxy) - map(p - e.yxy);
	n.z = map(p + e.yyx) - map(p - e.yyx);
	return normalize(n);
}

vec3 render(vec2 uv)
{
	vec3 ro = vec3(sin(iTime)*0.25, cos(iTime)*0.25, iTime*0.75);
	vec3 rd = normalize(vec3(uv, .5));
	vec3 p = vec3(0.);
	float t = 0.;
	for (int i = 0; i < 80; i++)
    {
		p = ro + rd * t;
		float d = map(p);
		if (d < .001 || t > 100.) break;
		t += .5 * d;
	}
	vec3 l = ro;
	vec3 n = normal(p);
	vec3 lp = normalize(l - p);
	float diff = .5 * max(dot(lp, n), 0.);
	return vec3(diff*0.54,diff*2.8,diff*1.85) / (1. + t * t * .01);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 col = render(uv);
	fragColor = vec4(col, 1.);
}

// "Growing Bumpy Polyhedron" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);

vec3 ltDir;
float dstFar, tCur, tFrac, nFace, nFaceN;
const float pi = 3.14159, phi = 1.618034;

vec4 SphFib (vec3 v, float n)
{   // Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vec4 (sqrt (ddMin), vfMin);
}

float ObjDf (vec3 p)
{
  vec4 f4;
  float d, dn, s;
  f4 = SphFib (normalize (p), nFace);
  s = sqrt (nFace) / 10.;
  d = RotToDir (f4.yzw, vec3 (0., 0., 1.), p - f4.yzw).z - 1. +
     0.1 * smoothstep (0.07, 0.17, f4.x * s) / s;
  if (tFrac > 0.) {
    f4 = SphFib (normalize (p), nFaceN);
    dn = RotToDir (f4.yzw, vec3 (0., 0., 1.), p - f4.yzw).z - 1. +
       0.1 * smoothstep (0.07, 0.17, f4.x * s) / s;
    d = mix (d, dn, smoothstep (0., 1., tFrac));
  }
  return 0.8 * d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0002 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = vec3 (0., 0.9, 0.) * (0.2 + 0.2 * max (dot (vn, - ltDir), 0.) +
       0.7 * pow (max (dot (vn, ltDir), 0.), 2.)) +
       0.3 * vec3 (1., 0., 0.) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 16.);
  } else col = vec3 (0., 0., 0.3 * (0.7 - 0.2 * rd.y));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv, ori, ca, sa;
  float el, az, tStep, nfMax;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tStep = 0.1;
  nfMax = 4000.;
  nFace = 6. + mod (floor (tCur / tStep), nfMax - 5.);
  nFaceN = min (nFace + 1., nfMax);
  tFrac = (fract (tCur / tStep) - 0.7) / 0.3;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.05 * pi * tCur;
    el += 0.4 * pi * sin (0.01 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -12.);
  rd = vuMat * normalize (vec3 (uv, 5.));
  dstFar = 20.;
  ltDir = vuMat * normalize (vec3 (-0.3, 0.5, -1.));
  col = ShowScene (ro, rd);
  uv += vec2 (-1.4, 0.9);
  uvv = abs (uv + vec2 (0.2, -0.05)) - vec2 (0.23, 0.08);
  if (max (uvv.x, uvv.y) < 0.) col = mix (col, vec3 (0.5, 1., 0.5),
     ShowInt (uv, vec2 (0.4, 0.1), 4., nFace));
  fragColor = vec4 (col, 1.);
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.12, 0.15, abs (q.x))) *
     (1. - smoothstep (0.55, 0.65, abs (q.y)));
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d = max (d, DigSeg (q));

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv <= 1) k = (iv == 0) ? 119 : 36;
  else if (iv <= 3) k = (iv == 2) ? 93 : 109;
  else if (iv <= 5) k = (iv == 4) ? 46 : 107;
  else if (iv <= 7) k = (iv == 6) ? 122 : 37;
  else              k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo)  DSG (q.xy - vp)  DSG (q.xy - vm)  DSG (q.yx)
  DSG (q.xy + vm)  DSG (q.xy + vp)  DSG (q.yx + vo)
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.)) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (sgn < 0.) {
      if (idChar == mxChar - nDig - 1.) s = ShowDig (q, -1);
      else ++ v;
    }
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

// "Fibonacci Polyhedra" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Faberge meets Fibonacci (no. 5 in the "egg" series)
  The Fibonacci scheme for near-uniform covering a sphere with disks is used to
  construct polyhedra.
*/

float PrBoxDf (vec3 p, vec3 b);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrEllips2Df (vec3 p, vec2 r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, qHit, vdFib;
vec2 opCs, rotCs, rShell;
float tCur, dstFar, opPhs, nFace;
int idObj;
const int idShell = 1, idHing = 2, idArm = 3, idPolyh = 4;
const float pi = 3.14159, phi = 1.618034;

vec3 SphFib (vec3 v, float n)
{   // based on Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vfMin;
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, r, hWid, shUp;
  dMin = dstFar;
  hWid = 0.3;
  q = p;
  q.z += rShell.x + 0.9 * hWid;
  d = PrCapsDf (q.yzx, 0.25, 0.95);
  DMINQ (idHing);
  q.yz = Rot2Cs (q.yz, opCs);
  q.y = abs (q.y);
  q.yz = Rot2Cs (q.yz, opCs);
  q.z -= hWid;
  d = max (PrBoxDf (q, vec3 (0.95, 0.25, hWid)), - q.y);
  DMINQ (idArm);
  q.z -= rShell.x + 0.9 * hWid;
  r = rShell.x + 0.1 * (1. - q.y / length (q)) * cos (32. * atan (q.x, q.z));
  d = max (max (PrEllips2Df (q.xzy, vec2 (r, rShell.y) + 0.1),
     - PrEllips2Df (q.xzy, vec2 (r, rShell.y) - 0.1)), - q.y);
  DMINQ (idShell);
  shUp = 1.2 + 1.6 * opPhs;
  q = p;
  q.y -= - rShell.y + shUp;
  d = PrCylDf (q.xzy, 0.1, shUp);
  q = p;
  q.y -= - rShell.y + 0.1;
  d = min (d, PrCylDf (q.xzy, 0.5, 0.05));
  DMINQ (idArm);
  q = p;
  q.y -= - rShell.y + 2. * shUp;
  q.xz = Rot2Cs (q.xz, rotCs);
  vdFib = SphFib (normalize (q), nFace);
  d = RotToDir (vdFib, vec3 (0., 0., 1.), q - vdFib).z - 1.2;
  DMINQ (idPolyh);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, s;
  bool isRefl;
  rShell = vec2 (4., 3.5);
  isRefl = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar && idObj == idPolyh) {
    ro += dstObj * rd;
    vdFib.xz = Rot2Cs (vdFib.xz, rotCs * vec2 (1., -1.));
    rd = reflect (rd, vdFib);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
    isRefl = true;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = vec4 (0.8, 0.85, 0.8, 0.2);
    if (idObj == idShell) {
      s = length (qHit / rShell.xyx);
      col4 = (s > 1. || qHit.y < 0.05) ? col4 : mix (vec4 (0.4, 0.9, 0.4, 0.1),
         vec4 (0.9, 0.7, 0.7, 0.1), smoothstep (0.3, 0.5, rShell.y - qHit.y) *
         smoothstep (0.97, 0.98, s));
      if (s > 1.) vn = VaryNf (64. * qHit, vn, 0.5);
    } else if (idObj == idHing) col4 *= 1. - 0.5 * SmoothBump (0.05, 0.1, 0.02,
       mod (4. * qHit.x, 1.));
    col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    if (! isRefl || idObj == idShell && s > 1.)
       col += 256. * pow (clamp (0.5 + 0.5 * dot (ltDir, reflect (rd, vn)), 0., 1.), 16.) *
       pow (1. - 0.6 * abs (dot (normalize (ltDir - rd), VaryNf (1024. * qHit, vn, 2.))), 8.);
    if (idObj == idShell && s < 1.) col *= 0.4 + 0.6 * smoothstep (0.1, 0.2, opPhs);
    if (isRefl) col *= 0.9;
  } else {
    col = (mix (vec3 (1., 0.9, 0.8) * (0.4 + 0.2 * rd.y), vec3 (0.1, 0.1, 0.5),
       max (SmoothBump (0.45, 0.55, 0.05, mod (64. * atan (rd.z, - rd.x) / pi, 1.)),
       SmoothBump (0.45, 0.55, 0.05, mod (64. * asin (rd.y) / pi, 1.)))) +
       vec3 (0.5, 0.5, 1.) * (((rd.y > max (abs (rd.x), abs (rd.z * 0.25))) ?
       min (2. * rd.y, 1.) : 0.05 * (1. + dot (rd, ltDir))) + 0.5 * step (0., rd.y) *
       pow (clamp (1.05 - 0.5 * length (max (abs (rd.xz / rd.y) - vec2 (1., 4.), 0.)), 0., 1.),
       8.))) * (isRefl ? 0.5 : 0.4);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  opPhs = SmoothBump (0.25, 0.85, 0.15, mod (0.1 * tCur, 1.));
  nFace = 32. * pow (2., mod (floor (0.1 * tCur), 6.));
  opCs = sin (0.2 * pi * opPhs + vec2 (0.5 * pi, 0.));
  rotCs = sin (0.01 * pi * tCur + vec2 (0.5 * pi, 0.));
  az = pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.5 * pi * (2. * mod (floor (0.04 * tCur), 2.) - 1.) *
       SmoothBump (0.25, 0.65, 0.1, mod (0.04 * tCur, 1.));
    el -= 0.05 * pi * sin (0.05 * pi * tCur);
  }
  el = clamp (el, -0.45 * pi, 0.45 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 2.7, -30.);
  zmFac = 4.2 + 2. * opPhs;
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 60.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., h * clamp (p.z / h, -1., 1.))) - r;
}

float PrEllips2Df (vec3 p, vec2 r)
{
  return (length (p / r.xxy) - 1.) * min (r.x, r.y);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}
float cellular(vec2 pos, int i, int j){
    pos = pos-.5;
    float pp =  3. * sin(4.*atan(pos.y, pos.x)*iTime);
    return length(pos * pp);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float bubble( vec2 uv) {
   // return length(.5 - uv);
    // Scale
    vec2 st = 5.4 * uv;

    // Tile the space
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 1.;  // minimun distance

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random2(i_st + neighbor);

			// Animate the point
            point = 0.5 + 0.5*sin(iTime + 6.2831*point) * vec2(.5, 1);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f_st;
            // Distance to the point
            float dist = length(diff);
			//dist = pow(dist, .7 + .5 * random2(i_st + neighbor + 114.215).x);
            // Keep the closer distance
            m_dist = opSmoothUnion(m_dist, dist, 0.5);
        }
    }  
    //return 1./(1.13+m_dist);
    return m_dist;
}

float lighting(vec3 light, vec3 normal){

    return .5 * max(dot( normal, light), .24);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= 1.64;

    //calculate normal
    float m_dist = bubble(uv);
    vec3 pos = vec3(uv, m_dist); 

    vec3 dx = vec3(.05,0.,0.);
    dx = pos - vec3(uv+ dx.xy, bubble(uv + dx.xy));

    vec3 dy = vec3(0.,.05,0.);
    dy = pos - vec3(uv+ dy.xy, bubble(uv + dy.xy));    

    vec3 normal =  normalize(cross(1./dx, 1./dy));

    // calculate color 
    float col = smoothstep(1.-uv.y*2.,1.-uv.y*1., m_dist * sin(iTime * .2));
    vec3 background = vec3(.60 , .14, .20)*uv.y;
    vec3 foreground = vec3(.0, .82306, .320);

    // lighting
    float light = lighting(normalize(vec3(1.2,sin(iTime)*-.72,3.14)), normal);
    light += lighting(normalize(vec3(4.2,-3.35,6.7)), normal);
  	light = max(light, col);
    vec3 color = light *  mix(foreground,background, col);

    // Output to screen
    fragColor =  vec4(.5 + .5 * normal, 1.0);
    //fragColor = vec4(m_dist);
    fragColor = vec4(color, 1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Projective geometry: the eleven point conic.
//
// Take 4 points (red) and the 6 lines (yellow) through pairs of points,
// forming a complete quadrangle.
//
// There are 3 diagonal points where the line through two points meets
// the line through the other two points (cyan).
//
// Given a line l (red), the 6 quadrangle lines meet l in 6 points and each
// point has a harmonic conjugate with the two points of the quadrangle
// on the same line (green).
//
// Additionally, the 4 points of the quadrangle and the line l
// define a pair of conics (blue and grey), passing through the 4 points
// and tangent to the line (these conics may be imaginary though).
//
// The three diagonal points, the six harmonic points and the two points
// of tangency of the pair of conics all lie on a third conic, the
// eleven point conic (white).
//
// Copyright (c) Matthew Arcus, 2018
// MIT License: https://opensource.org/licenses/MIT
//
// Controls:
// Drag mouse to change line
// '1' and '2': change initial configuration
// 'f': change line & point drawing style
// 'z': zoom in
//
////////////////////////////////////////////////////////////////////////////////

int configuration = 2;

const float lwidth = 0.015;
const float pwidth = 0.045;
float ledge0 = 0.0, ledge1 = lwidth;
float pedge0 = lwidth, pedge1 = pwidth;
const float scale = 1.5;
float zoom = 1.0;

const float PI =  3.141592654;

const vec3 pcolor0 = vec3(1,0,0);
const vec3 pcolor1 = vec3(0,1,0);
const vec3 pcolor2 = vec3(1,1,0);
const vec3 pcolor3 = vec3(0,1,1);
const vec3 pcolor4 = pcolor1;
const vec3 lcolor0 = pcolor0;
const vec3 lcolor1 = pcolor1;
const vec3 lcolor2 = vec3(1,1,0.2);
const vec3 ccolor0 = vec3(1,1,1);
const vec3 ccolor1 = vec3(0,0,1);
const vec3 ccolor2 = vec3(0.5,0.5,1);

// Solve Ax^2 + 2Bx + C = 0
bool quadratic(float A, float B, float C,
               out float x1, out float x2) {
   float D = B*B - A*C;
   if (D < 0.0) return false;
   D = sqrt(D);
   if (B < 0.0) D = -D;
   x1 = (-B-D)/A;
   x2 = C/(A*x1);
   return true;
}

// Return either intersection of lines p and q
// or line through points p and q, r = kp + jq
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  p3 = m*p3; 
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3[0];
  m[1] /= p3[1];
  m[2] /= p3[2];
  return transpose(m);
}

// Represent a projective conic as a 3x3 matrix:
//
// M = (a,d,e,
//      d,b,f,
//      e,f,c)
//
// is: axx + byy + czz + 2(dxy + exz + fyz) = 0
// calculated as pMp for p = (x,y,z).
//
// We can treat this as a distance field, scaled by the
// (x,y) derivative in order to get correct line widths.

// With this representation, the dual conic is just the inverse;
// if the determinant is zero then there is no dual and the
// conic is degenerate.

// Conic through 4 points and a line - there are 2 solutions, so
// need to solve quadratic. I'm sure I didn't invent this but
// can't remember the source.
// l.p = l'p == l'(MinvM)p = (Minv'l)'Mp
bool conic4points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 l4, out mat3 M0, out mat3 M1) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // and then to the square (1,1),(1,-1),(-1,1),(-1,-1)
  mat3 q = rproject(vec3(1,-1,1), vec3(-1,1,1),vec3(-1,-1,1),vec3(1,1,1));
  p = inverse(q)*p; // q is constant
  // Now construct a conic through the images of p0-p3 and l4
  l4 = transpose(inverse(p))*l4;
  float i = l4.x, j = l4.y, k = l4.z;
  float A = -j*j, B = 0.5*(i*i+j*j-k*k), C = -i*i;
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  M0 = mat3(t0, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t0);
  M0 = transpose(p)*M0*p;
  if (isnan(determinant(M0))) return false;
  M1 = mat3(t1, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t1);
  M1 = transpose(p)*M1*p;
  if (isnan(determinant(M1))) return false;
  return true;
}

// Find the two points of intersection of a line through p0 and
// p1 with the conic defined by M.
bool lineconic(vec3 p0, vec3 p1, mat3 M, out vec3 q0, out vec3 q1) {
  // (tp0 + p1)M(tp0 + p1) == 0
  float A = dot(p0,M*p0);
  float B = dot(p0,M*p1);
  float C = dot(p1,M*p1);
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  q0 = t0*p0+p1;
  q1 = t1*p0+p1;
  return true;
}

// Find the two points of intersection of the line l
// with the conic defined by c.
bool lineconic(vec3 l, mat3 M, out vec3 i0, out vec3 i1) {
  // Intersect l with two of the axes. This might of course
  // be the same point (1,0,0) here, so should use the third
  // axis.
  vec3 p0 = join(l,vec3(0,0,1));
  vec3 p1 = join(l,vec3(0,1,0));
  return lineconic(p0,p1,M,i0,i1);
}

// Construct the conic defined by 5 points.
// Method taken from "Geometry", Brannan, Esplan & Gray, CUP, 2012
mat3 conic5points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // Now construct a conic through the images of p0-p4,
  p4 = p*p4;
  float a = p4.x, b = p4.y, c = p4.z;
  float d = c*(a-b);
  float e = b*(c-a);
  float f = a*(b-c);
  mat3 m = mat3(0,d,e,
                d,0,f,
                e,f,0);
  // And combine the two.
  return transpose(p)*m*p;
}

// Harmonic conjugate of p,q and intersection of p,q with l.
vec3 harmonic(vec3 p, vec3 q, vec3 l) {
  // kp + jq is conjugate of kp - lq:
  // l.(kp - jq) = 0
  // k(l.p) = j(l.q)
  float k = dot(l,q);
  float j = dot(l,p);
  return k*p+j*q;
}

////////////////////////////////////////////////////////////////////////////////
// Drawing functions
////////////////////////////////////////////////////////////////////////////////

float point(vec3 pos, vec3 p) {
  if (p.z == 0.0) return 0.0;
  pos /= pos.z; p /= p.z; // Normalize
  float dist = distance(pos,p);
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(pedge0,pedge1,dist);
}

float line(vec3 pos, vec3 l) {
  float dist = dot(pos,l)/(pos.z*length(l.xy));
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(ledge0,ledge1,abs(dist));
}

float conic(vec3 pos, mat3 m) {
  float dist = dot(pos,m*pos);   // Distance from the conic
  vec3 grad = m*pos*2.0;         // The gradient uses the same matrix.
  dist /= pos.z*length(grad.xy); // Normalize for Euclidean distance
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(ledge0,ledge1,abs(dist));
}

// Screen coords to P2 coords
vec3 map(vec2 p) {
  return vec3(scale*zoom*(2.0*p - iResolution.xy) / iResolution.y, 1);
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(vec3 p) {
  float t = 0.5*iTime;
  p.x *= 1.0+0.5*sin(t);
  p.y *= 1.0+0.3*sin(0.618*t);
  p.xy = rotate(p.xy,0.2*t);
  //p.yz = rotate(p.yz,0.1*t);
  return p;
}

vec3 cmix(vec3 color0, vec3 color1, float level) {
  return mix(color0,color1,level);
}

const int CHAR_0 = 48;
const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_I = 73;
const int CHAR_Z = 90;
bool keypress(int code) {
#if defined LOCAL || __VERSION__ < 300
  return false;
#else
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  if (keypress(CHAR_Z)) zoom = 0.25;
  if (!keypress(CHAR_F)) {
      float pixelwidth = 2.0*scale/iResolution.y;
      pedge0 = pwidth-pixelwidth;
      pedge1 = pwidth+pixelwidth;
      ledge0 = lwidth-pixelwidth;
      ledge1 = lwidth+pixelwidth;
  }    
  configuration = int(keypress(CHAR_0+1)) + 2*int(!keypress(CHAR_0+2));
  vec3 pos = map(fragCoord.xy);
  vec3 p0,p1,p2,p3,l4; // p4 is the movable line
  if (configuration == 0) {
    p0 = vec3(1,0,0); p1 = vec3(0,1,0);
    p2 = vec3(0,0,1); p3 = vec3(1,1,1);
    l4 = vec3(0.5,-1,1);
  } else if (configuration == 1) {
    p0 = vec3(0,0,1); p1 = vec3(1,0,1);
    p2 = vec3(0,1,1); p3 = vec3(1,1,1);
    l4 = vec3(0.5,-1,1);
  } else if (configuration == 2) {
    p0 = vec3(0,0,1); p1 = vec3(0,1,1);
    p2 = vec3(0.866,-0.5,1); p3 = vec3(-0.866,-0.5,1);
    l4 = vec3(-1,0.5,1);
  } else {
    p0 = vec3(1,0,1);  p1 = vec3(0,1,1);
    p2 = vec3(-1,0,1); p3 = vec3(0,-1,1);
    l4 = vec3(0.5,-1,1);
  }
  p0 = transform(p0); p1 = transform(p1);
  p2 = transform(p2); p3 = transform(p3);
  if (iMouse.x != 0.0) {
    l4 = map(iMouse.xy);
  }
  vec3 l01 = join(p0,p1);
  vec3 l02 = join(p0,p2);
  vec3 l03 = join(p0,p3);
  vec3 l12 = join(p1,p2);
  vec3 l13 = join(p1,p3);
  vec3 l23 = join(p2,p3);

  // diagonal points
  vec3 d0 = join(l01,l23);
  vec3 d1 = join(l02,l13);
  vec3 d2 = join(l03,l12);

  // harmonic points
  vec3 h01 = harmonic(p0,p1,l4);
  vec3 h02 = harmonic(p0,p2,l4);
  vec3 h03 = harmonic(p0,p3,l4);
  vec3 h12 = harmonic(p1,p2,l4);
  vec3 h13 = harmonic(p1,p3,l4);
  vec3 h23 = harmonic(p2,p3,l4);

  // conics
  mat3 elevenpoint = conic5points(d0,d1,d2,h01,h23);
  mat3 M1,M2;
  bool doM1M2 = conic4points(p0,p1,p2,p3,l4,M1,M2);
  vec3 i0,i1;
  bool doN = lineconic(l4,elevenpoint,i0,i1);

  vec3 color = vec3(0);

  // The diagonal lines of the quadrangle
  color = cmix(color,lcolor2,line(pos,l01));
  color = cmix(color,lcolor2,line(pos,l02));
  color = cmix(color,lcolor2,line(pos,l03));
  color = cmix(color,lcolor2,line(pos,l12));
  color = cmix(color,lcolor2,line(pos,l13));
  color = cmix(color,lcolor2,line(pos,l23));

  // The moving line
  color = cmix(color,lcolor0,line(pos,l4));

  // The conics
  color = cmix(color,ccolor0,conic(pos,elevenpoint));
  if (doM1M2) {
    color = cmix(color,ccolor1,conic(pos,M1));
    color = cmix(color,ccolor2,conic(pos,M2));
  }

  // The points of the quadrangle
  color = cmix(color,pcolor0,point(pos,p0));
  color = cmix(color,pcolor0,point(pos,p1));
  color = cmix(color,pcolor0,point(pos,p2));
  color = cmix(color,pcolor0,point(pos,p3));

  // The midpoints of the sides of the quadrangle
  color = cmix(color,pcolor1,point(pos,h01));
  color = cmix(color,pcolor1,point(pos,h02));
  color = cmix(color,pcolor1,point(pos,h03));
  color = cmix(color,pcolor1,point(pos,h12));
  color = cmix(color,pcolor1,point(pos,h13));
  color = cmix(color,pcolor1,point(pos,h23));

  // The magic triangle
  color = cmix(color,pcolor3,point(pos,d0));
  color = cmix(color,pcolor3,point(pos,d1));
  color = cmix(color,pcolor3,point(pos,d2));

  color = cmix(color,pcolor4,point(pos,join(l01,l4)));
  color = cmix(color,pcolor4,point(pos,join(l02,l4)));
  color = cmix(color,pcolor4,point(pos,join(l03,l4)));
  color = cmix(color,pcolor4,point(pos,join(l12,l4)));
  color = cmix(color,pcolor4,point(pos,join(l13,l4)));
  color = cmix(color,pcolor4,point(pos,join(l23,l4)));

  if (doN) {
    color = cmix(color, pcolor2, point(pos,i0));
    color = cmix(color, pcolor2, point(pos,i1));
  }
  fragColor = vec4(pow(1.0*color,vec3(0.4545)),1);
}

