const float MARBLE_RADIUS  = 0.1075;
const float MARBLE_SPACING = 0.5;



float map(in vec3 m, out vec3 normal) {

    float angle = iTime*0.15;
    float c = cos(angle);
    float s = sin(angle);        

    mat3 sphereMat = mat3(vec3(c, 0.0, s),
                          vec3(0.0, 1.0, 0.0),
                          vec3(-s, 0.0, c));

    vec3 center = vec3(0, 0, -2.5 + cos(iTime*0.5)*1.5 );
    float radius = 4.0;

    vec3 v = m - center;

    v = sphereMat * v;

    float distToCenter = length(v) - radius;

    vec3 marbleCenter = floor((v + MARBLE_SPACING*0.5) / MARBLE_SPACING) * MARBLE_SPACING;

    if(length(marbleCenter) > radius - MARBLE_RADIUS*0.5) {
        return max(distToCenter, MARBLE_SPACING * 0.5);
    }
    else {
        float d = length(v - marbleCenter);
        normal = normalize(v - marbleCenter);

        if(d<0.0) return MARBLE_SPACING * 0.75;
        return d - MARBLE_RADIUS;
    }
}



vec2 getUV(in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv -= vec2(0.5, 0.5);    
    uv.x *= ratio;
    return uv;
}


float raymarch(in vec3 dir, out vec3 m, out vec3 normal) {

    float mattercrossed = 0.0;

    for(int i=0; i<100; i++) {
		float d = map(m, normal);        

        if(d > -0.05 && d < 0.05) {
            mattercrossed += 1.0;
        }        

        d = max(d, 0.01);

        m += dir * d * 0.5;
    }

    return mattercrossed;
}

vec4 lighting(float matter, vec3 m, in vec3 normal) {

    vec4 color = vec4(cos(m.x), cos(m.y), cos(m.z), 1.0);


    return color * matter * 0.02;
}

bool isdot(vec3 m) {

    vec3 closest = mod(m, vec3(0.1, 0.1, 0.1));

    float d = length(closest);

    return d < 0.05;
}



