#define _FoV 45.0
#define MAX_STEPS 128
#define STEP_SIZE 75.0
#define MAX_OCTAVES 12
#define MAX_LOOP 256

#define JITTER

const vec3 _SunPos= vec3(-100.0, 50.0, 1000.0);
const vec3 _SunColor = vec3(0.12, 0.10, 0.08);
const float _Density = 0.082;
const float _Abs = 0.001;
const float _Mie = 0.2;
const float _G = 0.9;

//Improved Heyney-Greenstein phase function
float cornetteShanksPhase(float mu, float g)
{
	float g2 = g * g;
	return (3.0 * (1.0 - g2) * (1.0 + mu * mu)) / (2.0 * (2.0 + g2) * pow(1.0 + g2 - 2.0 * g * mu, 1.5));
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                      		vec4(0.75,0.25,0.875,.375), 
                  		    vec4(0.1875,0.6875,0.0625,0.5625), 
                  	    	vec4(0.9375,0.4375,0.8125,0.3125));

//iq's gradient noise
vec2 hash( vec2 x )
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

//Fractal (pink) noise function S(f) ∝ 1/f^α, {0 <= α <=2}
float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    for (int i = 1; i < MAX_OCTAVES; i++)
    {
        if (i > octaves)
            break;

        val += noise(seed * freq * float(i)) / (pow(float(i), lac));
    }

    return val;
}

float sampleVolume(vec3 pos)
{
    float n = (fbm(pos, 4, 0.007, 1.5));
    n = abs(n);
    float h = (pos.y * pos.y)/256.0;
    n = max(0.0, n - abs(noise(pos * 0.025) * 1.0/h * 2.0));
    n = smoothstep(0.2, 1.0, n);
    return n * _Density * h;
}

//Radiative transfer approximation
vec4 bsdf(vec3 pos, vec3 dir, vec3 col, float phase, float ds, int s)
{    
    //int steps = int(min(float(s), float(MAX_STEPS)));
    vec4 result = vec4(col, 1.0);

    for (int i = 0; i < MAX_STEPS; i++)
    {
        if (i >= s)
            break;

        float depth = sampleVolume(pos);

        //Beer-Lambert Law: T = e^-τ, τ = μℓ
        float ext = (_Abs + _Mie) * depth;
        ext = max(ext, 0.0000001);
        float trans = exp(-ext * ds);
        trans *= (1.0 - depth * trans);

        float falloff = -pos.y/1500.0;

        vec3 lum = _SunColor * falloff * phase * _Mie * depth;
        vec3 intScatt = (lum - (lum*trans))/ext;

        result.rgb += intScatt * result.a;
        result.a *= trans;

        if (result.a <= 0.02)
            return result;

        pos += dir * ds;
    }

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Calculate eye vector
    vec3 rayOrigin = vec3(0.0, 0.0 + sin(iTime) * 10.0, iTime * 1500.0);
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    //Sky color
    vec4 col = mix(vec4(0.2, 0.1, 0.1, 1.0), vec4(0.5, 0.5, 1.0, 1.0), uv.y -0.2);

    //Assume light rays are parallel for distant source
    vec3 sunDir = normalize(_SunPos);

    //Light
    float sun = dot(sunDir, rayDir);
    col += smoothstep(0.999,1.0,sun) * vec4(1.0,0.7,0.4,1.0) * 1.25;
    col += smoothstep(0.975,1.02,sun) * vec4(0.4,0.25,0.2, 1.0);

    if (uv.y < 0.5)
    {       
        //Mie phase approx
        float mu = dot(sunDir, rayDir);
        float phase = cornetteShanksPhase(mu, _G);

        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        rayOrigin += rayDir * offset * STEP_SIZE;
        #endif

        //Clouds
    	col = bsdf(rayOrigin, rayDir, col.rgb, phase, STEP_SIZE, 96);
    }
    else
    {
        //Stars
        float t = abs(noise(vec2(50.0*uv.x + iTime, uv.y*50.0)));
        col += t * min(1.0,smoothstep(0.4, 1.0, noise(rayDir * 250.0)) * 8.0);
    }

    fragColor = col;
}

#define S(a,b,t) smoothstep(a,b,t)
#define nTime (iTime+9000.)/12.

float DDot(vec2 p1,vec2 p2, float blr) {
    return 1.-(distance(p1,p2)*blr);
}


float N21(vec2 p)
{	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col=vec3(0.,0.,0.);
    vec3 colmask;
    vec2 pos;

    //rotate screen (from Martijn Steinrucken aka BigWings)
    float s = sin(nTime);
    float c = cos(nTime);
    uv*=mat2(c, -s, s, c);             

    for(float i=1.0;i<=350.;i++)
    {
    	float t=N21(vec2(i,i));
        float tt=N21(vec2(i+i,i+9.));
    	pos = vec2(sin(nTime+i*t)+tt, cos(nTime+i)+tt);
        float blur=300.-(180.*(sin(nTime*tt)+0.5));

    	// blob colour
        float cc=N21(vec2(i+2.,i+3.));
    	colmask=vec3(1.+sin(iTime*t)/2.,1.+sin(iTime*cc)/2.,1.+cos(iTime)/2.);
        col=max(colmask*vec3(DDot(uv,pos,blur)),col);
        col=max(colmask*vec3(DDot(uv,pos*vec2(-1.,1.),blur)),col);
        col=max(colmask*vec3(DDot(uv,pos*vec2(-1.,-1.),blur)),col);
        col=max(colmask*vec3(DDot(uv,pos*vec2(1.,-1.),blur)),col);
    }

    fragColor = vec4(col,1.0);

}

