float PrSphDf (vec3 p, float r);
float PrCapsDf (vec3 p, float r, float h);
vec3 HueToRgb (float c);
float SmoothBump (float lo, float hi, float w, float x);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Hashff (float p);

vec3 ltDir;
float dstFar, tCur, sclVar, qHue;
vec2 sRot;
int qLev;
const int nLev = 3;
const float pi = 3.14159, phi = 1.618034;

vec4 SphFib (vec3 v, float n)
{   // Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, aMin, z, ni;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
      aMin = a;
    }
  }
  return vec4 (aMin, vfMin);
}

float ObjDf (vec3 p)
{
  vec4 f4;
  vec3 pn;
  float dMin, d, sTot, s, t;
  dMin = PrSphDf (p, 2.1);
  if (dMin < 0.05) {
    p.xz = Rot2Cs (p.xz, sRot);
    t = length (p);
    pn = p / t;
    f4 = SphFib (pn, 512.);
    dMin = t - 1.1 + 0.1 * smoothstep (0.05, 0.15, length (pn - f4.yzw));
    qLev = 0;
    qHue = 0.1;
    sTot = 1.;
    for (int j = 0; j < nLev; j ++) {
      if (j > 0) {
        p.z -= 3.;
        f4 = SphFib (normalize (p), 128.);
      }
      s = sclVar + 2. * Hashff (31. * f4.x);
      p = s * vec3 (1.3, 1.3, 1.) * RotToDir (f4.yzw, vec3 (0., 0., 1.), p - f4.yzw);
      sTot /= s;
      d = sTot * PrCapsDf (p, 0.3, 3.);
      if (d < dMin) {
        dMin = d;
        qLev = j + 1;
        t = 0.5 * Hashff (41. * f4.x);
        if (j == 0) qHue = 0.1 + t;
        else qHue += (1. - (float (j) / float (nLev))) * t;
      }
    }
    dMin *= 0.3;
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, s;
  sRot = sin (0.1 * pi * (floor (0.2 * tCur) + smoothstep (0., 0.2, mod (0.2 * tCur, 1.))) +
     vec2 (0.5 * pi, 0.));
  sclVar = 38. - 29. * SmoothBump (0.18, 0.75, 0.15, mod (0.05 * tCur, 1.));
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    s = float (qLev) / float (nLev);
    col = HueToRgb (mod (qHue, 1.)) * (0.2 + 0.8 * s) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
      0.5 * s * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
  } else col = mix (vec3 (1., 1., 0.5), vec3 (0., 0., 0.3 * (0.7 + 0.3 * rd.y)),
     smoothstep (0.0035, 0.004, length (SphFib (rd, 8192.).yzw - rd)));
  return col;
}

#define AA  0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  zmFac = 5.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
    zmFac += 15. * abs (mPtr.x);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -8.);
  dstFar = 15.;
  ltDir = vuMat * normalize (vec3 (0.5, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 4.;
#endif  
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.71 / canvas.y, 0.),
       0.5 * pi * (a + 0.5)), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.7)), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

vec3 HueToRgb (float c)
{
  return clamp (abs (fract (c + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define maxiter 250
#define m1 1.0
#define m2 0.9
#define r1 0.5
#define r2 0.5
#define v1 0.5
#define v2 0.95

void rotate (inout vec2 vertex, float rads)
{
  mat2 tmat = mat2(cos(rads), -sin(rads),
                   sin(rads), cos(rads));

  vertex.xy = vertex.xy * tmat;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    rotate(uv,0.35 * iTime);
    vec2 z = vec2(0.0, 0.0);
	float p = 0.0;
	float dist = 0.0;
	float x1 = tan(iTime*v1)*r1;
	float y1 = sin(iTime*v1)*r1;
	float x2 = tan(iTime*v2)*r2;
	float y2 = sin(iTime*v2)*r2;
	for (int i=0; i<maxiter; ++i)
	{
		z *= 2.0;
		z = mat2(z,-z.y,z.x) * z + uv;
		p = m1/sqrt((z.x-x1)*(z.x-x1)+(z.y-y1)*(z.y-y1))+m2/sqrt((z.x-x2)*(z.x-x2)+(z.y-y2)*(z.y-y2));
		dist = max(dist,p);

	}
	dist *= 0.0099;
	fragColor = vec4(dist/0.3, dist*dist/0.03, dist/0.112, 1.0);
}

