// Intersection of a ray and a rounded cone oriented in an arbitrary direction
// with a single square root and division (both delayed) for the body

// List of ray-surface intersectors at https://www.shadertoy.com/playlist/l3dXRf
//
// and http://iquilezles.org/www/articles/intersectors/intersectors.htm


vec4 iRoundedCone( in vec3  ro, in vec3  rd, 
                   in vec3  pa, in vec3  pb, 
                   in float ra, in float rb )
{
    vec3  ba = pb - pa;
	vec3  oa = ro - pa;
	vec3  ob = ro - pb;
    float rr = ra - rb;
    float m0 = dot(ba,ba);
    float m1 = dot(ba,oa);
    float m2 = dot(ba,rd);
    float m3 = dot(rd,oa);
    float m5 = dot(oa,oa);
	float m6 = dot(ob,rd);
    float m7 = dot(ob,ob);

    float d2 = m0-rr*rr;

	float k2 = d2    - m2*m2;
    float k1 = d2*m3 - m1*m2 + m2*rr*ra;
    float k0 = d2*m5 - m1*m1 + m1*rr*ra*2.0 - m0*ra*ra;

	float h = k1*k1 - k0*k2;
	if(h < 0.0) return vec4(-1.0);
    float t = (-sqrt(h)-k1)/k2;
  //if( t<0.0 ) return vec4(-1.0);

    float y = m1 - ra*rr + t*m2;
    if( y>0.0 && y<d2 ) 
    {
        return vec4(t, normalize( d2*(oa + t*rd)-ba*y) );
    }

    // Caps. I feel this can be done with a single square root instead of two
    float h1 = m3*m3 - m5 + ra*ra;
    float h2 = m6*m6 - m7 + rb*rb;
    if( max(h1,h2)<0.0 ) return vec4(-1.0);

    vec4 r = vec4(1e20);
    if( h1>0.0 )
    {        
    	t = -m3 - sqrt( h1 );
        r = vec4( t, (oa+t*rd)/ra );
    }
	if( h2>0.0 )
    {
    	t = -m6 - sqrt( h2 );
        if( t<r.x )
        r = vec4( t, (ob+t*rd)/rb );
    }

    return r;
}

vec3 pattern( in vec2 uv )
{
    vec3 col = vec3(0.6);
    col += 0.4*smoothstep(-0.01,0.01,cos(uv.x*0.5)*cos(uv.y*0.5)); 
    col *= smoothstep(-1.0,-0.98,cos(uv.x))*smoothstep(-1.0,-0.98,cos(uv.y));
    return col;
}

#define AA 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 1.0*cos(an), 0.4, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // aniamte cone
        #if 1
        vec3  pa = vec3(-0.1,-0.1,0.1);
        vec3  pb = vec3( 0.4, 0.3,0.3);
        float ra = 0.3;
        float rb = 0.1;
        #else
        vec3  pa = 0.4*cos(iTime*vec3(1.0,1.3,0.8)+vec3(0.0,2.0,4.0));
        vec3  pb = 0.4*cos(iTime*vec3(0.9,1.1,0.7)+vec3(1.0,3.0,6.0));
        float ra = 0.2 + 0.1*sin(iTime*1.7+0.0);
        float rb = 0.2 + 0.1*sin(iTime*1.8+2.0);
        #endif

        // raytrace
        vec4 tnor = iRoundedCone( ro, rd, pa, pb, ra, rb );

        float t = tnor.x;

        // shading/lighting	
        vec3 col = vec3(0.08)*(1.0-0.3*length(p)) + 0.02*rd.y;
        if( t>0.0 )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = tnor.yzw;

            vec3 w = normalize(pb-pa);
            vec3 u = normalize(cross(w,vec3(0,0,1)));
            vec3 v = normalize(cross(u,w) );
            vec3 q = (pos-pa)*mat3(u,v,w);
            col = pattern( vec2(16.0,64.0)*vec2(atan(q.y,q.x),q.z) );

            vec3 lig = normalize(vec3(0.7,0.6,0.3));
            vec3 hal = normalize(-rd+lig);
		    float dif = clamp( dot(nor,lig), 0.0, 1.0 );
		    float amb = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0)), 0.0, 1.0 );

		    col *= vec3(0.2,0.3,0.4)*amb + vec3(1.0,0.9,0.7)*dif;
            col += 0.4*pow(clamp(dot(hal,nor),0.0,1.0),12.0)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // dither to remove banding in the background
    tot += fract(sin(fragCoord.x*vec3(13,1,11)+fragCoord.y*vec3(1,7,5))*158.391832)/255.0;

	fragColor = vec4( tot, 1.0 );
}

#define AA 1
#define AA_SAMPLES 4
#define AA_WIDTH 0.004

#define BG_COLOR vec3(0.2, 0.6, 0.6)

// Circle parameters
#define CIRCLE_INNER_RADIUS_SQUARED 0.58
#define CIRCLE_OUTER_RADIUS_SQUARED 0.6
#define CIRCLE_COLOR vec3(1.0)
#define CIRCLE_CENTER vec2(0)
#define SEGMENTS 0

// Effects parameters
#define ROTATE_CLOCKWISE 1
#define ROTATION_SPEED 1.0

#define SCALE_CIRCLE 1
#define SCALE_SPEED 1.5
#define SCALE_LIMIT 1.8

#define CLOCKWISE_HIDING 1
#if CLOCKWISE_HIDING
	#define CIRCLE_DIR 0.0
#else
	#define CIRCLE_DIR 1.0
#endif

// Mathematical constants
#define PI_2 6.283185

// Shader constants
const float SEGMENT_ANGLE = PI_2 / float(1 << SEGMENTS);
const float SAMPLE_INCREMENT = AA_WIDTH / max(float(AA_SAMPLES - 1), 1.0);
const float INIT_OFFSET = -AA_WIDTH / 2.0;

// Returns angle of point relative to circle
float theta(vec2 point)
{
    float angle = atan(point.y, point.x);
    angle += PI_2 * float(angle < 0.0);
    return angle;
}

// Returns whether or not a pixel should get drawn
bool hide_pixel(vec2 dir)
{
    // use square distance to avoid taking the square root
    float square_dist = dot(dir, dir);
    // use 'or' operation to increase chances of avoiding the last check
    return square_dist > CIRCLE_OUTER_RADIUS_SQUARED || 
           square_dist < CIRCLE_INNER_RADIUS_SQUARED ||
           mod(floor(theta(dir) / SEGMENT_ANGLE), 2.0) != CIRCLE_DIR;
}

// Applies 2D rotation matrix to point
vec2 rotate_2d(vec2 point, float angle)
{
    float s_theta = sin(angle);
    float c_theta = cos(angle);
    return vec2(point.x * c_theta - point.y * s_theta, 
                point.x * s_theta + point.y * c_theta);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // clamp coordinates from -1 to 1, and account for aspect-ratio
    vec2 coord = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

#if SCALE_CIRCLE
    // make the circle shrink and grow
    coord *= sin(iTime * SCALE_SPEED) + SCALE_LIMIT;
#endif

#if ROTATE_CLOCKWISE
    coord = rotate_2d(coord - CIRCLE_CENTER, iTime * ROTATION_SPEED);
#else
    coord = rotate_2d(coord - CIRCLE_CENTER, -iTime * ROTATION_SPEED);
#endif

    float color_val
#if AA
 	= 0.0;
	float offset_y = INIT_OFFSET;
    for (int i = 0; i < AA_SAMPLES; ++i)
    {
        float offset_x = INIT_OFFSET;
        for (int j = 0; j < AA_SAMPLES; ++j)
        {
            color_val += float(hide_pixel(coord + vec2(offset_x, offset_y)));
            offset_x += SAMPLE_INCREMENT;
        }

        offset_y += SAMPLE_INCREMENT;
    }

    color_val /= float(AA_SAMPLES * AA_SAMPLES);
#else
    = float(hide_pixel(coord));
#endif

    fragColor = vec4(mix(CIRCLE_COLOR, BG_COLOR, color_val), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord;
    coord.y -= iResolution.y / 2.0;
    coord /= iMouse.x > 0.0 ? iMouse.x : iResolution.x / 2.0;

    fragColor = vec4(0.0);

    float angle = (sin(iTime / 2.0) * 0.375 + 0.42);

    if(abs(coord.y) < coord.x * tan(angle)) {
        float s = sin(angle);
        float k = log((2.0 / (1.0 - s)) - 1.0);
        float d = exp(ceil(log(coord.x / (1.0 + s)) / k) * k);

        float a = coord.x - d;
        float b = coord.y;
        float r = d * s;

        if(a*a + b*b > r*r) {
        	fragColor = vec4(1.0);
        }
    }
}

////////////////////////////////////////////////////////////////////////////////
//
// Spherical indexing.
//
// Inspired by http://donw.io/post/sphere-indexing/ but using a different method
//
// Map sphere normal to octahedron face by i) taking absolute values of x,y,z
// to map into primary quadrant, ii) intersect with surface x+y+z = 1.
// On the octahedron face, the triangle (1,0,0),(0,1,0),(0.0,1), Euclidean
// coordinates are the same as barycentric coordinates, which we can
// reinterpret as positions in the right angled triangle
// (0,0),(1,0),(0,1) just by dropping the z coordinate. Now the rows and
// columns are horizontal and vertical and we can find the indices
// just with some floors and mods.
//
////////////////////////////////////////////////////////////////////////////////

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

// Return square of the perp distance from p to line from A to B.
float linedistance2(vec3 p, vec3 A, vec3 B) {
  vec3 d = B-A;
  vec3 q = A+dot(p-A,d)/dot(d,d)*d;
  return dot(p-q,p-q);
}

// Normalize a vec2 to a vec3 bary coord with sum 1.
vec3 mkbary(vec2 p) {
  return vec3(p,1.0-dot(p,vec2(1)));
}

float N = 8.0;
float linewidth2 = 0.005*0.005;
vec3 getColor(vec3 p) {
  p = abs(p);            // Map to primary quadrant
  p /= dot(p,vec3(1));   // To x+y+z = 1 plane
  vec2 frac = N * p.xy;  // To (0,0),(N,0),(0,N) triangle
  vec2 xy = clamp(floor(frac),0.0,N-1.0);
  frac -= xy;
  // xy now has the integer row/column coordinates
  // frac has the coordinates within a row/column square
  // so work our which triangle in the square it is in.
  float parity = float(dot(frac,vec2(1)) > 1.0);
  // Compute the index. Adding quadrant left as exercise.
  // Bottom bit is upper/lower triangle
  float index = 2.0*xy.y + parity;
  // Clamp to avoid rounding problems at end.
  // row n goes from 0 up to 2(N-n-1)
  index = clamp(index,0.0,2.0*(N-xy.x-1.0));
  index += 2.0 * N*xy.x;

  // Now find distance to lines in triangle.
  // A,B,C are vertices of region triangle, in bary coordinates
  // If p has bary coords (a,b,c), then (a,b',c') is line
  // parallel to side opposite first vertex.
  vec3 A = mkbary((xy+vec2(1,0))/N);
  vec3 B = mkbary((xy+vec2(0,1))/N);
  vec3 C = mkbary((parity==0.0?xy:xy+vec2(1))/N);
  if (linedistance2(p,A,B) < linewidth2 ||
      linedistance2(p,B,C) < linewidth2 ||
      linedistance2(p,C,A) < linewidth2 ||
      false) {
    return vec3(1);
  }
  return hsv2rgb(vec3(index/(2.0*N),0.8,0.8));
}

float PI = 3.14159;

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  float r;      // radius
  vec3 p;       // centre
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t,t1;
  if (B >= 0.0) {
    t = -B-D; t1 = C/t;
  } else {
    t1 = -B+D; t = C/t1;
  }
  if (t < 0.0) t = t1;
  if (t < 0.0) return false;
  // Normal is the radial vector of sphere
  hit = Hit(t, (q+t*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit) {
  Sphere s = Sphere(1.0, vec3(0));
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;
float ambient;
float diffuse;

vec4 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec4(0,0,0,1);
  } else {
    vec3 n = hit.n;
    if (dot(r.d,n) > 0.0) n *= -1.0;
    vec3 baseColor = getColor(hit.n);
    vec3 color = baseColor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),5.0);
    color += 0.5*specular*vec3(1.0,1.0,1.0);
    color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return vec4(sqrt(color),1.0);
  }
}

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  p.yz = rotate(p.yz,iTime * 0.125);
  p.zx = rotate(p.zx,iTime * 0.2);
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  light = normalize(vec3(0.5,1.0,-1.0));
  ambient = 0.5;
  diffuse = 1.0-ambient;
  vec2 uv = 2.0*fragCoord.xy/iResolution.xy - 1.0;
  vec3 p = vec3(0,0,-6.0);
  // "screen" coordinate
  vec3 s = vec3(iResolution.x/iResolution.y * uv.x, uv.y, 0);
  vec3 d = s-p; // Direction from camera to screen point
  p = transform(p);
  d = transform(d);
  d = normalize(d);
  light = transform(light);
  fragColor = solve(Ray(p,d));
}

/////////////////////////////////////////////////////////////////////
//
// Apollonian Gasket: iterated inversion in a set of tangent circles
//
// Originally derived from https://www.shadertoy.com/view/4dsBDn
// by ebanflo but changed fairly substantially.
//
// Mouse drags central circle around.
// 'd' inverts in unit disk
// 'h' maps to half plane
// 'j' applies Joukovsky transform
//
/////////////////////////////////////////////////////////////////////

// N = 3 gives 4 mutually tangent circles.

const float N = 3.0;
const int max_iterations = 100;
const float pi = 3.14159265;

// Circles are represented as vec3(x,y,r2) where
// (x,y) is the centre and r2 is the squared radius.

// Invert pos in circle c
vec2 invert(vec2 pos, vec3 c) {
  vec2 p = pos-c.xy;
  float p2 = dot(p,p);
  return p*c.z/p2 + c.xy;
}

// Invert pos in circle if it is inside 
bool checkinverse(inout vec2 pos, vec3 c) {
  vec2 p = pos-c.xy;
  float p2 = dot(p,p);
  if (p2 > c.z) return false;
  pos = p*c.z/p2 + c.xy;
  return true;
}

// N circles in a ring, with tangency points on unit circle,
// plus a central circle, tangent to the others.
vec3 gasket(vec2 pos){
  float theta = pi/N;
  float r = 1.0/cos(theta);
  float s = tan(theta);
  for(int n = 0; n < max_iterations; n++){
    vec3 c = vec3(0,0,pow(r-s,2.0));
    // Try inverting in central circle
    if (!checkinverse(pos,c)) {
      bool found = false;
      // else try in the circles of the ring.
      for (float i = 0.0; i < N; i++) {
        float t = 0.2*iTime;
        vec3 c = vec3(r*sin(2.0*i*theta+t), r*cos(2.0*i*theta+t), s*s);
        if (checkinverse(pos,c)) {
          found = true;
          break;
        }
      }
      if (!found) return vec3(pos,n);
    }
  }
  return vec3(pos,max_iterations);
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 getCol(vec3 n){
  return hsv2rgb(vec3(mod(n.z/50.0,1.0),0.8,0.8));
}

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x - z.y*w.y,
              z.x*w.y + z.y*w.x);
}

vec2 cinv(vec2 z) {
  return z/dot(z,z)*vec2(1,-1);
}

vec2 csqrt(vec2 z) {
  float r = length(z);
  return vec2(sqrt(0.5*(r+z.x)),sign(z.y)*sqrt(0.5*(r-z.x)));
}

bool keypress(int code) {
  //return false;
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

const int CHAR_D = 68;
const int CHAR_H = 72;
const int CHAR_J = 74;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 z = 2.0 * fragCoord.xy / iResolution.y - vec2(iResolution.x / iResolution.y, 1.0);
  if (keypress(CHAR_H)) {
    z.y += 1.0;
    z = invert(z,vec3(0,-1,2)); // Map half plane to unit disk.
  }
  if (keypress(CHAR_D)) {
    z *= 20.0;
    z = invert(z,vec3(0,0,1)); // Invert in unit disk
  }
  if (keypress(CHAR_J)) {
    // Inverse Joukovsky transform
    // Solve w^2 - 2wz + 1 = 0
    // a = 1, b = -z, c = 1
    // (z +- sqrt(z^2 - 1))
    vec2 t = csqrt(cmul(z,z)-vec2(1,0));
    if (dot(z+t,z+t) < 1.0) z += t;
    else z -= t;
    //z = 0.5*(z+cinv(z)); // sanity check
  }
  vec2 m = vec2(0.1,-0.2);
  if (iMouse.x != 0.0) {
    m = 2.0 * iMouse.xy / iResolution.y - vec2(iResolution.x / iResolution.y, 1.0);
  }
    // If mouse outside unit circle, inversion is imaginary!
  if (true/*length(m) < 0.999*/) {
    vec2 c = m*(1.0/dot(m,m));
    z = invert(z,vec3(c,dot(c,c)-1.0));
  }

  vec3 data = gasket(z);
  vec3 baseColor = getCol(data);
  fragColor = vec4(pow(baseColor, vec3(1.2)),1);
}

const float MATH_PI = float( 3.14159265359 );
const vec3 SKY_COLOR = vec3(0.05, 0.2, 0.5);
const vec3 LIGHT = normalize( vec3(  0.1, 0.8,  0.9 ) );
const vec3 LIGHT_BACK = LIGHT * - 1.0;
const float AIRPLANE_SIZE = 20.0; // AIRPLANE描画範囲直径
const float AIRPLANE_SCALE = 1.0; // AIRPLANEスケール
const float TIME_SCALE = 1.0;
const float EP = 0.0001;
const int AIRPLANE_COUNT = 5;
const float PI = 3.141592;
const float PI2 = PI * 2.0;


const vec2 cloudrange = vec2(0.0, 10000.0);
const vec3 cloudmove = vec3(0.0 , 0.0, 600.0);

mat3 airplanes[AIRPLANE_COUNT];

float dot2(in vec2 v ) { return dot(v,v); }
float dot2(in vec3 v ) { return dot(v,v); }

// random/hash function              
float hash( float n )
{
  return fract(cos(n)*41415.92653);
}

// 3d noise function
float noise( in vec3 x )
{
  vec3 p  = floor(x);
  vec3 f  = smoothstep(0.0, 1.0, fract(x));
  float n = p.x + p.y*57.0 + 113.0*p.z;

  return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
    mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
    mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
}

mat3 m = mat3( 0.00,  1.60,  1.20, -1.60,  0.72, -0.96, -1.20, -0.96,  1.28 );

// Fractional Brownian motion
float fbm( vec3 p )
{
  float f = 0.5000*noise( p ); p = m*p*1.1;
  f += 0.2500*noise( p ); p = m*p*1.2;
  f += 0.1666*noise( p ); p = m*p;
  f += 0.0834*noise( p );
  return f;
}

vec3 foldXZ(vec3 p)
{
    p.x = abs(p.x);
    p.z = abs(p.z);
    return p;
}

mat2 rotate(float a)
{
    float s = sin(a),c = cos(a);
    return mat2(c, s, -s, c);
}

vec4 dElongate( in vec3 p, in vec3 h )
{
    vec3 q = abs(p)-h;
    return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}

float dSphere(vec3 pos, float size)
{
    return length(pos) - size;
}

float dEllipsoid(vec3 pos, vec3 size)
{
    return dSphere(pos / size, 1.0);
}

float dRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

float dTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float dBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

float dTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float dTree(vec3 po, inout vec4 hitColor)
{
    vec3 p = po;
    // p = vec3(floor(p.x),floor(p.y),floor(p.z));
    p.x = mod(p.x, 20.) - 10.0;
    p.z = mod(p.z, 20.) - 10.0;

    vec3 size = vec3(0.4, 4.0, 0.4);
    float branch = dBox(p, size);
    for (int i = 0; i < 2; i++)
    {
        vec3 q = foldXZ(p);
        q.y -= size.y;
        q.xy *= rotate(-0.4);
        q.zy *= rotate(-0.4);
        branch = min(branch, dBox(q, size));
        p = q;
    }

    p.y -= size.y;
    float leaf = dEllipsoid(p, vec3(3,2,3));

    if (branch < leaf)
    {
        hitColor = vec4(0.44, 0.29, 0.22, 1.0);
    }
    else
    {
        float hoge = step(fract(po.x) + fract(po.y) + fract(po.z), 0.9) * 0.5;
        hitColor = vec4(0.44 - hoge, 0.99 - hoge, 0.22 - hoge, 1.0);
    }

    return min(branch, leaf);
}

vec3 rotateX(vec3 position, float rotate)
{
    return vec3(position.x, position.z * -sin(rotate) + position.y * cos(rotate), position.z * cos(rotate) + position.y * sin(rotate));
}

float dAircraft(vec3 ray, inout vec4 hitColor)
{
    vec3 body1 = vec3(0.5, 0.6, 2.0) * AIRPLANE_SCALE;
    vec3 body1Pos = vec3(0, 0.2, -1.) * AIRPLANE_SCALE;
    float body1Rot = 3.14 * -0.03;
    vec3 body2 = vec3(1, 1, 5) * AIRPLANE_SCALE;
    vec3 body2Pos = vec3(0, 1, 0) * AIRPLANE_SCALE;
    float body2Rot = 3.14 * 0.0;
    vec3 body3 = vec3(0.5, 0.9, 7) * AIRPLANE_SCALE;
    vec3 body3Pos = vec3(1, -1, 1.1) * AIRPLANE_SCALE;
    float body3Rot = 3.14 * 0.5;
    vec2 body4 = vec2(0.3, 0.1) * AIRPLANE_SCALE;
    vec3 body4Pos = vec3(-1.5, 1.5, 1) * AIRPLANE_SCALE;
    float body4Distortion = 3.14 * 0.0;
    vec2 body5 = vec2(8.0, -6.0) * AIRPLANE_SCALE;
    vec3 body5Pos = vec3(0, -1, 0) * AIRPLANE_SCALE;
    vec2 body6 = vec2(4.0, -3.0) * AIRPLANE_SCALE;
    vec3 body6Pos = vec3(0, -1.1, -5) * AIRPLANE_SCALE;
    vec3 body7 = vec3(2.4, 0.5, 2.0) * AIRPLANE_SCALE;
    vec3 body7Pos = vec3(0, 1.5, 5) * AIRPLANE_SCALE;
    vec3 body8_1 = vec3(1.2, 0, -4.0) * AIRPLANE_SCALE;
    vec3 body8_2 = vec3(2.4, 2.5, -7.5) * AIRPLANE_SCALE;
    vec3 body8_3 = vec3(1.4, -1., -7.5) * AIRPLANE_SCALE;
    vec3 body8Pos = vec3(0, -1, 0) * AIRPLANE_SCALE;

    float body1Depth = dEllipsoid(rotateX(ray, body1Rot) + body1Pos, body1);
    float body2Depth = dEllipsoid(rotateX(ray, body2Rot) + body2Pos, body2);
    float body3Depth = dRoundCone(rotateX(vec3(ray.x, ray.y, ray.z), body3Rot) + body3Pos, body3.x, body3.y, body3.z);
    // absしたい
    float body3_1Depth = dRoundCone(rotateX(vec3(-ray.x, ray.y, ray.z), body3Rot) + body3Pos, body3.x, body3.y, body3.z);
    vec4 body4_ = dElongate(vec3(abs(ray.x), ray.y, ray.z) + body4Pos + vec3(0, 0, ray.y * -0.5), vec3(0.6, 0.1, 2.0));
    float body4Depth = body4_.w + dTorus(rotateX(body4_.xzy + vec3(0, 0, 0), body4Distortion), body4);
    float body5Depth = dTriangle(ray, vec3(body5.x, 0.0, body5.y) + body5Pos, vec3(-body5.x, 0.0, body5.y) + body5Pos, body5Pos) - 0.3;
    float body6Depth = dTriangle(ray, vec3(body6.x, 0.0, body6.y) + body6Pos, vec3(-body6.x, 0.0, body6.y) + body6Pos, body6Pos) - 0.2;
    float body7Depth = dBox(ray + body7Pos + vec3(0, 0, ray.y * -0.5), body7);
    float body8Depth = dTriangle(vec3(abs(ray.x), ray.y, ray.z), body8_1 + body8Pos, body8_2 + body8Pos, body8_3 + body8Pos) - 0.1;

    hitColor = vec4(1.0, 1.0, 1.0, 1.0);
    float depth = min(min(min(min(min(min(min(min(body1Depth, body2Depth), body3Depth), body3_1Depth), body4Depth), body5Depth), body6Depth), body7Depth), body8Depth);

    if (EP > abs(depth - body1Depth))
    {
        hitColor = vec4(0.2, 0.2, 0.2, 1.0);
    }

    return depth;
}

vec3 dAircraftNormal(vec3 pos)
{
    vec4 _tmp;
    return normalize(vec3(
            dAircraft(pos, _tmp) - dAircraft(vec3(pos.x - EP, pos.y, pos.z), _tmp),
            dAircraft(pos, _tmp) - dAircraft(vec3(pos.x, pos.y - EP, pos.z), _tmp),
            dAircraft(pos, _tmp) - dAircraft(vec3(pos.x, pos.y, pos.z - EP), _tmp)
        ));
}

mat4 getWorldMat(mat3 transform)
{
    // trans world
    vec3 axisX = cross(transform[1], transform[2]) * -1.0;

    return mat4(
        axisX.x, axisX.y, axisX.z, 0,
        transform[1].x, transform[1].y, transform[1].z, 0,
        transform[2].x, transform[2].y, transform[2].z, 0,
        0, 0, 0, 1 
        );
}

void airplaneRender(inout vec4 color, inout float depth, float time, vec3 cameraPos, vec3 rayDirection)
{
    for (int i = 0; i < AIRPLANE_COUNT; i++)
    {
        mat4 worldMat = getWorldMat(airplanes[i]);

        vec3 localCameraPosition = (worldMat * vec4(cameraPos - airplanes[i][0], 1.0)).xyz;
        vec3 localRayDirection = (worldMat * vec4(rayDirection, 1.0)).xyz;

        float airplaneDepth = length(localCameraPosition) - AIRPLANE_SIZE * 0.5;
        vec4 hitColor = color;


        float marchDepth = 0.0;
        for (int i = 0; i < 1000; i++)
        {
            if (marchDepth > airplaneDepth + AIRPLANE_SIZE || depth < marchDepth)
            {
                break;
            }

            vec3 ray = localCameraPosition + localRayDirection * marchDepth;
            float diff = dAircraft(ray, hitColor);

            if (diff < EP)
            {
                depth = marchDepth + diff;
                vec3 normal = dAircraftNormal(ray);
                color = vec4(vec3(clamp(dot(normal, LIGHT) * hitColor, 0.0, 1.0)) + 0.2, 1.0);
            }
            else
            {
                // 細かい所に精度をよせる
                marchDepth += diff;
            }

            marchDepth += AIRPLANE_SIZE * 0.001;
        }
    }
}

void skyRender(inout vec4 color, inout float depth, float time, vec3 cameraPos, vec3 rayDirection)
{
    float sundot = clamp(dot(rayDirection, LIGHT),0.0,1.0);

    // render sky
    float t = pow(1.0-0.7*rayDirection.y, 15.0);
    vec3 col = 0.8 * SKY_COLOR;
    // sun
    col += 0.47*vec3(1.6,1.4,1.0)*pow( sundot, 350.0 );
    // sun haze
    col += 0.4*vec3(0.8,0.9,1.0)*pow( sundot, 2.0 );

    // CLOUDS
    vec4 sum = vec4(0,0,0,0);
    float c = 0.;
    for(int i=0;i<1000;i++) // or while
    { 
        c +=100.; // cloud depth
        vec3 cpos = cameraPos + c*rayDirection + cloudmove * iTime; // cloud position
        if(cloudrange.x < cpos.y && cpos.y < cloudrange.y){
            float alpha = smoothstep(0.5, 1.0, fbm( cpos*0.00025 ))*.9; // fractal cloud density
            vec3 localcolor = mix(vec3( 1.1, 1.05, 1.0 ), 0.7*vec3( 0.4,0.4,0.3 ), alpha); // density color white->gray
            alpha = (1.0-sum.w)*alpha; // alpha/density saturation (the more a cloud layer's density, the more the higher layers will be hidden)
            sum += vec4(localcolor*alpha, alpha); // sum up weightened color
        }

        if (0. < rayDirection.y && cloudrange.y < cpos.y)
        break;
        if (rayDirection.y < 0. && cpos.y < cloudrange.x)
        break;
        if (0.98 < sum.w)
        break;
        if (depth < c)
        break;
    }



    float alpha = smoothstep(0.7, 1.0, sum.w);
    sum.rgb /= sum.w+0.0001;

    sum.rgb -= 0.6*vec3(0.8, 0.75, 0.7)*pow(sundot,13.0)*alpha;
    sum.rgb += 0.2*vec3(1.3, 1.2, 1.0)* pow(sundot,5.0)*(1.0-alpha);

    col = mix( col, sum.rgb , sum.w );

    color = color + vec4(col, 1.0);
}

void objectRender(inout vec4 color, inout float depth, float time, vec3 cameraPos, vec3 rayDirection)
{
    airplaneRender(color, depth, time, cameraPos, rayDirection);
}

vec3 getAirplanePosition(float time, int index)
{
    vec3 line = vec3(0, 5000., 500);
    vec3 line2 = vec3(cos(time * 0.2) * 1000.0 - 250., 20, sin(time * 0.2 - 10.0) * 1000.0);
    vec3 member = vec3(float(index) * 20., 10.0, float(index) * 100.);
    return line + line2 + member;
}

void objectUpdate(float time)
{
    mat4 leaderWorld = inverse(getWorldMat(airplanes[0]));

    for(int index = 0; index < AIRPLANE_COUNT; index++)
    {
        vec3 position = getAirplanePosition(time, index);
        vec3 prevPosition = getAirplanePosition(time - EP, index);
        vec3 nextPosition = getAirplanePosition(time + EP, index);
        vec3 dir = normalize(nextPosition - position);
        vec3 top = vec3(0, 1, 0);
        airplanes[index] = mat3(position, top, dir);
    }
}

void cameraUpdate(vec2 fragCoord, float time, out vec3 cameraPosition, out vec3 rayDirection)
{
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    cameraPosition = vec3(sin(1.0*time -10.0) * 100., 5000., 0.0);

    vec3 cameraDirection = normalize(airplanes[0][0] - cameraPosition);
    vec3 cameraTop = normalize(cameraDirection + vec3(0, 1, 0));
    vec3 right = normalize(cross(cameraDirection, cameraTop));
    cameraTop = cross(cameraDirection, right) * -1.0;

    rayDirection = normalize(uv.x * right + uv.y * cameraTop + cameraDirection);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime * TIME_SCALE;
    vec3 cameraPos;
