//a sphere intersection function from iq
//https://www.shadertoy.com/view/4djSDy
vec2 sphIntersect( in vec3 ro, in vec3 rd, float size)
{
	float b = dot( ro, rd );
	float c = dot( ro, ro )-size*size;
	float h = b*b - c;
	if( h<0.0 ) return vec2(99999);
	return vec2(-b-sqrt(h),-b+sqrt(h));
}

//same function but i changed vec3 with vec2
vec2 sphIntersect( in vec2 ro, in vec2 rd, float size)
{
	float b = dot( ro, rd );
	float c = dot( ro, ro )-size*size;
	float h = b*b - c;
	if( h<0.0 ) return vec2(99999);
	return vec2(-b-sqrt(h),-b+sqrt(h));
}

vec3 texture2(vec3 p, vec3 n) {
    vec3 c = cos(p*3.0-n);
    vec3 s = sin(p*3.0+n*3.0);

    vec3 col = s*c.yzx*0.5+0.5;
    col *= dot(n,normalize(vec3(-1)))*0.5+0.5;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragUV = fragCoord / iResolution.xy;
    seed = iTime;

    vec3 acc = vec3(0);
    for (int aa = 0; aa < antialiasing; aa++) {
#if antialiasing==1
        vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
#else
        vec2 uv = ((fragCoord+vec2(rand(),rand()))*2.0-iResolution.xy)/iResolution.y;
#endif

        vec3 rd = normalize(vec3(uv,1));
        vec3 ro = vec3(0.5,0.5,iTime);

        if (length(iMouse.xy) > 40.0) {
            rd.yz *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
            rd.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
        }

        vec3 ird = 1.0/vec3(length(rd.yz),length(rd.xz),length(rd.xy));

        float dist = 0.0;
        vec3 n = vec3(0,0,-1);
        for (int i = 0; i < steps; i++) {
            vec3 p = fract(ro+rd*dist)-0.5;

            vec2 a = vec2(-1,1);
            float l = 0.0;

            //for cylinder normals
            vec3 mask = vec3(1);

            vec2 d = sphIntersect(p,rd,0.58);
            if (d.x < 0.0) {
                l = d.y;
            }

            d = sphIntersect(p.yz,rd.yz*ird.x,0.4)*ird.x;
            if (d.x < 0.0 && d.y > l) {
                mask = vec3(0,1,1);
                l = d.y;
            }

            d = sphIntersect(p.xz,rd.xz*ird.y,0.4)*ird.y;
            if (d.x < 0.0 && d.y > l) {
                mask = vec3(1,0,1);
                l = d.y;
            }

            d = sphIntersect(p.xy,rd.xy*ird.z,0.4)*ird.z;
            if (d.x < 0.0 && d.y > l) {
                mask = vec3(1,1,0);
                l = d.y;
            }

            if (l > 0.0) {
                dist += l;
                p += rd*l;
                n = -p*mask;
            } else {
                break;
            }
        }

        vec3 background = vec3(0.2);
        vec3 col = background;

        if (dist < maxdist) {
            vec3 p = ro+rd*dist;

            n = normalize(n);

            col = texture2(p, n);

            col = mix(col,background,sqrt(dist/maxdist));
        }
        acc += col;
    }



    fragColor = vec4(sqrt(acc/float(antialiasing)),1);
}

const float scale = .35,
    width = .07,
    loops = 4.,
    tau = acos(-1.) * 2.,
    totalAngle = loops * tau;

vec3 hue(float theta) {
	theta = fract(theta / tau + 10.) * 6.;
    /*if (theta > 0.) {*/
        if (theta < 1.) return vec3(1., theta, 0.); // r -> y
        if (theta < 2.) return vec3(2. - theta, 1., 0.); // y -> g
        if (theta < 3.) return vec3(0., 1., theta - 2.); // g -> c
        if (theta < 4.) return vec3(0., 4. - theta, 1.); // c -> b
        if (theta < 5.) return vec3(theta - 4., 0., 1.); // b -> m
        /*if (theta < 6.)*/ return vec3(1., 0., 6. - theta); // m -> r
    //}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy * .5)
        / min(iResolution.y, iResolution.x) / scale;
    float r = length(uv),
        theta = atan(uv.y, uv.x);

    fragColor = vec4(0.,0.,0.,1.);

    for (float i = 0.; i < totalAngle; i += tau) {
        float l = 1., phi = theta + i;

        l += sin(phi * .5 + iTime * .72) * .03;
        l += sin(phi * .25 + iTime * .82) * .027;
        l += sin(phi * .75 + iTime * .91) * .023;

        fragColor.rgb += hue(phi / loops)
            * smoothstep(0., 2. / loops,
                        1. - abs(r - l) * 15.);
    }

}

