//#define MAGICNUM 360./32.
#ifndef MAGICNUM
#define MAGICNUM 4000./(iTime + 45.)
#endif

float circle(in vec2 _st, in vec2 _pos, in float _radius){
  vec2 _c = _st - _pos;
	return step(dot(_c,_c), _radius);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  	vec2 st = 2. * fragCoord.xy / iResolution.xy - 1.;
  	st.x *= iResolution.x/iResolution.y;

    float color = 0.;

    for (float i = 0.; i < 360.; i += MAGICNUM) {
   		color += circle(st, 0.5 * vec2(sin(i), cos(i)), 0.25);
        if (texelFetch( iChannel0, ivec2(77,2),0 ).x == 0.) {
          color += circle(st, 0.5 * vec2(sin(-i), cos(-i)), 0.25);
        }
    }
    if (texelFetch( iChannel0, ivec2(32,2),0 ).x == 0.) {
    	color /= MAGICNUM / 4.;
    	fragColor = vec4(hsv2rgb(vec3(color, 1., .8)),1.0);
    } else {
     	color = mod(color, 2.);
        fragColor = vec4(vec3(color), 1.);
    }
}

#define PI 3.141592654
#define PREC 0.001 // derivative precision, to small (< 0.00001) deteriors quality & performance
#define SIZE vec2(40,30) // function size/precision
#define WATER vec4(0.21, 0.4, 0.81, 1) // water color, no use yet ...
#define CAMERA vec3(0.5, 0.5, 1) // camera position, direction is always [0, 0, -1]

struct Light
{
  vec3 pos;
  vec4 color;
};

//	Classic Perlin 2D Noise
//	by Stefan Gustavson
//
vec2 fade(vec2 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}

vec4 permute(vec4 x) {return mod(((x*34.0)+1.0)*x, 289.0);}

float cnoise(vec2 P) {
  vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
  vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
  Pi = mod(Pi, 289.0); // To avoid truncation effects in permutation
  vec4 ix = Pi.xzxz;
  vec4 iy = Pi.yyww;
  vec4 fx = Pf.xzxz;
  vec4 fy = Pf.yyww;
  vec4 i = permute(permute(ix) + iy);
  vec4 gx = 2.0 * fract(i * 0.0243902439) - 1.0; // 1/41 = 0.024...
  vec4 gy = abs(gx) - 0.5;
  vec4 tx = floor(gx + 0.5);
  gx = gx - tx;
  vec2 g00 = vec2(gx.x,gy.x);
  vec2 g10 = vec2(gx.y,gy.y);
  vec2 g01 = vec2(gx.z,gy.z);
  vec2 g11 = vec2(gx.w,gy.w);
  vec4 norm = 1.79284291400159 - 0.85373472095314 * vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
  g00 *= norm.x;
  g01 *= norm.y;
  g10 *= norm.z;
  g11 *= norm.w;
  float n00 = dot(g00, vec2(fx.x, fy.x));
  float n10 = dot(g10, vec2(fx.y, fy.y));
  float n01 = dot(g01, vec2(fx.z, fy.z));
  float n11 = dot(g11, vec2(fx.w, fy.w));
  vec2 fade_xy = fade(Pf.xy);
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
  float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
  return 2.3 * n_xy;
}
// End of Stefan Gustavson's code

float func(vec2 coords, float offset, float mult) {
  //return (cnoise(coords + vec2(offset)) + 1) / 3 * mult;
  return ((cnoise(coords + vec2(offset)) + 1.0) / 2.0 + (cnoise(coords * vec2(8.0) + vec2(offset) * vec2(8.0))) / 80.0) * mult +
  (cos(0.1 * PI * coords.x) * sin(0.3 * PI * coords.y)) * sin(offset) / 5.0;
}

float xDerivative(vec2 coords, float offset, float mult) {
  return (func(coords + vec2(PREC, 0), offset, mult) - func(coords - vec2(PREC, 0), offset, mult))/((coords.x + PREC) - (coords.x - PREC));
}

float yDerivative(vec2 coords, float offset, float mult) {
  return (func(coords + vec2(0, PREC), offset, mult) - func(coords - vec2(0, PREC), offset, mult))/((coords.y + PREC) - (coords.y - PREC));
}

float mult = 0.3;
Light LIGHT = Light(vec3(1, 0.5, 1), vec4(1, 1, 1, 1)); // light position & color

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {  
  vec2 texCoords = fragCoord / iResolution.xy;

  mult = (sin(iTime) * 0.7 + 1.0) / 2.0;
  LIGHT.pos.x = (cos(iTime * 0.5 * 1.21) + 1.0) / 2.0;

  float waveHeight = func(texCoords * SIZE, iTime, mult);

  // calculating derivatives
  float xDerivative = xDerivative(texCoords * SIZE, iTime, mult);
  float yDerivative = yDerivative(texCoords * SIZE, iTime, mult);

  // surface normal, light incidence angle and camera angle
  vec3 surfaceNormal = normalize(vec3(
    -xDerivative,
    -yDerivative,
    3
  ));
  vec3 lightVect = normalize(vec3(
    LIGHT.pos.x - texCoords.x,
    LIGHT.pos.y - texCoords.y,
    LIGHT.pos.z - waveHeight
  ));
  vec3 cameraVect = normalize(vec3(
    CAMERA.x - texCoords.x,
    CAMERA.y - texCoords.y,
    CAMERA.z - waveHeight
  ));

  // depth calculation
  vec3 cameraToWater = vec3(
    texCoords.x - CAMERA.x,
    texCoords.y - CAMERA.y,
    waveHeight - CAMERA.z
  );

  float depth = pow(length(cameraToWater / (waveHeight - CAMERA.z) * waveHeight), 1.0);

  // refraction offset
  vec3 refraction = normalize(refract(cameraVect, surfaceNormal, 1.3));

  vec2 off;
  off.x = refraction.x * waveHeight / SIZE.x;
  off.y = refraction.y * waveHeight / SIZE.y;

  // computing reflection/refraction ratio
  float reflectionAmount = pow(dot(surfaceNormal, lightVect), 75.0);

  // output color
  vec4 finalColor = (texture(iChannel0, texCoords + off) * WATER * (1.0 - reflectionAmount)) + (LIGHT.color * reflectionAmount * 0.9);
  finalColor.a = depth;

  //finalColor *= vec4(2, 2, 2, 1); //uncomment line for brightter water
  fragColor = finalColor;
}

#define PI 3.1415926535897932384626433832795

