// Converts RGB colors to a linear light scale
vec3 toLinear(vec3 color, transfer trc)
{
	bvec3 cutoff = lessThan(color, vec3(trc.cutoffToLinear));
	bvec3 negCutoff = lessThanEqual(color, vec3(-1.0*trc.cutoffToLinear));
	vec3 higher = pow((color + trc.off)/(1.0 + trc.off), vec3(trc.power));
	vec3 lower = color/trc.slope;
	vec3 neg = -1.0*pow((color - trc.off)/(-1.0 - trc.off), vec3(trc.power));

	color = mix(higher, lower, cutoff);
	color = mix(color, neg, negCutoff);

	return color;
}

// Gamma-corrects RGB colors to be sent to a display
vec3 toGamma(vec3 color, transfer trc)
{
	bvec3 cutoff = lessThan(color, vec3(trc.cutoffToGamma));
	bvec3 negCutoff = lessThanEqual(color, vec3(-1.0*trc.cutoffToGamma));
	vec3 higher = (1.0 + trc.off)*pow(color, vec3(1.0/trc.power)) - trc.off;
	vec3 lower = color*trc.slope;
	vec3 neg = (-1.0 - trc.off)*pow(-1.0*color, vec3(1.0/trc.power)) + trc.off;

	color = mix(higher, lower, cutoff);
	color = mix(color, neg, negCutoff);

	return color;
}

// x = current wavelength
// u = control point wavelength
#define gauss(x, u, o, A)\
	A/o*exp(-0.5*pow((x - u)/o, 2.0))

// Converts from a wavelength to LMS.
// Constructed using multi-peak Gaussian functions generated using Labplot
// and closely fitted to the CIE 2006 2° cone fundamentals created by
// Stockman & Sharp 2000).
// Takes an 'amount' vector which determines how strong each type of
// color blindness will be
vec3 waveToLms(float wave, vec3 amount)
{
	// LMS Gaussian function parameters for each cone type
	const vec3[5] lParams = vec3[](
		vec3(449.682, 21.6622, 2.36612),
		vec3(477.589, 11.0682, 1.39883),
		vec3(532.488, 25.7494, 34.0478),
		vec3(570.2, 5.91487, 0.243387),
		vec3(585.858, 34.98, 77.8669)
	);

	const vec3[5] mParams = vec3[](
		vec3(450.237, 19.5222, 3.33537),
		vec3(479.559, 13.3211, 3.68813),
		vec3(519.924, 17.1502, 9.68484),
		vec3(542.8, 3.27696, 0.105766),
		vec3(552.158, 33.3895, 77.9298)
	);

	const vec3[5] sParams = vec3[](
		vec3(467.661, 8.84562, 5.32073),
		vec3(422.211, 10.2028, 8.58498),
		vec3(443.084, 11.9848, 19.6347),
		vec3(444.863, 1.30608, -0.0330768),
		vec3(460.886, 25.7907, 24.9128)
	);

	// Color blindness simulation constants
	const vec3 white = inverse(xyzFromLms)*whiteE;
	const vec3 blue = inverse(xyzFromLms)*LmsRgb.primaries[2];
	const vec3 red = inverse(xyzFromLms)*LmsRgb.primaries[0];

	const vec2 prota = inverse(mat2(
		white.g, blue.g,
		white.b, blue.b
	))*vec2(white.r, blue.r);

	const vec2 deuta = inverse(mat2(
		white.r, blue.r,
		white.b, blue.b
	))*vec2(white.g, blue.g);

	const vec2 trita = inverse(mat2(
		white.r, red.r,
		white.g, red.g
	))*vec2(white.b, red.b);

	// Color blindness adjusted parameters for each cone type
	vec3[5] lParamsMod = vec3[](
		mix(lParams[0], mParams[0], amount.x),
		mix(lParams[1], mParams[1], amount.x),
		mix(lParams[2], mParams[2], amount.x),
		mix(lParams[3], mParams[3], amount.x),
		mix(lParams[4], mParams[4], amount.x)
	);

	vec3[5] mParamsMod = vec3[](
		mix(mParams[0], lParams[0], amount.y),
		mix(mParams[1], lParams[1], amount.y),
		mix(mParams[2], lParams[2], amount.y),
		mix(mParams[3], lParams[3], amount.y),
		mix(mParams[4], lParams[4], amount.y)
	);

	vec3[5] sParamsMod = vec3[](
		mix(sParams[0], mParams[0], amount.z),
		mix(sParams[1], mParams[1], amount.z),
		mix(sParams[2], mParams[2], amount.z),
		mix(sParams[3], mParams[3], amount.z),
		mix(sParams[4], mParams[4], amount.z)
	);

	// Color blindness adaptation matrices
	/*mat3 adaptProta = mat3(
		1.0 - amount.x, 0.0, 0.0,
		prota.x*amount.x, 1.0, 0.0,
		prota.y*amount.x, 0.0, 1.0
	);

	mat3 adaptDeuta = mat3(
		1.0, deuta.x*amount.y, 0.0,
		0.0, 1.0 - amount.y, 0.0,
		0.0, deuta.y*amount.y, 1.0
	);

	mat3 adaptTrita = mat3(
		1.0, 0.0, trita.x*amount.z,
		0.0, 1.0, trita.y*amount.z,
		0.0, 0.0, 1.0 - amount.z
	);

	mat3 adapt = adaptTrita*adaptDeuta*adaptProta;*/

	mat3 adapt = mat3(
		1.0 - amount.x, deuta.x*amount.y, trita.x*amount.z,
		prota.x*amount.x, 1.0 - amount.y, trita.y*amount.z,
		prota.y*amount.x, deuta.y*amount.y, 1.0 - amount.z
	);

	// Return the LMS values for the given wavelength
	return (adapt*vec3(1))*vec3(
		// L cone response curve
		gauss(wave, lParamsMod[0].x, lParamsMod[0].y, lParamsMod[0].z) +
		gauss(wave, lParamsMod[1].x, lParamsMod[1].y, lParamsMod[1].z) +
		gauss(wave, lParamsMod[2].x, lParamsMod[2].y, lParamsMod[2].z) +
		gauss(wave, lParamsMod[3].x, lParamsMod[3].y, lParamsMod[3].z) +
		gauss(wave, lParamsMod[4].x, lParamsMod[4].y, lParamsMod[4].z),

		// M cone response curve
		gauss(wave, mParamsMod[0].x, mParamsMod[0].y, mParamsMod[0].z) +
		gauss(wave, mParamsMod[1].x, mParamsMod[1].y, mParamsMod[1].z) +
		gauss(wave, mParamsMod[2].x, mParamsMod[2].y, mParamsMod[2].z) +
		gauss(wave, mParamsMod[3].x, mParamsMod[3].y, mParamsMod[3].z) +
		gauss(wave, mParamsMod[4].x, mParamsMod[4].y, mParamsMod[4].z),

		// S cone response curve
		gauss(wave, sParamsMod[0].x, sParamsMod[0].y, sParamsMod[0].z) +
		gauss(wave, sParamsMod[1].x, sParamsMod[1].y, sParamsMod[1].z) +
		gauss(wave, sParamsMod[2].x, sParamsMod[2].y, sParamsMod[2].z) +
		gauss(wave, sParamsMod[3].x, sParamsMod[3].y, sParamsMod[3].z) +
		gauss(wave, sParamsMod[4].x, sParamsMod[4].y, sParamsMod[4].z)
	)/sqrt(2.0*PI);
}

// Scales a color to the closest in-gamut representation of that color
// Preserves both hue and luminosity, but at the cost of saturation
vec3 gamutScale(vec3 color)
{
	float luma = (toXyz*color).y;

	float low = min(color.r, min(color.g, min(color.b, 0.0)));
	float high = max(color.r, max(color.g, max(color.b, 1.0)));

	float lowScale = low/(low - luma);
	float highScale = max((high - 1.0)/(high - luma), 0.0);
	float scale = max(lowScale, highScale);
	color += scale*(luma - color);

	return color;
}


const float minWave = 390.0;
const float maxWave = 730.0;
const float waveRange = maxWave - minWave;


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	// Number of pixels to the left and right of the current pixel to sample
	// the LMS spectral values
	const float offset = 0.5;

	// Which period of 5-second intervals is currently active
	// Synchronizes with the default animation's timing
	float timeSegment = mod(iTime/5.0, 3.0);

	// How strong the color blindness is for each channel
	vec3 amount;

	// By default (or if the leftmost column of pixels was clicked last)
	if (iMouse.x == 0.0) {
		// Have the amount of each channel move on a sine wave that is
		// different for each channel, clamped so that only one changes
		// at a time, each taking 5 seconds. iTime values of note:

		// amount.x ('L') starts:  0.0; peaks:  2.5; ends:  5.0
		// amount.y ('M') starts:  5.0; peaks:  7.5; ends: 10.0
		// amount.z ('S') starts: 10.0; peaks: 12.5; ends: 15.0
		amount = vec3(iTime*2.0/15.0);
		amount += vec3(1.0/6.0, -0.5, -7.0/6.0);
		amount = clamp(sin(amount*PI)*2.0 - 1.0, 0.0, 1.0);
	} else {
		// Depending on which 'time segment' is currently observed, change
		// which curve the user can edit with their mouse

		// These are meant to be synchronized with the animation detailed
		// above, that way a user can pause the shader to manipulate the
		// curve that was being animated at the time they paused it
		amount = vec3(
			iMouse.x/iResolution.x*float(floor(timeSegment) == 0.0),
			iMouse.x/iResolution.x*float(floor(timeSegment) - 1.0 == 0.0),
			iMouse.x/iResolution.x*float(floor(timeSegment) - 2.0 == 0.0)
		);
	}

	// Obtain the LMS values to eventually become the color for each pixel
	vec2 uv = gl_FragCoord.xy/iResolution.xy;
	vec3 color = waveToLms(waveRange*uv.x + minWave, amount);

	// Only show the spectrum in the center slice
	bvec3 showSpectrum = bvec3(int(uv.y*3.0) % 3 - 1);
	color = mix(color, vec3(0), showSpectrum);

	// Uniformly brighten the image to fit it within sRGB while
	// retaining full accuracy
	color += inverse(xyzFromLms)*vec3(1.076);
	color /= 3.71;

	// Perform white balance and convert to RGB
	color = toRgb*xyzFromLms*whiteBalance*color;

	// Scale out-of-gamut colors just in case
	color = gamutScale(color);

	// Sample of the line 'offset' pixels to the left and right of the
	// current column of pixels
	vec3 bef = waveToLms(waveRange*(fragCoord.x + offset)/iResolution.x + minWave, amount)/2.5;
	vec3 aft = waveToLms(waveRange*(fragCoord.x - offset)/iResolution.x + minWave, amount)/2.5;

	// Apply white balance to LMS curve samples
	bef = toLms*xyzFromLms*whiteBalance*bef;
	aft = toLms*xyzFromLms*whiteBalance*aft;


	// Calculate distance from the L line
	vec2 p1L = vec2(fragCoord.x - offset, bef.x*iResolution.y);
	vec2 p2L = vec2(fragCoord.x + offset, aft.x*iResolution.y);
	vec2 toUvL = p2L - fragCoord;
	vec2 unitL = normalize(p2L - p1L);
	float distL = length(toUvL - dot(toUvL, unitL)*unitL);

	// Calculate distance from the M line
	vec2 p1M = vec2(fragCoord.x - offset, bef.y*iResolution.y);
	vec2 p2M = vec2(fragCoord.x + offset, aft.y*iResolution.y);
	vec2 toUvM = p2M - fragCoord;
	vec2 unitM = normalize(p2M - p1M);
	float distM = length(toUvM - dot(toUvM, unitM)*unitM);

	// Calculate distance from the S line
	vec2 p1S = vec2(fragCoord.x - offset, bef.z*iResolution.y);
	vec2 p2S = vec2(fragCoord.x + offset, aft.z*iResolution.y);
	vec2 toUvS = p2S - fragCoord;
	vec2 unitS = normalize(p2S - p1S);
	float distS = length(toUvS - dot(toUvS, unitS)*unitS);


	// Calculate line colors
	vec3 lineL = inverse(toLms)*whiteBalance*vec3(1, 0.1, 0.1);
	vec3 lineM = inverse(toLms)*whiteBalance*vec3(0.1, 1, 0.1);
	vec3 lineS = inverse(toLms)*whiteBalance*vec3(0.1, 0.1, 1);

	// Set all colored lines to the same luminosity
	lineL = lineL/lineL.y*0.25;
	lineM = lineM/lineM.y*0.25;
	lineS = lineS/lineS.y*0.25;

	// Convert line colors to RGB and scale them to be in-gamut so they blend smoothly
	lineL = gamutScale(toRgb*lineL);
	lineM = gamutScale(toRgb*lineM);
	lineS = gamutScale(toRgb*lineS);


	// Draw the black lines
	color = mix(vec3(0), color, clamp(distL - 2.5*iResolution.y/1024.0, 0.0, 1.0));
	color = mix(vec3(0), color, clamp(distM - 2.5*iResolution.y/1024.0, 0.0, 1.0));
	color = mix(vec3(0), color, clamp(distS - 2.5*iResolution.y/1024.0, 0.0, 1.0));

	// Draw the colored lines
	color = mix(lineL, color, clamp(distL - 1.0*iResolution.y/1024.0, 0.0, 1.0));
	color = mix(lineM, color, clamp(distM - 1.0*iResolution.y/1024.0, 0.0, 1.0));
	color = mix(lineS, color, clamp(distS - 1.0*iResolution.y/1024.0, 0.0, 1.0));


	// Apply display transfer characteristics (or 'gamma')
	color = toGamma(color, space.trc);

	// Set the pixel's color
	fragColor = vec4(color, 1);
}

