/*
* Leon's mod polar from : https://www.shadertoy.com/view/XsByWd
*/

vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}

/*
* end mod polar
*/

float	scene(vec3 p)
{  
    float	mind = 1e5;
    float	toris = 1e5;
    p.z += -37.;
    rotate(p.xz, 1.57-.035*iTime );
    rotate(p.yz, 1.57-.05*iTime );
    vec2 q;

    q = vec2( length(p.xz)-5., p.y );
	rotate(q, iTime);

    q = abs(q)-3.;

    toris = length(q)-2.5;

    mind = mylength(p.xyz)-20.;
    p.xyz = fract(p.xyz*2.)-.5;
    mind = max(mind, (length(p.xyz)+.0+toris*1.0+-5.50+20.0*.1250025*(1./max(toris*toris + .25, .05)) ) );
    mind = abs(mind)+.005;

    h += (1.5-rc)*1./max(.01, toris*toris+ 2.25);
    h += vec3(.5, .1, .1)*1./max(.01, toris*toris*10.+.25);
    h += vec3(.92, .27,.1)*1./max(.01, mind*mind*40. + .2505101);

    return (mind);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);

	    for (float i = -1.; i < I_MAX; ++i)
	    {
	    	p = pos + dir * dist.y;
	        dist.x = scene(p);
	        dist.y += dist.x*.2;
	        if (dist.x < E || dist.y > FAR)
            {
                break;
            }
	        s.x++;
    }
    s.y = dist.y;
    return (s);
}

