vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}

// Default zoom point of (-1.2501902, 0.00612967 i), but previosly (-1.2501046 + 0.00611021 i)
// Zooms in on point that is good at showing rounding errors as it zooms in more and more 
#define cx -1.2501902
#define cy 0.00612967

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Variables
	float scale = 1.0 / (iTime * iTime * iTime + 1.0);
    float ax, ay;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Change perspectives w/ zoom
    ax = cx + ((uv.x * 3.5) - 2.5) * scale;
    ay = cy + ((uv.y * 2.0) - 1.0) * scale;
    vec2 c = vec2 (ax, ay);

    // Set up calculation (x = real, y = imaginary)
    vec2 z;
    int i;

    // Actual math of mandelbrot set
    for (i = 0; !(i > 512 || dot (z, z) > 16.0); i++) 
    {
        // Find "z = z^2 + c"
        z = vec2 ((z.x * z.x) - (z.y * z.y), 2.0 * z.x * z.y) + c;
    }

    float color;

    // If part of set, make it black, if not, divide by 512 for colored version
    if (i > 512)
    {
        color = 0.0;
    } 
    else
    {
		color = float (i) * 0.0625;
    }

    fragColor = vec4 (color, color, color, 1.0);
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

// Operator
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}


float unionSDF(float distA, float distB) {
    return min(distA, distB);
}


float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}


//Smooth 2 function
float blendingSDF( float a, float b)
{
    float k = 1.5;
    float h = max( k-abs(a-b), 0.0 );
    return min( a, b ) - h*h*h/(6.0*k*k);
}



float cubeSDF(vec3 p) {

    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);


    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);


    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

float sphereSDF(vec3 p) {
    return length(p) - 1.0;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// Twist
float opTwist( vec3 p )
{
    float c = cos(4.0*p.y + iTime);
    float s = sin(4.0*p.y + iTime);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return sdTorus(q, vec2(0.7,0.2));
}

// Displacement
 	float opDisplace( vec3 p )
{
    float d1 =  sphereSDF(p);
    float d2 = sin(2.0*p.x+ iTime)*sin(2.0*p.y+ iTime)*sin(2.0*p.z+ iTime);
    return d1+d2;
}


float sceneSDF(vec3 samplePoint) {
    float sphereDist = sphereSDF((samplePoint + vec3(3.0,0.0,0))/1.2) * 1.2;
    float cubeDist = cubeSDF(samplePoint+  + vec3(3.0,sin(iTime),0));
    float Diff = differenceSDF(cubeDist, sphereDist);


    float sphereDist1 = sphereSDF(samplePoint/1.2) * 1.2;
    float cubeDist1 = cubeSDF(samplePoint + vec3(0.0,-sin(iTime),0));
    float Uni =  unionSDF(sphereDist1,cubeDist1);

    float sphereDist2 = sphereSDF((samplePoint + vec3(-3.0,0.0,0))/1.2) * 1.2;
    float cubeDist2 = cubeSDF(samplePoint + vec3(-3.0,sin(iTime),0));
    float Inter =  intersectSDF(sphereDist2,cubeDist2);

    float sphereDist3 = sphereSDF((samplePoint + vec3(-8.0,0.0,0))/1.2) * 1.2;
    float sphereDist13 = sphereSDF((samplePoint + vec3(-8.0,2.5*-sin(iTime),0))/1.2) * 1.2;
    float sphereDist14 = sphereSDF((samplePoint + vec3(-8.0+ 2.5*-sin(iTime),0.0,0))/1.2) * 1.2;

    float Blend1 =  blendingSDF(sphereDist3,sphereDist13);
    float Blend2 =  blendingSDF(sphereDist13,sphereDist14);
    float Blend = blendingSDF(Blend1, Blend2);


    float Twist = opTwist(samplePoint + vec3(6.0,0.0,0));

    float Displace = opDisplace(samplePoint + vec3(9.0,0.0,0));

    float sum = unionSDF(unionSDF(unionSDF(unionSDF(unionSDF(Diff,Uni),Inter),Blend),Twist),Displace);




    return sum;
}


float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}



vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}



vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}


vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {

        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {

        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}


vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 ,
                          40.0,
                          -40.0 );
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);


    return color;
}


mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, -30.0);

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    vec3 K_a = vec3(0.2, 0.2, 0.2);
    vec3 K_d = vec3(0.7, 0.2, 0.2);
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

