      float sd = SphereSDF(pn.xyz);  

      sd = max(BoxSDF(pn.xyz,vec3(0.95)),-sd); 
      float  torus = TorusSDF(CreateRotationY(iTime)*(p + CreateRotationY(iTime*0.3)*vec3(0.0,0.0,2.5)) ,0.9f,0.4f);
      sd = min(torus,sd);      
      float pd =  PlaneSDF(p,23.0); 
      float fin = min(sd,pd);
      closest = OBJECT;
      if(fin == pd)
      { closest = FLOOR_PLANE;}
      else if(fin == torus) 
      {closest = OBJECT2;}
      return fin;
}

vec3 GetNormal(vec3 p)
{
    int dummy = 0;
    return 
    normalize(vec3(
		CombineSDF(vec3(p.x + EPS, p.y, p.z), dummy) - CombineSDF(vec3(p.x - EPS, p.y, p.z), dummy),
		CombineSDF(vec3(p.x, p.y + EPS, p.z), dummy) - CombineSDF(vec3(p.x, p.y - EPS, p.z), dummy),
		CombineSDF(vec3(p.x, p.y, p.z + EPS), dummy) - CombineSDF(vec3(p.x, p.y, p.z - EPS), dummy)
		));
}

float Trace(Ray r,inout int hit)
{
    hit = 0;
    float t = 0.0f;

    for(int i =0; i < MAX_STEPS; ++i)
    {
       vec3 p = r.Orgin + r.Direction * t;
       int closest = 0;     
       float sd = CombineSDF(p, closest);

       if(sd < EPS)
       {
          hit = closest;
          return t;
       }
       t += sd;
    }
    return 0.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Frag coord normalization 
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Make center of the screen be (0,0)
    uv = uv * 2.0 - 1.0; 
    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //FOV
    float fovDist = tan(FOV_DEG  *0.5 * M_PI / 180.0); 
    uv *= fovDist;

    //TODO: TASK2
    //CHANGE CAMERA POSITION AND ORIENTATION HERE:
    Ray r;

    vec3 uv2 = vec3(uv,1.0);		//Camera angle stuff tick
    uv2 *= CreateRotationX(radians(35.0));
    r.Direction = normalize(uv2);
    r.Orgin = vec3(0,4,-5.0);


    int hit = NO_HIT;   // indicates type of the object hit
    float t = Trace(r,hit);  
    vec3 hitAt = r.Orgin + r.Direction * t;
    vec3 N = GetNormal(hitAt);

    float diff,spec = 0.0;
    vec3 col = vec3(0); 

    if(hit == OBJECT) // Block
    {        
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        col =  vec3(0.2,0.04,0.2)+ vec3(0.5,0.0,0.5) * diff + vec3(1.0) * spec;
    }
    else if(hit == OBJECT2) // Torus
    {
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        vec3 baseCol = vec3(clamp(cos(hitAt.y),0.0,1.0), clamp(sin(hitAt.y),0.0,1.0),0.0);
        col =  0.3 * baseCol + 0.7 * baseCol * diff + vec3(1.0)*spec; 
    } 
    else if(hit == FLOOR_PLANE) // Floor
    {
        float ar = iResolution.x/iResolution.y;
        vec2 planeUV = vec2(80.0+hitAt.x,hitAt.z)/200.0;
        col = texture(iChannel0, planeUV).xyz; 


        //TODO: TASK3
        // ADD FLOOR PATTERNS

        col *= abs(sin(hitAt.x*0.2));	//Black pattern tick - Consant is arbitray relating to spacing

        vec3 purple = vec3(0.5,0,0.5);
        col += 0.5 * abs(sin(hitAt.z*0.2)) * purple; //Purple pattern tick - Front constant does brightness

        // Stationary Spotlight tick - uses min to avoid over brightness with close objects
        //col = min(col*(20.0/length(hitAt)),col);	

        //Moving Spotlight - again uses lots of arbitary constants
        float move = abs(sin(iTime)*100.0);
        vec3 spotPos =vec3(0,0,move); 
        col = min(col*(20.0/length(spotPos-hitAt)),col);

    }

    else // NO HIT - BACKGROUND COLOR
    {
      col = 0.5 + 0.5 * cos(uv.xyx + vec3(0,2.3,3.5));
    }

    //TODO: TASK4
    //SCREEN SPACE EFFECTS

    //Constant greyscale
    //float grey = 0.21*col.x + 0.72*col.y + 0.07*col.z;
    //col = vec3(grey);


    //Left to Right transistion
    float screenX = fragCoord.x/iResolution.x;

    float level = pow(screenX,2.8);
    float grey = 0.21*col.x + 0.72*col.y + 0.07*col.z;
    col = vec3(grey*(1.0-level)+col.x*level,grey*(1.0-level)+col.y*level,grey*(1.0-level)+col.z*level);



   // Output to screen
    fragColor = vec4(col,1.0);
}

