#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

float box(vec2 p, vec2 s) {
    vec2 q = abs(p)-s;
    return length(max(q,0.0))+min(max(q.x,q.y),0.0);
}

float box(vec3 p, vec3 s) {
    vec3 q = abs(p)-s;
    return length(max(q,0.0))+min(max(max(q.x,q.y),q.z),0.0);
}

float boxcircle(vec2 p, float r0, float r1, float sign0, float sign1) {

    if (r1 < r0) {
        vec2 q = abs(p);
        q = vec2(min(q.x,q.y),max(q.x,q.y));
        vec2 corner = vec2(sqrt(r0*r0-r1*r1),1);
        vec2 q2 = q-corner;
        vec2 n = vec2(-corner.y,corner.x);
        if (min(sign1*dot(q2,n),-sign0*q2.x)>0.0) return length(q2);
    }

    float len = -sign0*(length(p)-r0);
    len = max(len,sign1*box(p,vec2(r1)));
    return len;
}

float map(vec3 p, float r0, float r1, float sign0, float sign1) {

    if (r1 < r0) {
        vec3 p2 = abs(p);
        vec3 q = vec3(min(min(p2.x,p2.y),p2.z),0,max(max(p2.x,p2.y),p2.z));
        q.y = dot(p2-q,vec3(1));
        vec2 q2 = vec2(length(q.xy),q.z);

        vec2 corner = vec2(sqrt(r0*r0-r1*r1),1);
        q2 = q2-corner;
        vec2 n = vec2(-corner.y,corner.x);
        if (min(sign1*dot(q2,n),-sign0*q2.x)>0.0) return length(q2);
    }

    float len = -sign0*(length(p)-r0);
    len = max(len,sign1*box(p,vec3(r1)));
    return len;
}

vec3 findnormal(vec3 p, float eps, float r0, float r1, float sign0, float sign1) {
    vec2 e = vec2(0,eps);

    return normalize(vec3(
        map(p+e.yxx,r0,r1,sign0,sign1),
        map(p+e.xyx,r0,r1,sign0,sign1),
        map(p+e.xxy,r0,r1,sign0,sign1))
       -map(p      ,r0,r1,sign0,sign1));
}

float line( vec2 pa, vec2 ba) 
{
    float h = max( dot(pa,ba)/dot(ba,ba), 0.0 );
    return length( pa - ba*h );
}

float visualize(vec2 p, float r0, float r1, float sign0, float sign1) {

    float len = abs(length(p)-r0);
    //len = min(len,abs(box(p,vec2(r1))));
    //float len = 1000.0;

    if (r1 < r0) {
        p = abs(p);
        p = vec2(min(p.x,p.y),max(p.x,p.y));
        vec2 corner = vec2(sqrt(r0*r0-r1*r1),1);
        vec2 q = p-corner;
        vec2 n = vec2(-corner.y,corner.x);

        len = min(len,min(abs(line(q,-corner*sign0)),abs(line(q,vec2(0,1)*sign1))));
    }
    //if (min(sign0*dot(q,n),sign1*dot(q,vec2(-1,0)))>0.0)len = length(q);

    return max(0.25-abs(len)*iResolution.y/32.0,0.0);
}

