        if (length(iMouse.xy) > 40.0) {
            rd.yz *= rot(iMouse.y/iResolution.y*3.15-3.15*0.5);
            rd.xz *= rot(iMouse.x/iResolution.x*3.15*2.0-3.15);
            ro.yz *= rot(iMouse.y/iResolution.y*3.15-3.15*0.5);
            ro.xz *= rot(iMouse.x/iResolution.x*3.15*2.0-3.15);
        }


        float d = 0.0;

        for( int i = 0; i < 200; i++) {
            vec3 p = ro+rd*d;
            float l = map(p,r0,r1,sign0,sign1)-0.2;
            d += l;
            if (d > 10.0 || l < d/iResolution.y) {
                break;
            }
        }
        if (d < 10.0) {
            vec3 p = ro+rd*d;

            vec3 normal = findnormal(p,0.001,r0,r1,sign0,sign1);
            vec3 light = vec3(-1.0/sqrt(3.0));


            fragColor = vec4(normal*0.2+0.8,1);
            fragColor *= max(dot(normal,-light),0.1);
        } else {
            fragColor = vec4(vec3(0.6,0.5,0.8)*(rd.y*0.5+0.5)
                            +vec3(0.8,0.7,0.4)*(dot(rd,vec3(-1.0/sqrt(3.0)))*0.5+0.5),1);
        }
    }
    // squareroot for 2.0 gamma
    fragColor = sqrt(fragColor);
}

