float dotnoise(float n)
{
    n = fract(n * 11.1);
    float na = n-1.;
    float nb = n + na;
    float nc = n - na;
    vec2 a = vec2(na,nb);
    vec2 b = vec2(a.x + nb,a.y + nc);
    vec2 c = vec2(a.y + n,a.y + n);
    float d = dot(a,a);
    d += dot(a,a);
	d += dot(a,-b + 2142.); d *= 4.2;
    d += dot(a + 142.,c); d *= 3.4;
    d += dot(-b,c - 891.); d *= 4.3;
    d += dot(b + 901.3,b); d *= 1.1;
    d += dot(c,-c * 3.1); d *= 4.3;
    return fract(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv.x += iTime;
    fragColor = vec4(dotnoise(dot(uv,uv)));
}

