void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (fragCoord - iResolution.xy / 2.) / iResolution.x;
	p *= 10.;

	float c = 0.;
	float t = fract(iTime);
	t *= t;
	t *= 2. - t;

	p *= mat2(1., -t, t, 1.) / (t * t + 1.);
	vec2 q = floor(p + .5);
	p -= q;
	p *= mat2(1., t, -t, 1.);

	float d = 10.;
	for (int i = 0; i < 4; i++) {
		d = min(d, max(abs(p.x - .5 - t * .5), abs(p.y - .5 + t * .5)) - t * .5);
		p *= mat2(0., 1., -1., 0.);
	}

	c = (.5 - t * .5) * mod(floor(q.x) + floor(q.y) + ceil(sign(max(abs(p.x), abs(p.y)) - .5) * .5), 2.);
	c += (c - 1. + t * .5) * floor(sign(d) * .5);
	fragColor = vec4(c);
}

