void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;

    float theta = atan(uv.y, uv.x);

    float i = 5.0, j = 6.0;

    float n = cos(0.003*i*iTime)*4.0+4.0;
    float d = sin(0.003*j*iTime)*4.0+4.0;

    float c = abs(iMouse.x/iResolution.x*2.0 - 1.0);		//using mouse to control "c"

    float r = 0.0;

    float factor = 0.0;
    for(int i=0; i < 16; i++)
    {
        r = (sin(n/d*theta))*0.5 + c;			//rose curve function
        float tmp = abs(length(uv) - r);
        factor += 1.0 - smoothstep(-1.5,1.5, tmp / fwidth(length(uv) - r));;
        theta += 3.1415926*2.0;
    }

    fragColor = vec4( 1. - factor );
}

#define reflections 2.
#define optimized 0

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float box(vec3 p, vec3 b) {
    vec3 a = abs(p) - b;
    return max(a.x, max(a.y, a.z));
}

vec4 sdf(vec3 p) {
    vec3 o = vec3(1000000.);
    for (float i = 0.; i < 8.; i++) {
        p -= vec3(1.83, 2.21, 1.916);
        float a = 0.8 + sin(cos(p.x + p.y) + p.z) * .23;
        p.xy *= mat2(cos(a), sin(a), -sin(a), cos(a));
        p = abs(p);
        a = 0.214;
        p.yz *= mat2(cos(a), sin(a), -sin(a), cos(a));
        p -= vec3(-.52, .6, -.23);
        a = -0.21;
        p.xz *= mat2(cos(a), sin(a), -sin(a), cos(a));

        vec3 d = p - vec3(cos(iTime * .07), sin(iTime * .15), 1.) * 2.;
        vec2 d1 = p.xz;
        o = min(o, vec3(dot(d, d), dot(d1, d1), p.z));
    }
    p -= vec3(1., 2., 3.);
    return vec4(max(sphere(p, 2.3085), -box(p, vec3(.5, 1.15, 3.9))), o*vec3(.1, .1, .5));
}

vec3 normal(vec3 p) {
    vec2 eps = vec2(.05, 0.);
    float dx = sdf(p + eps.xyy).x - sdf(p - eps.xyy).x;
    float dy = sdf(p + eps.yxy).x - sdf(p - eps.yxy).x;
    float dz = sdf(p + eps.yyx).x - sdf(p - eps.yyx).x;
    return normalize(vec3(dx, dy, dz));
}

float ambientOcclusion(vec3 p, vec3 n, float dist, float steps) {
    float ao = 0.;
    for (float i = 1.; i <= steps; i++) {
        ao += sdf(p + n * (i / steps * dist)).x;
    }
    return clamp(ao / steps, 0., 1.);
}

float translucency(vec3 p, vec3 n, float dist, float steps) {
    float ao = 0.;
    for (float i = 1.; i <= steps; i++) {
        ao += sdf(p - n * (i / steps * dist)).x;
    }
    return clamp(ao / steps, 0., 1.);
}

vec3 draw(vec3 pos, vec3 n, float i, float t, vec3 dir, vec3 o) {
    vec3 lightDir = normalize(vec3(1.));
    float diffuse = max(0., dot(lightDir, n)) * .4;
    float specular = pow(max(0., dot(normalize(lightDir + dir), n)), 16.) * .25;
    float rim = pow(1. - abs(dot(n, dir)), 5.) * .3;
    vec3 lightColor = vec3(.9, .85, .7);
    float ao = ambientOcclusion(pos, n, 0.25, 5.);
    float trans = translucency(pos, n, .4, 3.)*0.25;
    vec3 lighting = (ao + diffuse) * lightColor 
        + (rim + specular) * vec3(0.3, 0.4,.7)
        + trans * vec3(0.9, .4, .4);
    return o * lighting + vec3(i / 500., t / 50., i / 500.);
}

