void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelPos = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float fov = radians(60.);
    vec3 dir = normalize(vec3(pixelPos, 1. / tan(.5 * fov)));

    float a = iTime * .1;
    dir.xz *= mat2(cos(a), sin(a), -sin(a), cos(a));
    vec3 camPos = vec3(3., -4., -11.);


    float i, t;
    vec3 pos, o;
    bool hit = trace(camPos, dir, pos, i, t, o);
    vec3 n = normal(pos);

    vec3 col = vec3(0.);
    if (hit) {
        col = draw(pos, n, i, t, -dir, o);

        for (float j = 1.; j <= reflections; j++) {
            dir = reflect(dir, n);
            hit = trace(camPos, dir, pos, i, t, o);
            n = normal(pos);
            vec3 reflCol = draw(pos, n, i, t, -dir, o);

            col = mix(col, reflCol, .1 / j);
        }
    }

    fragColor = vec4(col,1.0);
}

