const float PI = 3.141592653589793;

const vec3 ambient = vec3(0.01);
const vec3 lightColor = vec3(1.);
vec3 lightOrigin = vec3(4.,4.,4.);
const float lightRadius = 3.;
const float lightIntensity = 10.;

uint seed;

uint pcg_hash(uint seed) {
    uint state = seed * 747796405u + 2891336453u;
    uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
    return (word >> 22u) ^ word;
}
float rand_float() {
    seed = pcg_hash(seed);
    return float(seed) * (1.0 / 4294967296.0);
}
vec2 rand_vec2() {
    float r = rand_float() * 2.0 * PI;
    return vec2(cos(r), sin(r));
}
vec3 rand_vec3() {
    float r = rand_float() * 2. * PI;
    float z = rand_float() * 2. - 1.;
    float z_scale = sqrt(1.0-z*z);
    return vec3(cos(r)*z_scale, sin(r)*z_scale, z);
}
uint initSeed() {
    uint seed = pcg_hash(uint(gl_FragCoord.x));
    seed = pcg_hash(seed + uint(gl_FragCoord.y));
    seed = pcg_hash(seed + uint(iTime*1000.));
    return seed;
}

vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra) {
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h < 0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

float plaIntersect(in vec3 ro, in vec3 rd, in vec4 p) {
    return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

mat3 setCamera(in vec3 ro, in vec3 ta, in float cr) {
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr), 0.);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

bool intersect(in vec3 ro, in vec3 rd,
    out vec3 pos, out vec3 normal, out vec3 color,
    out float roughness, out bool light) {

        vec3 sphPostions[4];
        sphPostions[0] = vec3(-0.5, 0.25, -0.5);
        sphPostions[1] = vec3(-0.5, 0.25, 0.5);
        sphPostions[2] = vec3(0.5, 0.25, -0.5);
        sphPostions[3] = vec3(0.5, 0.25, 0.5);

        vec3 sphColors[4];
        sphColors[0] = vec3(0.0, 0.5, 1.0);
        sphColors[1] = vec3(1.0, 0.0, 0.5);
        sphColors[2] = vec3(0.5, 1.0, 0.0);
        sphColors[3] = vec3(1.0, 0.5, 0.5);

        vec3 sphCenter;
        float sphRadius = 0.25;

        float tmin = 1e38, t;
        bool hit = false;

        vec4 plane = vec4(0.,1.,0.,0.);

        t = plaIntersect(ro, rd, plane);
        // t = (0.0 - ro.y) / rd.y;
        if (t > 0.0 && t<tmin) {
            tmin = t;
            pos = ro + rd * t;
            normal = vec3(0,1,0);
            // coffee color
            color = vec3(.8, .2, .2) * 0.25;
            // coffee roughness
            roughness = 0.;
            light = false;
            hit = true;
        }
        for (int i = 0; i < 4; i++) {
            sphCenter = sphPostions[i];
            t = sphIntersect(ro, rd, sphCenter, sphRadius).x;
            if (t>0.0 && t<tmin) {
                tmin = t;
                pos = ro + t*rd;
                normal = normalize(pos - sphCenter);

                roughness = 0.1;
                color = sphColors[i];

                light = false;
                hit = true;
            }
        }


        t = sphIntersect(ro, rd, lightOrigin, lightRadius).x;
        if (t>0.0 && t<tmin) {
            tmin = t;
            pos = ro + t*rd;
            normal = normalize(pos - lightOrigin);
            roughness = 0.;
            color = lightColor;
            light = true;
            hit = true;
        }

        return hit;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    seed = initSeed();
    vec2 jitter = vec2(rand_float(), rand_float()) ;

    vec2 uv = (gl_FragCoord.xy + jitter) / iResolution.xy; // 0 <> 1
    vec2 p = (-iResolution.xy + 2. * (gl_FragCoord.xy + jitter)) / iResolution.y; // -1 <> 1 by height

    vec3 ro = vec3(2.*sin(iTime), 2., -2.*cos(iTime));
    vec3 ta = vec3(0., 0., 0.);

    lightOrigin = vec3(4.* cos(iTime/.7), 4., 4.* sin(iTime/.7));

    mat3 camera = setCamera(ro, ta, 0.);

    vec3 rd = camera * normalize(vec3(p.xy, 2.2));


    // rt
    vec3 accum = vec3(0.);
    vec3 mask = vec3(1.);
    for (int i = 0; i < 10; i++) {
        vec3 pos, normal, color;
        float roughness;
        bool light;

        if (!intersect(ro, rd, pos, normal, color, roughness, light)) {
            // hit nothing
            accum += ambient * mask;
            break;
        }

        // hit light
        if (light) {
            accum += lightColor * mask;
            break;
        }

        // hit obj
        mask *= color;


        // pos to light origin ray test
        vec3 _v3;
        float _f;
        vec3 lo = lightOrigin + rand_vec3() * lightRadius;
        vec3 ld = normalize(lo - pos);
        if (intersect(pos + ld * 0.0001, ld, _v3, _v3, _v3, _f, light) && light) {
            float d = clamp(dot(normal, ld), 0.0, 1.0);
            d *= pow(asin(lightRadius/distance(pos, lightOrigin)), 2.0);
            accum += d * lightIntensity * lightColor * mask;
        }

        rd = normalize(mix(reflect(rd, normal), normal + rand_vec3(), roughness));
        ro = pos + 0.0001 * rd;

    }
    accum = pow( accum, vec3(0.4545) );
    fragColor = vec4(accum, 1.);

}

