mat2 rotate(float a) {
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}

vec3 layer(vec2 uv, float time) {
	vec3 col = vec3(0.);
	for (int i = 0; i < 15; i++) {
		vec2 p = 2. * hash2(float(i) + vec2(2.)) - 1.;
		p -= vec2(sin(.1 * hash(float(i) + vec2(20., 50.)) * time + hash(float(i) + vec2(10.))), 
			  cos(.1 * hash(float(i) + vec2(20., 40.)) * time + hash(float(i) + vec2(10.))));
		float k = (.5 * hash(float(i) + vec2(25., 75.)) + .01);
		col += palette(k * 3., vec3(.5), vec3(.5), vec3(1.), vec3(.0, .33, .67)) / length(uv - p);
	}
	col /= 120.;
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec2 st = fragCoord.xy / iResolution.xy;
	vec3 col = vec3(0.);
	for (float i = 0.; i < 1.; i += .1) {
		uv *= rotate(iTime * 0.2 + i);
		float t = fract(iTime * .1 + i);
		float s = smoothstep(2., 0., t);
		float f = smoothstep(.8, 0., t)
		 * smoothstep(.0, .2, t);
		col += layer(uv * s, iTime) * f;
	}
	vec3 bb = texture(iChannel0, st - 0.5).rgb;
	fragColor = vec4(col * .2 + bb * .9, 1.);
}

float random (vec2 st) {
