void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float scale = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/scale;

    float dot_size = 0.9;

    // Output to screen
    float row = trunc(uv.y * 30.0);
    float col = trunc(uv.x * 30.0);

    // Time varying pixel color
    vec3 color = 0.5 + 0.5*cos(iTime+0.15*vec3(col, row, col)+vec3(0,2,4));


    float px = fract(uv.x * 30.0);
    float py = fract(uv.y * 30.0);
    if (px < dot_size && py < dot_size) {
      float distance = distance(vec2(px, py), vec2(dot_size/2.0));
      float noise = 0.15*(random(uv)-0.5);
      fragColor = vec4(color, 1.0) * vec4(1.0 - distance*distance + noise);
    }
    else
      fragColor = vec4(0.0);
}

