//Utils
mat3 CreateRotationX(float rotation)
{
    mat3 rotateX =
     mat3(
        1.0, 0.0, 0.0,
		0.0, cos(rotation), -sin(rotation),
		0.0, sin(rotation), cos(rotation)
    );

    return rotateX;
}

mat3 CreateRotationY(float rotation)
{
    mat3 rotateY = 
     mat3(
        cos(rotation), 0.0, sin(rotation),
		0.0, 1.0, 0.0, 
		-sin(rotation), 0.0, cos(rotation)
    );

    return rotateY;
}

mat3 CreateRotationZ(float rotation)
{
    mat3 rotateZ =
    mat3(
        cos(rotation), -sin(rotation), 0.0,
		sin(rotation), cos(rotation), 0.0,
		0.0, 0.0, 1.0
    );

    return rotateZ;
}

// SDFs
float SphereSDF(vec3 p)
{
    return length(p)-1.1;
}

float TorusSDF(vec3 p, float R, float r)
{  
    float xy = length(p.xy) - R;
    float z = length(vec2(xy,p.z))-r; 
    return z;
}

float BoxSDF(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0))-0.1;
}

float CylinderSDF(vec3 p,float radius)
{   
    float d = length(p.xz)-radius;
    d = max(d,p.y-1.0);
    d = max(d,-p.y-1.0);
    return d;
}

float PlaneSDF(vec3 p, float y)
{
    return p.y+y;
}

vec3 RepeatX(vec3 p, float offset)
{
    p.x = mod(p.x,offset) - offset/2.0;
    return p;
}

vec3 Twist(vec3 p, float amount)
{
    float c = cos(amount*p.y);
    float s = sin(amount*p.y);

    // Create 2D rotation matrix
    mat2 rm = mat2(c,-s,s,c);
    p.xz *=rm;
    return p;
}

