//Main scene distance field function
float CombineSDF(vec3 p, inout int closest)
{
      vec3 pn = p*vec3(1.0,0.6,1.0);;
      pn =  Twist(pn+vec3(0,0,-0.3),sin(iTime)*0.5);//CreateRotationY(iTime)* vec4(p,0);
      float sd = SphereSDF(pn.xyz);  

      sd = max(BoxSDF(pn.xyz,vec3(0.95)),-sd); 
      float  torus = TorusSDF(CreateRotationY(iTime)*(p + CreateRotationY(iTime*0.3)*vec3(0.0,0.0,2.5)) ,0.9f,0.4f);
      sd = min(torus,sd);      
      float pd =  PlaneSDF(p,23.0); 
      float fin = min(sd,pd);
      closest = OBJECT;
      if(fin == pd)
      { closest = FLOOR_PLANE;}
      else if(fin == torus) 
      {closest = OBJECT2;}
      return fin;
}

vec3 GetNormal(vec3 p)
{
    int dummy = 0;
    return 
    normalize(vec3(
		CombineSDF(vec3(p.x + EPS, p.y, p.z), dummy) - CombineSDF(vec3(p.x - EPS, p.y, p.z), dummy),
		CombineSDF(vec3(p.x, p.y + EPS, p.z), dummy) - CombineSDF(vec3(p.x, p.y - EPS, p.z), dummy),
		CombineSDF(vec3(p.x, p.y, p.z + EPS), dummy) - CombineSDF(vec3(p.x, p.y, p.z - EPS), dummy)
		));
}

float Trace(Ray r,inout int hit)
{
    hit = 0;
    float t = 0.0f;

    for(int i =0; i < MAX_STEPS; ++i)
    {
       vec3 p = r.Orgin + r.Direction * t;
       int closest = 0;     
       float sd = CombineSDF(p, closest);

       if(sd < EPS)
       {
          hit = closest;
          return t;
       }
       t += sd;
    }
    return 0.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Frag coord normalization 
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Make center of the screen be (0,0)
    uv = uv * 2.0 - 1.0; 
    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //FOV
    float fovDist = tan(FOV_DEG  *0.5 * M_PI / 180.0); 
    uv *= fovDist;

    //TODO: TASK2
    //CHANGE CAMERA POSITION AND ORIENTATION HERE:
    Ray r;
    r.Direction = normalize(vec3(uv,1.0)) * CreateRotationX(35.0 * DEG_TO_RAD);
    r.Orgin = vec3(0,4.0,-5.0);

    int hit = NO_HIT;   // indicates type of the object hit
    float t = Trace(r,hit);  
    vec3 hitAt = r.Orgin + r.Direction * t;
    vec3 N = GetNormal(hitAt);

    float diff,spec = 0.0;
    vec3 col = vec3(0); 

    if(hit == OBJECT) // Block
    {        
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        col =  vec3(0.2,0.04,0.2)+ vec3(0.5,0.0,0.5) * diff + vec3(1.0) * spec;
    }
    else if(hit == OBJECT2) // Torus
    {
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        vec3 baseCol = vec3(clamp(cos(hitAt.y),0.0,1.0), clamp(sin(hitAt.y),0.0,1.0),0.0);
        col =  0.3 * baseCol + 0.7 * baseCol * diff + vec3(1.0)*spec; 
    } 
    else if(hit == FLOOR_PLANE) // Floor
    {
        float ar = iResolution.x/iResolution.y;
        vec2 planeUV = vec2(80.0+hitAt.x,hitAt.z)/200.0;
        col = (texture(iChannel0, planeUV).xyz); 
        //TODO: TASK3
        // ADD FLOOR PATTERNS
        col = col * abs(sin(hitAt.x * 0.4));
        col = col + vec3(0.5,0.0,0.5)*abs(sin(hitAt.z*0.1)*0.4);
        col = col * vec3(1,1,1) * (18.0/(distance(vec2(hitAt.x + 10.0 * sin(iTime), hitAt.z + 10.0 * sin(iTime)), uv)));


    }

    else // NO HIT - BACKGROUND COLOR
    {
      col = 0.5 + 0.5 * cos(uv.xyx + vec3(0,2.3,3.5));
    }
    //TODO: TASK3
    //SCREEN SPACE EFFECTS

    float ltor = fragCoord.x/iResolution.x;

    float xcol = ((col.x * (1.0 + 2.0 * pow(ltor,2.8))) + (col.y * (1.0 - pow(ltor,2.8))) + (col.z * (1.0-pow(ltor,2.8))))/3.0;
	float ycol = ((col.x * (1.0 - pow(ltor,2.8))) + (col.y * (1.0 + 2.0 * pow(ltor,2.8))) + (col.z * (1.0-pow(ltor,2.8))))/3.0;
    float zcol = ((col.x * (1.0 - pow(ltor,2.8))) + (col.y * (1.0-pow(ltor,2.8))) + (col.z * (1.0 + 2.0 * pow(ltor,2.8))))/3.0;
    col = vec3(xcol, ycol, zcol);

   // Output to screen
    fragColor = vec4(col,1.0);
}

// palette generator from iq: https://www.shadertoy.com/view/ll2GD3
vec3 palette (float t) {
	vec3 a = vec3(0.5,0.5,0.5);
	vec3 b = vec3(0.5,0.5,0.5);
	vec3 c = vec3(2.0,1.0,0.0);
	vec3 d = vec3(0.5,0.20,0.25);
	return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set up uv coordinate system
    vec2 center = vec2(sin(iTime*0.25)/2., cos(iTime*0.31)/2.);
	vec2 p = ((2. * fragCoord.xy) - iResolution.xy) / iResolution.xy;

    // create uniform rectangular distance field
	float left = step(p.x, center.x);
	float bot = step(p.y, center.y);
	float pNewX1 = (p.x - center.x)/(1.0 - center.x);
	float pNewY1 = (p.y - center.y)/(1.0 - center.y);
	float pNewX2 = (p.x - center.x)/(-1.0 - center.x);
	float pNewY2 = (p.y - center.y)/(-1.0 - center.y);
	vec2 pNew = vec2(pNewX2*left+pNewX1*(1.0-left), pNewY2*bot+pNewY1*(1.0-bot));
	float dist = max(abs(pNew.x), abs(pNew.y));

    // generate your palette color based on the distance field
	vec3 palette = palette(fract(iTime*0.1+dist));

    // transform the distance field to create boxes with highlights
	float boxes = pow(dist, .3);
	boxes = sin(boxes * 300.) * 0.5 + 0.5;
    boxes = smoothstep(-0.15, 0.95, boxes);
	float highlight = pow((boxes), 9.0)/2.;

    // final color
	vec3 color = (palette * boxes) + (palette * highlight);
    color *= sqrt(dist);
	fragColor = vec4(color, 1.0);
}

//Jamie Pendergast
vec2 c_mul(vec2 l, vec2 r)
{
    vec2 z;
    z.x = (l.x * r.x) - (l.y * l.y);
    z.y = (l.x * l.y) + (r.x * r.y);
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = -1. + 2. * fragCoord/iResolution.xy;
    c.x *= iResolution.x / iResolution.y;
	c *= exp(-iTime); // zooming    
    c += vec2(-0.25,0.);  //panning

    vec2 a = c; // first complex number
    vec2 z = c; // second complex number

    vec2 offset = vec2(2.,0.); //definately play with this
    int i = 0;

    for(i; i < 2000; ++i)
    {
        if(dot(z,z) > 512.) break;
        a = c_mul(z - c,z - c) + c; //play with this
        z = c_mul(a - offset,a) - c; //play with this
    }


    if(i == 2000)
    {
    	fragColor = vec4(0.3,0.,0.,1.);
        return;
    }

    float rat = (float(i) - log2(log2(dot(z,z))));
    rat /= 100.;


    fragColor = vec4(rat,rat *= 2.,rat *= 2.,1.);
}

