float KelpDf (vec3 p)
{
  p.xz -= 1.;
  p.xz = Rot2D (p.xz, p.y * pi * floor (vRan.y * 10.) / 40.);
  return 0.9 * PrRndBoxDf (p, vec3 ((0.1 + 0.5 * vRan.x) *
    (0.8 + 0.2 * sin (0.2 * pi * p.y)), 30., 0.02 + 0.01 * vRan.y), 0.005);
}

