void BSetConf (vec3 cId)
{
  vRan = Hashv3f (dot (cId, vec3 (33., 43., 53.)));
  bOcc = (vRan.x * smoothstep (2., 5., length (cId)) > 0.95);
}

float BubDf (vec3 p, vec3 pw)
{
  vec3 b, vr;
  float s, t, r, d, a;
  t = tCur;
  vr = vRan - 0.5;
  s = pow (fract (dot (vr, vec3 (1.)) * 100.), 4.);
  pw.y += t;
  pw = 7. * pw + 2. * pi * vr + vec3 (t, 0., t);
  d = sin ((t + vr.y) * 3. * pi * (1. - s)) * 0.3 * vr.x * (1. - s);
  a = vr.z * t;
  b = d * vec3 (cos (a), 0., sin (a));
  r = 0.1 - 0.05 * s + dot (sin (pw), vec3 (1.)) * s * 0.02;
  return 0.9 * PrSphDf (p - b, r);
}

float BObjRay (vec3 ro, vec3 rd)
{
  vec3 p, cId, cIdP, s;
  float dHit, d;
  rd = RdNonz (rd);
  dHit = 0.1;
  cIdP = vec3 (-99.);
  for (int j = 0; j < 80; j ++) {
    p = ro + rd * dHit;
    p.y -= tCur * 4. + 40.;
    cId = floor (p / bRep);
    if (cId != cIdP) {
      cIdP = cId;
      BSetConf (cId);
    }
    d = bOcc ? BubDf (p - bRep * (cId + 0.5), p) : dstFar;
    s = (bRep * (cId + step (0., rd)) - p) / rd;
    d = min (d, abs (Minv3 (s)) + 0.01);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 BObjNf (vec3 p, vec3 pw)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  v = vec4 (- BubDf (p + e.xxx, pw), BubDf (p + e.xyy, pw), BubDf (p + e.yxy, pw),
     BubDf (p + e.yyx, pw));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 FishCol (vec3 p)
{
  vec4 col4;
  vec3 ve;
  vec2 spk;
  float s, t;
  col4 = vec4 (HsvToRgb (vec3 (0.15 + 0.8 * fHue, 0.8, 1.)), 0.2);
  if (idObj == idBody) {
    col4.rgb *= 0.7 + 0.3 * smoothstep (-0.1, 0.1, pFish.y);
    spk = SpkShp ();
    col4.rgb = mix (col4.rgb, vec3 (0.9, 0.7, 0.7), 0.5 * smoothstep (0., 0.01, spk.x) *
       SmoothBump (0.25, 0.75, 0.1, mod (128. * atan (0.7 * pFish.x, pFish.y - 0.1) / pi, 1.)));
    col4 = mix (col4, vec4 (0.9, 0.9, 0.95, 0.5), 0.5 * step (0.02, spk.x) * step (spk.y, 0.02));
  } else if (idObj == idMouth) {
    col4.rgb = mix (col4.rgb, vec3 (0.9, 0.7, 0.7), 0.5);
  } else if (idObj == idFinV || idObj == idFinL) {
    col4.rgb = mix (col4.rgb, vec3 (1.), 0.3 * smoothstep (0.4, 0.5, length (pFish)));
  } else if (idObj == idTail) {
    col4.rgb = mix (col4.rgb, vec3 (1.), 0.3 * smoothstep (0.6, 0.7, length (pFish)));
  } else if (idObj == idEye) {
    if (abs (pFish.y - eyeOff.y - 0.04) < 0.001 + 0.1 * SmoothBump (0.13, 0.87, 0.07,
       mod (0.5 * tCur + 2. * pi * eyeBlnk, 1.))) {
      ve = eyeOff * vec3 (sign (pFish.x), 1., 1.) * szFac + fCell - (fShift + fMove);
      ve = RotToDirLim (normalize (vuPos - ve), vec3 (sign (pFish.x), 0., 0.), p - ve, 0.35 * pi);
      s = length (ve.yz) / szFac;
      t = (1. - step (s, 0.06 + 0.02 * SmoothBump (0.25, 0.75, 0.25,
         mod (12. * atan (ve.y, ve.z) / pi, 1.))));
      col4.rgb = vec3 (1.) * t * (0.5 + 0.5 * smoothstep (0.07, 0.1, s));
      col4.a = (t == 0.) ? -1. : 0.3;
    } else idObj = idBody;
  }
  return col4;
}

float WatShd (vec3 rd)
{
  vec2 p;
  float t, h;
  rd = RdNonz (rd);
  p = 20. * rd.xz / rd.y;
  t = tCur * 2.;
  h = sin (2. * p.x + 0.77 * t + sin (0.73 * p.y - t)) + sin (0.81 * p.y - 0.89 * t +
     sin (0.33 * p.x + 0.34 * t)) + 0.5 * (sin (1.43 * p.x - t) + sin (0.63 * p.y + t));
  h *= 0.04 * smoothstep (0.5, 1., rd.y);
  return h;
}

float TurbLt (vec3 p, vec3 vn, float t)
{
  vec4 b;
  vec2 q, qq;
  float c, tt;
  q = 2. * pi * mod (vec2 (dot (p.yzx, vn), dot (p.zxy, vn)), 1.) - 256.;
  t += 11.;
  c = 0.;
  qq = q;
  for (float j = 1.; j <= 7.; j ++) {
    tt = t * (1. + 1. / j);
    b = sin (tt + vec4 (- qq + vec2 (0.5 * pi, 0.), qq + vec2 (0., 0.5 * pi)));
    qq = q + tt + b.xy + b.zw;
    c += 1. / length (q / sin (qq));
  }
  return clamp (pow (abs (1.25 - abs (0.167 + 40. * c)), 8.), 0., 1.);
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = tCur * 4.;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0., 0.5, 0.8), vec3 (0.25, 0.4, 1.), 0.5 + 0.5 * rd.y) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + gd * 0.05);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn, cId, p;
  float dstObj, d;
  fRep = vec3 (6., 6.5, 11.5);
  kRep = vec3 (4., 40., 4.);
  bRep = vec3 (4., 4., 4.);
  eyeOff = vec3 (0.13);
  fMove = vec3 (0., 0., -1.5 * tCur);
  bgCol = BgCol (rd);
  dstObj = dstFar;
  d = FObjRay (ro, rd);
  if (d < dstObj) {
    dstObj = d;
  }
  d = KObjRay (ro, rd);
  if (d < dstObj) {
    dstObj = d;
    idObj = idKelp;
  }
  d = BObjRay (ro, rd);
  if (d < dstObj) {
    dstObj = d;
    idObj = idBub;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    if (idObj <= idEye) {
      p = ro + fMove;
      cId = floor (p / fRep);
      FSetConf (cId);
      fCell = fRep * (cId + 0.5);
      vn = FObjNf (p - fCell);
      col4 = FishCol (ro);
    } else if (idObj == idKelp) {
      p = ro;
      p.x += sin (tCur + p.y * 0.2) * 0.5;
      cId = floor (p / kRep);
      KSetConf (cId);
      vn = KObjNf (p - kRep * (cId + 0.5));
      col4 = vec4 (0.2, 0.7, 0.3, 0.);
    } else if (idObj == idBub) {
      p = ro;
      p.y -= tCur * 4. + 40.;
      cId = floor (p / bRep);
      BSetConf (cId);
      vn = BObjNf (p - bRep * (cId + 0.5), p);
      col4 = vec4 (0.7 * mix (vec3 (1.), BgCol (reflect (rd, vn)), 0.7), 0.5);
    }
    if (col4.a >= 0.) {
      col = col4.rgb * (0.1 + 0.2 * bgCol + 0.7 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
      if (idObj != idEye) col += 0.1 * TurbLt (0.5 * ro, abs (vn), 0.5 * tCur) *
         smoothstep (-0.3, -0.1, vn.y);
      else col = mix (col, vec3 (1., 0., 0.), 0.1 * dot (vn, - rd));
      if (idObj == idBub) col *= 0.5 + 0.5 * clamp (rd.y + 1., 0., 1.5);
    } else {
      rd = reflect (rd, vn);
      col = 0.5 * (BgCol (rd) + WatShd (rd));
    }
    col = mix (col, bgCol, smoothstep (0.3, 0.95, dstObj / dstFar));
  } else col = bgCol + WatShd (rd);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = pi + 2. * pi * SmoothBump (0.25, 0.75, 0.25, mod (0.01 * tCur, 1.));
  el = -0.22 * pi * sin (0.017 * pi * tCur);
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.6 * pi * mPtr.y;
  }
  el = clamp (el, -0.3 * pi, 0.3 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
     mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0.01);
  vuPos = ro;
  ltDir = normalize (vec3 (0.2, 1., -0.2));
  dstFar = 50.;
  col = vec3 (0.);
  if (abs (uv.y) < 0.85) {
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = 0.; a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), 2.));
      col += (1. / naa) * ShowScene (ro, rd);
    }
    col = pow (col, vec3 (0.8));
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrRndBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 RotToDirLim (vec3 v1, vec3 v2, vec3 p, float aMax)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = max (dot (v1, v2), cos (aMax));
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

vec3 Hashv3f (float p)
{
  return fract (sin (p + vec3 (37., 39., 41.)) * cHashM);
}

