// iq's Capsule sdf modified
float sdThing( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    vec3 pbh = pa - ba*h;
    return max(1.5,mylength(pbh)) - r*length( pbh )*.25;
}

float	scene(vec3 p)
{  
    float	var;
    float	mind = 1e5;
    p.z -=-30.;
    rotate(p.xz, 1.57*1.0-1.05*iTime*1.0+.5*sin(iTime*2.));

    vec3 pp = p;
    ret_col = 1.-vec3(.5, .5, .3);

    rotate(p.zy, -iTime*.125+p.x*(.125+.105*sin(-iTime*.25) ));

    mind = sdThing(p, vec3(-10., -5.0*cos(iTime*1.+p.x*.5), -5.0*sin(iTime*1.+p.x*.5) ), vec3(10.,.0,.0), 2.5);
    mind = abs(mind)-.25;
    mind = mix(mind, max(mind, sin(p.x*3.+iTime*50.)+.998), .5 + .5*sin(iTime*.25));    
    mind = mix(abs(mind)+.01, mind, .5 + .5*sin(iTime*.25));

    h += vec3(2.75,.8,.5)*vec3(1.)*.0125/max(.01, (mind)*(mind) );

    return (mind)*.25;
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);
    for (float i = -1.; i < I_MAX; ++i)
    {
	   	p = pos + dir * dist.y;
	    dist.x = scene(p)*1.;
        dist.y += dist.x; // makes artefacts disappear
        if (dist.x < E || dist.y > FAR)
        {
            break;
        }
        s.x++;
    }
    s.y = dist.y;
    return (s);
}

// Utilities

float	mylength(vec3 p) {return max(max(abs(p.x), abs(p.y)), abs(p.z));}
float	mylength(vec2 p) {return (max(abs(p.x), abs(p.y)));}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 cameraLookAt(vec3 target, vec3 camPos, vec3 up, vec2 uv, float camNear)
{
    vec3 axisZ = normalize(target - camPos);
    vec3 axisX = cross(axisZ, up);
    vec3 axisY = cross(axisX, axisZ);
    return normalize(axisX * uv.x + axisY * uv.y + camNear * axisZ); 
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}

const int Steps = 1000;
const float Epsilon = 0.01; // Marching epsilon
const float T=0.5;

const float rA=10.0; // Maximum ray marching or sphere tracing distance from origin
const float rB=40.0; // Minimum

// Transforms
vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca*p.y - sa*p.z, sa*p.y + ca*p.z);
}

vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca*p.x + sa*p.z, p.y, -sa*p.x + ca*p.z);
}

vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca*p.x + sa*p.y, -sa*p.x + ca*p.y, p.z);
}

vec3 scale(vec3 p, float s)
{
    return p * s;
}


// Smooth falloff function
// r : small radius
// R : Large radius
float falloff( float r, float R )
{
  float x = clamp(r/R,0.0,1.0);
  float y = (1.0-x*x);
  return y*y*y;
}

// Primitive functions

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : large radius
float blob(vec3 p, vec3 c, float e,float R)
{
  return e*falloff(length(p-c),R);
}

// Cone skeleton
// p : point 
// o : origin of skeleton
// h : size of cone
// a : angle of adjacent/opposite
// e : energy associated to skeleton
// r : radius
float cone(vec3 p, vec3 o, float e, float R)
{
	vec2 q    = vec2( length( p.xz ), p.y );
    float d1  = -p.y - o.z;
    float d2  = max( dot(q,o.xy), p.y );
    float res = length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.0);
    return e * falloff(res,R);
}

// Line skeleton
// p : point
// l1 : first point of the line
// l2 : second point of the line
// e : energy associated to skeleton
// R : large radius
float line(vec3 p, vec3 l1, vec3 l2, float e, float R) 
{
	vec3 direction = normalize(l2 - l1);
    vec3 l1p = p - l1;

    float dotProduct = dot(direction, l1p);

    if (dotProduct < 0.0) {
		return e*falloff(length(p-l1),R);
    } else if (dotProduct > length(l2 - l1)) {
        return e*falloff(length(p-l2),R);
    }

    // projection of p on the line
    vec3 l1H = dotProduct * direction;
    vec3 h = l1 + l1H;

	return e*falloff(length(p-h), R);
}

// Line skeleton with decrease
// p : point
// l1 : first point of the line
// l2 : second point of the line
// e : energy associated to skeleton
// R : large radius
float lineDecreasing(vec3 p, vec3 l1, vec3 l2, float e, float R, float minDecreasingRatio) {

	vec3 direction = normalize(l2 - l1);
    vec3 l1p = p - l1;

    float dotProduct = dot(direction, l1p);

    if (dotProduct <= 0.0) {
		return e*falloff(length(p-l1),R);
    } else if (dotProduct >= length(l2 - l1)) {
        return e*falloff(length(p-l2),minDecreasingRatio * R);
    }

    // projection of p on the line
    vec3 l1H = dotProduct * direction;
    vec3 h = l1 + l1H;

    float decreasingRatio = 1.0 - (dotProduct / length(l2-l1)) * (1.0 - minDecreasingRatio);

	return e*falloff(length(p-h), decreasingRatio * R);
}

// Circle skeleton
// p : point
// c : center
// r : radius
// n : circle normal
// e : energy associated to skeleton
// R : large radius
float circle(vec3 p, vec3 c, float r, vec3 n, float e, float R) {

    vec3 delta = p - c;
	vec3 CQ = delta - (dot(delta, n) * n);

    vec3 K = c + r * normalize(CQ);

	return e*falloff(length(p-K), R);
}

// Half Circle skeleton
// p : point
// c : center
// r : radius
// n : circle normal
// d : filled half direction
// e : energy associated to skeleton
// R : large radius
float halfCircle(vec3 p, vec3 c, float r, vec3 n, vec3 d, float e, float R) {

    float distance = 0.0;

    vec3 delta = p - c;

    if (dot(delta, d) < 0.0) {
        vec3 orthoVec = cross(d, n);

  		return line(p, c -  orthoVec * r, c + orthoVec * r, e, R);   
    } else {
        vec3 CQ = delta - (dot(delta, n) * n);

        vec3 K = c + r * normalize(CQ);

        distance = length(p-K);
    }

    return e*falloff(distance, R);
}

// Cuboid
// p : point
// c : center of cuboid
// X : size of x edge
// Y : size of Y edge
// Z : size of z edge
// e : energy asociated to skeleton
// R : large radius
float cuboid(vec3 p, vec3 c, float X, float Y, float Z, float e, float R)
{	
    float x = abs(p.x- c.x);
    float y = abs(p.y- c.y);
    float z = abs(p.z- c.z);

    float distX = 0., distY = 0., distZ = 0.;
    if (x > X/2.)
        distX = x - X/2.;
    if (y > Y/2.)
        distY = y - Y/2.;
    if (z > Z/2.)
        distZ = z - Z/2.;

    float dist = sqrt(distX*distX + distY*distY + distZ*distZ);


    //return e * falloff(max(max(x,y),z),R);       
    return e * falloff(dist,R);     
}

// Cube
// p : point 
// s : size of cube
// e : energy associated to skeleton
// s : size of cube
// R : large radius
float cube(vec3 p, vec3 s, float e, float R) {
	return e * falloff(length(max(abs(p) - s, 0.0)), R);
}

// Tore
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// r : small radius
// R : large radius
float tore(vec3 p, vec3 c, float e, float r, float R)
{
    p.x = p.x - c.x;
    p.y = p.y - c.y;
    p.z = p.z - c.z;
    float xz  = (sqrt((p.x*p.x) + (p.z*p.z)) - R);
	float yr  = (p.y*p.y)-(r*r);
    float res = ((xz * xz) + yr);
    return e * falloff(res,R);
}           

// Blending
// a : field function of left sub-tree
// b : field function of right sub-tree
float Blend(float a,float b)
{
    return a+b;
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
    return max(a,b);
}

//Intersection
// a : field function of left sub-tree
// b : field function of right sub-tree
float Intersection(float a, float  b)
{
    return min(a,b);
}

// Private
// a : field function of left sub-tree
// b : field function of right sub-tree
float Deprived(float a, float b)
{
    return a-b;
}

float Interpolation(float a, float b, float c)
{
    return mix(a,b,c);
}

// Potential field of the object
// p : point
float object(vec3 p)
{
    p.z=-p.z;
    float tmp = p.x;
    if(iTime < 10.) {
        p.x = tmp - 10. + iTime;
        p = scale(p,1.5);
    }
    else
        p = scale(p,1.5);

    // Boby
	vec3 pabs = vec3(abs(p.x),p.y,p.z);
    // Corps
    float v = cuboid(p, vec3(0.0,0.0,0.0), 2.0,4.5,1.0,1.0, 4.0); 
    // Les yeux
    //Les globes
    float gb = blob(pabs,vec3(1.0,1.6,2.5),2.5,1.3);
    // L'oeuil
    float y = blob(pabs,vec3(1.0,1.6,2.5),2.5,1.3);
	y = Union(v,blob(pabs,vec3(1.05,1.65,3.0),3.5,0.55));

    v = Blend(v,gb);
    v = Union(v,y);
    // Les sourcils
    v = Blend(v,cuboid(rotateZ(pabs,-0.55),vec3(0.0,3.0,2.15),0.12,0.3,0.1,1.0,.2));
	v = Blend(v,cuboid(pabs,vec3(1.0,2.75,2.15),0.12,0.3,0.1,1.0,.2));
    v = Blend(v,cuboid(rotateZ(pabs,0.55),vec3(1.75,2.0 ,2.15),0.12,0.3,0.1,1.0,.2));

    // Le nez
    v = Blend(v,line(p,vec3(0.0,.8,1.5),vec3(0.0,1.0,2.5),1.0,0.5));

    // Bouche
    v = Deprived(v,Intersection(blob(p,vec3(0.0,-.8,1.5),50.0,1.),blob(p,vec3(0.0,-.3,1.5),50.0,1.)));

    // Les dents
    //v = Union(v,cuboid(p,vec3(0.25,.1,1.15),0.3,0.4,0.15,1.0,1.0));
    //v = Union(v,cuboid(p,vec3(-0.25,.1,1.15),0.3,0.4,0.15,1.0,1.0));

    // Les manches
    if( iTime < 25.){
    	v = Blend(v,cone(rotateZ(vec3(pabs.x - 2.5,pabs.y - 1.2,pabs.z),1.5), vec3(0.5,0.5,0.7), 0.6, .2));
    	// Les bras
    	v = Blend(v,line(pabs, vec3(3.5, 1.35, 0.0),vec3(6.1,1.5,0.0), .8, 0.8));  
    } 

    // Les jambes
    v = Blend(v,cone(rotateY(vec3(pabs.x - 0.5,pabs.y + 3.4,pabs.z),1.5), vec3(0.5,0.5,0.6), 0.6, .2));
    v = Blend(v,line(pabs, vec3(1.0, -3.0, 0.0),vec3(1.0,-6,0.0), .8, 0.8));  

    v = Deprived(v,blob(pabs, vec3(1.9,-1.3,2.),1.0, 0.45));
    v = Deprived(v,blob(pabs, vec3(1.4,-2.3,2.),1.0, 0.5));

  // PatPat  
    if(iTime < 10.) {
      p.x = tmp + 10. - iTime; 
      p = scale(p,0.9);
    }  
  if(iTime > 25.)  
  	p = scale(p,0.7);
  // pieds
  float g = lineDecreasing(vec3(abs(p.x) - 0.75,p.y,p.z) ,vec3( 0.0, -2.0, 0.0), vec3(1.0, -4.0, 0.0 + sin(4.0*iTime)),1.0,1.75, 0.3);

  // buste
  g = Blend(g,blob(p,vec3(0.0, -0.75 ,0.0),2.5,2.5));
  // bras
  g = Blend(g,lineDecreasing(vec3(-abs(p.x), p.y, p.z),vec3(-1.0, -0.25, 0.0), vec3(-3.0, 0.5, 0.0 + sin(4.0*iTime)), 1.0,1.25, 0.5));
  // tete
  g = Blend(g,lineDecreasing(p,vec3(0.0, 1.0, 0.0), vec3(0.0, 3.0, 0.0), 1.5, 1.5, 0.5));
  // yeux
  g = Blend(g, blob(vec3(abs(p.x) - 1.25, p.y, p.z), vec3(-0.9, 2.0, 0.5), 1.0, 0.5));
  g = Blend(g, blob(vec3(abs(p.x) - 1.25, p.y, p.z), vec3(-0.9, 2.0, 0.7), 1.0, 0.1));

  // nombril
  g = Deprived(g, blob(p, vec3(0.0, -1.5, 1.8), 1.0, 0.9));

  // sourcils
  g = Union(g, line(vec3(abs(p.x) - 0.1, p.y, p.z), vec3(0.6, 2.7, 0.5), vec3(0.0, 2.75, 0.5), 0.7, 0.2));

  // bouche
  g = Blend(g, halfCircle(p, vec3(0.0, 1.3, 0.8), 0.4, vec3(0.0, 0.0, -1.0), vec3(0.0, -1.0, 0.0), 1.0, 0.2));

 if (iTime > 25.)
    return Interpolation(g,v,0.6)-T;   
 else if(iTime > 20.)
    return blob(p,vec3(0.0),1.0,exp(0.07*iTime)) - T;
 else if(iTime > 12.)
   return (Interpolation(g,v,0.1 + sin(0.5*iTime))-T) * (0.25*iTime); 
 else
    return Blend(g,v) - T;
}

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.0001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

    // Don't start at the origin, instead move a little bit forward
    float t=rA;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p  = o + (t*u);
    float v = object(p);
    // Hit object
      if (v > 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += Epsilon;
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}

// Trace ray using ray marching
// o  : ray origin
// u  : ray direction
// h  : hit
// s  : Number of steps
float SphereTrace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

    // Don't start at the origin, instead move a little bit forward
    float omega      = 1.2;
    float t          = rA;
    float c_t		 = rA;
    float previous_v = 0.;
    float stepL      = 0.;
    float c_error    = 999.;
	float signe      = object(o) < 0. ? -1. : 1.;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3  p   = o+(t*u);
    float s_v = signe * object(p);   
    float v   = object(p);
    bool test = (omega > 1. && (v + previous_v) < stepL);  

    if(test) {
        stepL  -= omega * stepL;
        omega  =  1.0;
    }
    else {
        stepL = s_v * omega; 
    }          

    previous_v  = v;
    float error = s_v/t;

    if(!test && error < c_error){
    	c_t     = t;
        c_error = error;
    }

      if (v > 0.) {
		s=i;
        h = true;
        break;
      }
    // Move along ray
    t += max(stepL,0.15);        
    // Escape marched far away
      if (!test && t>rB)
        break;
  }
  return t;
}


// Background color
vec3 background(vec3 rd)
{
  return mix(vec3(0.4, 0.3, 0.0), vec3(0.7, 0.8, 1.0), rd.y*0.5+0.5);
}

vec3 objectColor() {
  return mix(vec3(1.0, 0.3, 0.3), vec3(1.0, 1.0, 0.0), 0.25 * sin(0.5*iTime));
}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n)
{
  // point light
  const vec3 lightPos   = vec3(5.0, 5.0, 5.0);
  const vec3 lightColor = vec3(0.5, 0.5, 0.5);
  vec3 c = vec3(0.0);
  if(p.x < 0. && iTime < 12.)	
  	c = vec3(1.0, 0.3, 0.3);
  else if(p.x > 0. && iTime < 12.)  
 	c = vec3(1.0, .5, 0.0);
  else
    c = objectColor();

  vec3 l = normalize(lightPos - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = 0.5*(1.0+dot(n, l));

  c += diff*lightColor;

  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return vec3(t,0.25+0.75*t,0.5-0.5*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 pixel = (gl_FragCoord.xy / iResolution.xy)*2.0-1.0;

  // compute ray origin and direction
  float asp = iResolution.x / iResolution.y;
  vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -4.0));
  vec3 ro = vec3(0.0, 0.0, 20.0);

  // vec2 mouse = iMouse.xy / iResolution.xy;
  float ax=3.2;
  float ay=0.0;  
  if (iMouse.z > 0.0) {
   	ax = iMouse.x / iResolution.x * 6.0 + 0.5;
    ay = iMouse.y / iResolution.y * 6.0 + 0.5;  
  }
  ro = rotateY(ro, ax);
  rd = rotateY(rd, ax);
  //ro = rotateX(ro, ay);
  //rd = rotateX(rd, ay);  

  // Trace ray
  bool hit;

  // Number of steps
  int s;

  //float t = Trace(ro,rd,hit,s);
  float t = SphereTrace(ro, rd, hit,s);
  vec3 pos=ro+t*rd;
  // Shade background
  vec3 rgb = vec3(1.0);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pos);

    // Shade object with light
    rgb = Shade(pos, n);
    //rgb = vec3(0.5);  
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  //rgb = ShadeSteps(s);

  fragColor=vec4(rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 temp = uv * 2.0 - vec2(1.0);
    if(dot(temp,temp) > sin(atan(temp.y,temp.x)*5.0+iTime))
        discard;

    // Output to screen
    fragColor = vec4(col,1.0);
}

