// Created by Reinder Nijhoff 2015
// @reindernijhoff
//
// https://www.shadertoy.com/view/XdcGzr
//
// Based on matrix - 255 char by FabriceNeyret2: https://www.shadertoy.com/view/4tlXR4
// compacting to 2-tweets patriciogv's Matrix shader https://www.shadertoy.com/view/MlfXzN ( 819 -> 255 chars ) 
// But first go see patriciogv's comments and readable sources :-D
//
// All credits go to FabriceNeyret2
//


#define R fract(23.*sin(p.x*7.+p.y*8.))

void mainImage( out vec4 o, vec2 i) {
    vec2 j = fract(i*=.21), 
         p = vec2(92,int(iTime*(7.+8.*sin(i-=j).x)))+i;
    o-=o; o.g=R; p*=j; o*=R>.5&&j.x<.6&&j.y<.8?1.:0.;
}

// simple sphere march (+ fake light / ref)
mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}
#define	TAU 6.28318

// IQ's patched sphere parametrization to squash texture on to a sphere.
// Reference: http://iquilezles.org/www/articles/patchedsphere/patchedsphere.htm

vec2 sphereToCube(in vec3 pointOnSphere)
{
   return vec2(pointOnSphere.x/pointOnSphere.z,pointOnSphere.y/pointOnSphere.z);
}
/* Check if x and y are between 0 and 1. If so, return v,
 * otherwise return zeros. This allows us to use a sum of
 * vectors to test what face of the cube we are on */ 
vec2 insideBounds(vec2 v)
{
    vec2 s = step(vec2(-1.,-1.), v) - step(vec2(1.,1.), v);
    return s.x * s.y * v;
}

float GetWaveDisplacement(vec3 p)
{
    float time = iTime;
	float waveStrength = 0.1;
	float frequency = 4.0;
	float waveSpeed = 0.15;
	float rotSpeed = 0.1;
	float twist = 0.24;
	float falloffRange = 2.0;	// the other values have been tweaked around this...

	float d = length(p);
	p.xz *= rotate(d*twist+(time*rotSpeed)*TAU);
	vec2 dv = p.xz*0.15;
	d = length(dv);
	d = clamp(d,0.0,falloffRange);
	float d2 = d-falloffRange;
	float t = fract(time*waveSpeed)*TAU;
	float s = sin(frequency*d*d+t);
	float k = s * waveStrength * d2*d2;
	k *= p.x*p.z*0.5;
	//k-= 0.4;					// mix it up a little...
	k -= sin(fract(time*0.1)*TAU)*0.4*d2;			// really mix it up... :)
	k = smoothstep(0.0,0.45,k*k);
	return k;
}

float getSphereMappedTexture(in vec3 pointOnSphere)
{
    /* Test to determine which face we are drawing on.
     * Opposing faces are taken care of by the absolute
     * value, leaving us only three tests to perform.
     */
    vec2 st = (
        insideBounds(sphereToCube(pointOnSphere)) +
        insideBounds(sphereToCube(pointOnSphere.zyx)) +
        insideBounds(sphereToCube(pointOnSphere.xzy)));

    st *= 12.0;
    float k = GetWaveDisplacement(vec3(st.x,0.0,st.y))*0.5;
    k = clamp(k,0.0,1.0);
	return 1.0-k;
    //return textureFunc(st);
}



float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBumpedSphere(vec3 p)
{
	float k = getSphereMappedTexture(p) * 0.25;		// 
	float d = sdSphere(p, 4.0);
    return d+k;
}


float map(vec3 p)
{
    vec4 tt = vec4(iTime*0.03,iTime*0.07,iTime*0.5,iTime*0.75) * TAU;
	p.xz *= rotate(tt.x);
    p.zy *= rotate(tt.y);
    return sdBumpedSphere(p);
}

vec3 normal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}
vec3 render(vec3 ro, vec3 rd)
{
	// march	
	float tmin = 0.1;
	float tmax = 10.;
	vec3 p;
	float t = tmin;
	for (int i = 0; i < 180; i++)
	{
		p = ro + t * rd;
		float d = map(p);
		t += d*0.5;
		if (t > tmax)
			break;		
	}

    // light
	if (t < tmax)
	{
	   	vec3 lightDir = normalize(vec3(0.0, 1.0, -1.0));
		vec3 nor = normal(p);

		float dif = max(dot(nor, lightDir), 0.0);
		vec3 c = vec3(0.5) * dif;

        float tf = 0.02;
		c += vec3(0.35,0.02,0.58)*0.5 + reflect(vec3(p.x*tf, p.y*tf, 0.05), nor);

		vec3 ref = reflect(rd, nor);
		float spe = max(dot(ref, lightDir), 0.0);
		c += vec3(2.0) * pow(spe, 32.);
		return c;
	}

	return vec3(0.24,0.24,0.35);
}

mat3 camera(vec3 ro, vec3 ta, vec3 up)
{
	vec3 nz = normalize(ta - ro);
	vec3 nx = cross(nz, normalize(up));
	vec3 ny = cross(nx, nz);
	return mat3(nx, ny, nz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 q = (2.0*fragCoord.xy / iResolution.xy)-1.0;
	vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.xy;
	p.x *= iResolution.x / iResolution.y;

	vec3 ro = vec3(0.0, 0.0, 0.0);
    float ang = radians(-90.0);
    float d = 6.0;
    ro.z = sin(ang)*d;
    ro.x = cos(ang)*d;
	vec3 ta = vec3(0.0, 0.0, 0.0);

	vec3 rd = camera(ro, ta, vec3(0.0, 1.0, 0.0)) * normalize(vec3(p.xy, 1.0));

    // render
	vec3 c = render(ro, rd);

    // vignette
    float rf = sqrt(dot(q, q)) * 0.35;
    float rf2_1 = rf * rf + 1.0;
    float e = 1.0 / (rf2_1 * rf2_1);    
    c*=e;    

	fragColor = vec4(c, 1.0);
}

vec3 v;
vec3 lightSource = vec3(20.0, 0.0, 6.0);
float bLightSource = 0.6;
float ba = 0.4;

vec2 random2(vec2 st){
    //hash function to generate noise
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.177);
    }

float gNoise(vec2 st) {
    //gradient noise
	vec2 i = floor(st);
	vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
    }

float pi(){
 	return radians(180.0);   
}

vec2 intersectCircle(vec2 cp, vec2 rView){
    		//compute p and q from pq-formula
            float cp_projected = dot(rView, cp);
            return vec2(cp_projected, pow(cp_projected, 2.0)-dot(cp, cp));
    }



float diffuse(vec3 p, vec3 normal){
            vec3 dirLight = normalize(lightSource-p);
            return bLightSource*max(0.0, dot(normal, dirLight));
    }

vec3 chess(vec3 p, vec3 pFrag, vec3 normal){
    //computes the chess pattern with diffuse lightning
    vec3 cSand = vec3(0.76, 0.7, 0.5);
    vec3 cSandStone = vec3(0.76, 0.57, 0.5);
    vec3 cRock = vec3(0.71, 0.7, 0.65);

    float tileSize = 4.0;
    float pixSize = 3.0*2.0*length(v-p)
        							/(min(iResolution.x, iResolution.y)
                                    *length(v-pFrag)
                                    *tileSize);

    vec2 ij = floor(p.xy/tileSize);
   	vec3 cChess;
    if(mod(ij.x, 2.0)==mod(ij.y, 2.0)){
        cChess = cSand;
    }
    else{
     	cChess = cSandStone;   
    }

    vec2 pLocal = abs(fract(p.xy/tileSize)-0.5);
    float maxLocal = max(pLocal.x, pLocal.y);

    float bd = diffuse(p, normal);
    return (ba+bd)*mix(cRock, cChess, smoothstep(0.0, pixSize, 0.49-maxLocal));  
}

vec3 rostrum(vec3 p, vec3 rView, float radius){
    //essentially intersect ray with cylinder
    vec3 cRock = vec3(0.71, 0.7, 0.65);
    float h = 2.0;

    vec2 sol = intersectCircle(-p.xy, normalize(rView.xy));

    vec3 pCylinder = p + rView*
        				(sol.x+sqrt(sol.y+pow(radius, 2.0)))
                         /length(rView.xy);

    vec3 nCylinder = normalize(vec3(-pCylinder.xy, 0.0));
    vec3 pPlane = p - (p.z-h) * rView / rView.z;

    float bd = mix(diffuse(pCylinder, nCylinder),
             diffuse(pPlane, vec3(0.0, 0.0, 1.0)),
             smoothstep(h-0.1, h, pCylinder.z));
    return (ba+bd)*cRock;
}

vec3 computeBackground(vec3 p, vec3 rView){
 	vec3 cSand = vec3(0.76, 0.7, 0.5);
    vec3 cSandStone = vec3(0.76, 0.57, 0.5);
    vec3 cSky = vec3(0.5, 0.76, 0.7);

    //check if XY-plane is visible
    if(dot(rView, vec3(0.0, 0.0, 1.0))>-0.01*3.14){
     	return cSky;   
    }

    //calculate intersection with XY-plane
    vec3 pXY = p - p.z * rView / rView.z;
    float l0 = length(pXY);
    float l1 = length(p - (p.z-2.0) * rView / rView.z);
    vec2 polar = vec2(acos(pXY.x/l0)*sign(pXY.y)+pi(), l0);

    if(l1>30.0){

     	return cSky;  
    }
    else if(l0>25.0){
        //compute rostrum
        vec3 cRostrum = rostrum(p, rView, 25.0);
        vec3 mix0 = mix(chess(pXY, p, vec3(0.0, 0.0, 1.0)),
                        cRostrum,
                   		smoothstep(25.0, 25.4, l0));  
     	return mix(mix0, cSky,
                   smoothstep(29.3, 30.0, l1));   
    }
    else{
        //compute chess pattern
        return chess(pXY, p, vec3(0.0, 0.0, 1.0));   
    }
}




mat3 rotationMatrix(const float angle, const vec3 axis){
 	float alpha = radians(angle);
    float c = cos(alpha);
    float s = sin(alpha);
    mat3 R;
    R[0] = axis * axis.x * (1.0-c) + vec3(c, axis.z*s, -axis.y*s);
    R[1] = axis * axis.y * (1.0-c) + vec3(-axis.z*s, c, axis.x*s);
    R[2] = axis * axis.z * (1.0-c) + vec3(axis.y*s, -axis.x*s, c);
    return R;
}

vec3 getCameraPosition(vec4 mouse){
    //computes position of camera from difference between
    //clicked and current pixel-coordinates
    vec3 v0 = 20.0*vec3(1.0, 0.0, 1.0);

    if(iMouse.w<0.){
        return v0;
    }
    else{
        vec3 v;
    	vec2 angle = 200.0*(mouse.xy-mouse.zw);
        angle.y = min(abs(angle.y), 40.0)*sign(angle.y);

        vec3 axVert = vec3(0.0, 0.0, 1.0);
        mat3 rVert = rotationMatrix(angle.x, axVert);
        v = rVert*v0;

        vec3 axFlat = normalize(cross(-v, axVert));
		mat3 rFlat = rotationMatrix(angle.y, axFlat);
        return rFlat*v;
    }
}

bool overlap(vec2 i0, vec2 i1){
    //true if intervall 0 overlaps with intervall 1
    if(i0.x>i0.y){
      float temp = i0.x;
      i0.x = i0.y;
      i0.y = temp;  
    }
    return (clamp(i0.x, i1.x, i1.y)!=clamp(i0.y, i1.x, i1.y));
}

float f(vec2 pxy, float radius){
 	//height of floor
    float smoother = smoothstep(-radius, -0.3*radius, -length(pxy));
    return 0.5*smoother*(1.0+sin(iTime+10.0*gNoise(0.2*pxy)));
}

vec3 raymarch(vec3 p, vec3 r, float radius, vec2 center){
            float tol = 0.01;
    		vec3 pHat = p;
            float fValue=1000.0;
            float tolMarch = 30.0*tol;

    		int iMax = 1000;
            int i = 0;

    		//first perform simple raymarch
            while(fValue>0.0 && i<iMax){
            pHat = pHat+tolMarch*r;
            fValue = min(pHat.z-f(pHat.xy, radius),
                         radius-length(center-pHat.xy));
            }

    if(pHat.z-f(pHat.xy, radius)>0.0){
        //terminate if ray hit boundary of cylinder
        return vec3(1000.0);
    }
    else{
        //perform local bisection for better quality
        float b1 = (pHat.z-p.z)/r.z;
        float b0 = b1-tolMarch;


        float eta = 10000.0;

        i = 0;
        float b;
        while(abs(eta)>tol && i<iMax){
                    b = 0.5*(b0+b1);
                    pHat = p+b*r;
                    eta = pHat.z-f(pHat.xy, radius);
                    if(eta<0.0){
                            b1 = b;
                    }
                    else{
                            b0 = b;
                    }
                    i = i+1;
            }		
    	return pHat;
    }
}

vec4 warpedFloor(vec3 pFrag, vec3 p0, vec3 rView, float radius, vec2 center){

 	vec3 pSurface = raymarch(p0, rView, radius, center);
    if (length(center-pSurface.xy)>radius){
        	//if ray hit cylinder return vector with alpha = 0
        	return vec4(0.0);
    	}
    else{
        	vec3 pProjected = vec3(pSurface.xy, 0.0);
        	float h = 0.001;

            float fValue = f(pProjected.xy, radius);
            float dx = (f(pProjected.xy+vec2(h, 0.0), radius)-fValue)/h;
            float dy = (f(pProjected.xy+vec2(0.0, h), radius)-fValue)/h;

        	vec3 normal = normalize(cross(vec3(1.0, 0.0, dx), vec3(0.0, 1.0, dy)));


        	return vec4(chess(pProjected, pFrag, normal), 1.0);
    }


}

vec4 computeObject(vec3 p, vec3 rView){
    vec3 cBrick = vec3(0.8, 0.25, 0.33);
    vec2 center = vec2(0.0);
    float radius =  10.0;
    float h =1.0;

    //check if ray intersects a covering cylinder
    //if yes, perform raymarch to compute the pixels color
    vec2 sol = intersectCircle(center-p.xy, normalize(rView.xy));
    float lxy = length(rView.xy);

    if(pow(radius, 2.0)+sol.y<=0.0){
        return vec4(cBrick, 0.0);
    }

    float temp = sol.x+sqrt(sol.y+pow(radius, 2.0));
    vec3 p0 = p+(-sol.y-pow(radius, 2.0)+pow(sol.x, 2.0))*rView/(temp*lxy);
    vec3 p1 = p+temp*rView/lxy;


    if(overlap(vec2(p1.z, p0.z), vec2(0, h))){
        return warpedFloor(p, p0, rView, radius, center);

    }
       else{

           return vec4(cBrick, 0.0);
       }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0*(fragCoord-0.5*iResolution.xy)/min(iResolution.x, iResolution.y);
    vec4 mouse = iMouse/min(iResolution.x, iResolution.y);

    // set up camera
    //vec3 v = 10.0*vec3(1.0, 0.0, 1.0);
    v = getCameraPosition(mouse);
    vec3 vDir = normalize(-v);
    vec3 vLeft = normalize(cross(vDir, vec3(0.0, 0.0, 1.0)));
    vec3 vUp = cross(vLeft, vDir);
    float dScreen = 2.5;

    //calculate fragment position in space
    vec3 pFrag = v + dScreen * vDir + uv.x*vLeft + uv.y * vUp;

    vec3 rView = normalize(pFrag-v);

    //compute the warped segment and if visible
    //the background
    vec4 color = computeObject(pFrag, rView);
    if(color.w<0.5){
       color = vec4(computeBackground(pFrag, rView), 1.0);
    }


    // Output to screen
    fragColor = color;
}

// @lsdlive
// CC-BY-NC-SA


mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

// Tunnel pattern studied from shane & shau
// i.e. https://www.shadertoy.com/view/4tKXzV
vec2 path(float t) {
	float a = sin(t*.2 + 1.5), b = sin(t*.2);
	return vec2(a*2., a*b);
}

// http://mercury.sexy/hg_sdf/
// hglib mirrorOctant
void mo(inout vec2 p, vec2 d) {
	p = abs(p) - d;
	if (p.y > p.x)p = p.yx;
}

// hglib pMod1
float re(float p, float d) {
	return mod(p - d * .5, d) - d * .5;
}

// hglib pModPolar
void amod(inout vec2 p, float d) {
	float a = re(atan(p.x, p.y), d);
	p = vec2(cos(a), sin(a)) * length(p);
}

// iq's signed cross sc() - http://iquilezles.org/www/articles/menger/menger.htm
float sc(vec3 p, float d) {
	p = abs(p);
	p = max(p, p.yzx);
	return min(p.x, min(p.y, p.z)) - d;
}

float g = 0.;
float de(vec3 p) {

	p.xy -= path(p.z);

	p.xy *= r2d(p.z*.05);

	vec3 q = p;

    // cylinder section
	amod(q.xy, 6.28 / 7.);
	mo(q.xy, vec2(1, .6));
	q.xy *= r2d(q.z*.5);
	mo(q.xy, vec2(.1, .2));
	amod(q.xy, 6.28 / 3.);
	float cyl2 = length(q.xy) - .05;

    p.z = re(p.z, .4);

    // cross structure section
	amod(p.xy, .785*2.);
	mo(p.xz, vec2(3, .01));
	amod(p.xy, .785*.5);
	p.x = abs(p.x) - 2.1;
	mo(p.xy, vec2(.4, 2));
	float d = sc(p, .02);

	g += .008 / (.01 + d * d);// glow trick from balkhan https://www.shadertoy.com/view/4t2yW1
	d = min(d, cyl2); // outside of the glow computation for cool effect & better contrast

	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;

	float dt = iTime * 3.;
	vec3 ro = vec3(0, 0, -3. + dt);
	vec3 ta = vec3(0, 0, dt);

	ro.xy += path(ro.z);
	ta.xy += path(ta.z);

	vec3 fwd = normalize(ta - ro);
	vec3 right = normalize(cross(fwd, vec3(0, 1, 0)));
	vec3 up = normalize(cross(right, fwd));
	vec3 rd = normalize(fwd + right * uv.x + up * uv.y);

	vec3 p;
	float t = 0., ri;
	for (float i = 0.; i < 1.; i += .02) {
		ri = i;
		p = ro + rd * t;
		float d = de(p);
		d = max(abs(d), .002);// phantom mode trick from aiekick https://www.shadertoy.com/view/MtScWW
		t += d * .8;
	}

	vec3 c = mix(vec3(.5, .4, .3), vec3(.2, .1, .2), uv.x + ri);
	c += g * .034;// glow trick from balkhan https://www.shadertoy.com/view/4t2yW1

	fragColor = vec4(c, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float fft = texture(iChannel0,vec2(uv.x,uv.y)).x;
    vec3 col;
    col.x = uv.y*sin(iTime);
    col.y = uv.x*cos(iTime);
    col.z = (uv.x+fft)*sin(cos(uv.y)*iTime);

    if(fragCoord.y>iResolution.y/2.5 && fragCoord.y<iResolution.y/1.5){
        col.y+=fft*uv.y;
        col.x+=fft*uv.x;
        col.z+=fft*uv.y;
    }

    // Output to screen
    fragColor = vec4(col*(1.+(fft*0.1)),1.0);
}

