//------------------------------------------------------------------------
// Modelling 
//------------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y;
}

#define PI 3.141592
#define	TAU 6.28318

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// simple bend(Y) mod
vec2 opBendTest( vec2 p, float angle, float xmod )
{
    p.x += xmod;
    p = rotate( angle * p.x ) * p.xy;
    p.x -= xmod;
    return p;
}
// simple spriral(x) mod
float spiral(vec2 p, float sa, float b)
{
  float a = atan(p.y, p.x);
  float l = length(p);
  float n = (log(l/sa)/b - a) / (2.*PI);
  float upper_ray = sa * exp(b *(a + 2.*PI*ceil(n)));
  float lower_ray = sa * exp(b *(a + 2.*PI*floor(n)));
  return min(abs(upper_ray - l), abs(l-lower_ray));
}

// 2D-shapes (Trapezoid, Ring, Box, Triangle)
float dot2(in vec2 v ) { return dot(v,v); }
float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);

	p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y < 0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );

    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;

    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float ring(vec2 uv, float rad, float thickness)
{
    return abs(rad - length(uv)) - thickness;
}

float sdBox( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p) - b;
	return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}    

// signed distance to a 2D triangle
float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float NewSymbol(vec2 uv)
{
    vec2 p = uv+vec2(0.27,0.09);
	float s1 = length(p)-0.2;						// circle1
  	float s2 = length(p+vec2(-0.22,-0.01))-0.18;	// circle2
	p.x = spiral(p.xy, PI*0.5, -0.33);				// Spiral distort
    float d = sdTrapezoid(p,0.015,0.015,0.08);		// could just be a box...
	d = max(d, s1);									// subtract circle
	d = max(d,-s2);									// subtract circle

    p = vec2(abs(uv.x),uv.y);
    //d = min(d,sdTriangle(vec2(-0.155,0.1),vec2(-0.055,0.1),vec2(-0.105,0.05),p.yx)); // mid-cross tri
    d = min(d, sdTrapezoid(p.yx+vec2(0.105,-0.075),0.0,0.05,0.025));   			// mid-cross trapeziod!
    d = min(d,sdBox(p+vec2(-0.05,0.105),vec2(0.025,0.025)));						// mid-cross bar
    d = min(d,sdTriangle(vec2(0.0, -0.425),vec2(0.1, -0.25),vec2(-0.025, -0.35),p.xy)); // bottom triangle

	p = opBendTest(uv+vec2(0.16, -0.088),radians(77.0),-0.103);
    d = smin(d, sdTrapezoid(p.yx,0.018,0.025,0.19), 0.012); // bent arm (attempted to smooth the join, needs work)

    d = min(d,sdBox(uv+vec2(0.0,0.13),vec2(0.032,0.2)));	// main body
    d = min(d,sdBox(uv+vec2(-0.08,-0.085),vec2(0.09,0.025))); // horn1 (bar)

    float cuts = length( uv+vec2(-0.17,-0.32))-0.21;
    cuts = min(cuts,length( uv+vec2(-0.17,0.15))-0.21);
    cuts = min(cuts,length( uv+vec2(-0.73,-0.085))-0.4);
    p = uv+vec2(-0.26,-0.085);
    d = min(max(sdTrapezoid(p.yx,0.025,0.13,0.09),-cuts),d); // horn2 (cutout)

    d = smin(d,ring(uv+vec2(0.0,-0.26),0.13,0.028),0.01);			// top-ring


    return d;
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opTwist( vec3 p )
{
    p.y += 0.5;
    float scale = sin(iTime*1.25)*0.15;

    float  c = cos(scale*p.y+scale);
    float  s = sin(scale*p.y+scale);
    mat2   m = mat2(c,-s,s,c);
    p = vec3(m*p.zy,p.x);
    p.y -= 0.5;
    return p;
}

vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca * p.y - sa * p.z, sa * p.y + ca * p.z);
}
vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}
vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x - sa * p.y, sa * p.x + ca * p.y, p.z);
}


vec2 doModel( vec3 p )
{
    vec2 res = vec2( sdPlane(p+vec3(0.0,0.45,0.0)), 1.0);	// floor...

    float t1 = fract(iTime*0.42)*TAU;
    float t2 = fract(-iTime*0.2)*TAU;

    vec3 r1 = rotateY(p,t1);
    r1 = rotateY(r1, sin(t2-p.y*1.95));
    r1 = opTwist(r1);

    float symbolDist = NewSymbol(r1.xy);
    float dep = 0.02;
    vec2 e = vec2( symbolDist, abs(r1.z) - dep );
    symbolDist = min(max(e.x,e.y),0.0) + length(max(e,0.0));
    symbolDist -= 0.01;    

    res = opU(res,vec2(symbolDist,3.0));
    return res;
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in float mat )
{
    vec3 mal = vec3(0.0);
    if (mat<=1.0)
    {
        float f = mod( floor(1.5*pos.z) + floor(1.5*pos.x), 2.0);
        mal = 0.2 + 0.2*f+0.2*vec3(0.45,0.85,1.0)*2.0;
    }
    else
		mal = vec3(0.65,0.35,0.35)+reflect(pos*0.1, nor);     
    vec3  lig = normalize(vec3(0.7,0.35,1.0));		// dir
	vec3 color = mal * (dot(nor, lig) + pow(dot(nor, lig), 10.0)*4.0);

    // shadow
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0;
    if( dif>0.01 )
        sha=calcSoftshadow( pos+0.01*nor, lig );
    color *= sha;

    // fog    
    //-----------------------------
	color *= exp(-0.0085*dis*dis);

    return color;
}


vec2 calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 10.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	//float res = -1.0;
    vec2 res = vec2(-1.0,0.0);
    float c = 0.0;

    for( int i=0; i<100; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
        vec2 res2 = doModel( ro+rd*t );
	    h = res2.x;
        c = res2.y;

        t += h*0.75;
    }

    if( t<maxd )
    {
        res.x = t;
        res.y = c;
    }
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.001;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.25,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

  	vec3 col = vec3(0.1);

	// raymarch
    vec2 res = calcIntersection( ro, rd ); 
    float t = res.x;
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);
        col = doLighting( pos, nor, rd, t, res.y );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );
    fragColor = vec4( col, 1.0 );
}

// Settings
const float FOV_DEG = 80.0f;
const int MAX_STEPS = 300;
const vec3 LIGHT_DIR = vec3(1.0,1.0,-1.0);

// Consts / Enumerations 
const float EPS = 0.0001f;
const float DEG_TO_RAD = 0.0174533f;
const float M_PI = 3.14159265358979323846;
const int FLOOR_PLANE = 1;
const int OBJECT = 2;
const int OBJECT2 = 3;
const int NO_HIT = 0;

///////////////////////////////////////////////////////////

struct Ray
{
    vec3 Orgin;
    vec3 Direction;
};

//Utils
mat3 CreateRotationX(float rotation)
{
    mat3 rotateX =
     mat3(
        1.0, 0.0, 0.0,
		0.0, cos(rotation), -sin(rotation),
		0.0, sin(rotation), cos(rotation)
    );

    return rotateX;
}

mat3 CreateRotationY(float rotation)
{
    mat3 rotateY = 
     mat3(
        cos(rotation), 0.0, sin(rotation),
		0.0, 1.0, 0.0, 
		-sin(rotation), 0.0, cos(rotation)
    );

    return rotateY;
}

mat3 CreateRotationZ(float rotation)
{
    mat3 rotateZ =
    mat3(
        cos(rotation), -sin(rotation), 0.0,
		sin(rotation), cos(rotation), 0.0,
		0.0, 0.0, 1.0
    );

    return rotateZ;
}

// SDFs
float SphereSDF(vec3 p)
{
    return length(p)-1.1;
}

float TorusSDF(vec3 p, float R, float r)
{  
    float xy = length(p.xy) - R;
    float z = length(vec2(xy,p.z))-r; 
    return z;
}

float BoxSDF(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0))-0.1;
}

float CylinderSDF(vec3 p,float radius)
{   
    float d = length(p.xz)-radius;
    d = max(d,p.y-1.0);
    d = max(d,-p.y-1.0);
    return d;
}

float PlaneSDF(vec3 p, float y)
{
    return p.y+y;
}

vec3 RepeatX(vec3 p, float offset)
{
    p.x = mod(p.x,offset) - offset/2.0;
    return p;
}

vec3 Twist(vec3 p, float amount)
{
    float c = cos(amount*p.y);
    float s = sin(amount*p.y);

    // Create 2D rotation matrix
    mat2 rm = mat2(c,-s,s,c);
    p.xz *=rm;
    return p;
}

void Phong(vec3 L, vec3 N, vec3 V, float sh, out float d, out float s)
{
    // IMPLEMENT PHONG MODEL HERE
    d = dot(N, L);
    s = pow(dot(V, normalize(reflect(V, N))), sh);
}

//Main scene distance field function
float CombineSDF(vec3 p, inout int closest)
{
      vec3 pn = p*vec3(1.0,0.6,1.0);;
      pn =  Twist(pn+vec3(0,0,-0.3),sin(iTime)*0.5);//CreateRotationY(iTime)* vec4(p,0);
      float sd = SphereSDF(pn.xyz);  

      sd = max(BoxSDF(pn.xyz,vec3(0.95)),-sd); 
      float  torus = TorusSDF(CreateRotationY(iTime)*(p + CreateRotationY(iTime*0.3)*vec3(0.0,0.0,2.5)) ,0.9f,0.4f);
      sd = min(torus,sd);      
      float pd =  PlaneSDF(p,23.0); 
      float fin = min(sd,pd);
      closest = OBJECT;
      if(fin == pd)
      { closest = FLOOR_PLANE;}
      else if(fin == torus) 
      {closest = OBJECT2;}
      return fin;
}

vec3 GetNormal(vec3 p)
{
    int dummy = 0;
    return 
    normalize(vec3(
		CombineSDF(vec3(p.x + EPS, p.y, p.z), dummy) - CombineSDF(vec3(p.x - EPS, p.y, p.z), dummy),
		CombineSDF(vec3(p.x, p.y + EPS, p.z), dummy) - CombineSDF(vec3(p.x, p.y - EPS, p.z), dummy),
		CombineSDF(vec3(p.x, p.y, p.z + EPS), dummy) - CombineSDF(vec3(p.x, p.y, p.z - EPS), dummy)
		));
}

float Trace(Ray r,inout int hit)
{
    hit = 0;
    float t = 0.0f;

    for(int i =0; i < MAX_STEPS; ++i)
    {
       vec3 p = r.Orgin + r.Direction * t;
       int closest = 0;     
       float sd = CombineSDF(p, closest);

       if(sd < EPS)
       {
          hit = closest;
          return t;
       }
       t += sd;
    }
    return 0.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Frag coord normalization 
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Make center of the screen be (0,0)
    uv = uv * 2.0 - 1.0; 
    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //FOV
    float fovDist = tan(FOV_DEG  *0.5 * M_PI / 180.0); 
    uv *= fovDist;

    //TODO: TASK2
    //CHANGE CAMERA POSITION AND ORIENTATION HERE:
    Ray r;
    r.Direction = normalize(vec3(uv,1.0) * CreateRotationX(35.0 * DEG_TO_RAD));
    r.Orgin = vec3(0,0,-4.0) + vec3(0,4.0,-1.0);

    int hit = NO_HIT;   // indicates type of the object hit
    float t = Trace(r,hit);  
    vec3 hitAt = r.Orgin + r.Direction * t;
    vec3 N = GetNormal(hitAt);

    float diff,spec = 0.0;
    vec3 col = vec3(0); 

    if(hit == OBJECT) // Block
    {        
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        col =  vec3(0.2,0.04,0.2)+ vec3(0.5,0.0,0.5) * diff + vec3(1.0) * spec;
    }
    else if(hit == OBJECT2) // Torus
    {
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        vec3 baseCol = vec3(clamp(cos(hitAt.y),0.0,1.0), clamp(sin(hitAt.y),0.0,1.0),0.0);
        col =  0.3 * baseCol + 0.7 * baseCol * diff + vec3(1.0)*spec; 
    } 
    else if(hit == FLOOR_PLANE) // Floor
    {
        float ar = iResolution.x/iResolution.y;
        vec2 planeUV = vec2(80.0+hitAt.x,hitAt.z)/200.0;
        col = texture(iChannel0, planeUV).xyz; 
        //TODO: TASK3
        // ADD FLOOR PATTERNS
        col *= ((cos(hitAt.x / 2.0) + 1.0) / 2.0);

        vec3 secondColor = vec3(0.2, 0.7, 0.3);
        vec3 thirdColor = vec3(0.4, 0.1, 0.6); 
        vec3 hColor = secondColor + (thirdColor - secondColor) * ((sin(hitAt.x / 18.0) + 1.0) / 2.0);
		col = hColor + (col - hColor) * ((cos(hitAt.z / 2.0) + 1.0) / 2.0);

        float maxLength = 40.0;
        vec3 origin = vec3(cos(iTime) * 50.0, sin(iTime) * 50.0, 0.0);
        col = col * (min(maxLength/ length(hitAt - origin), 1.0));
    }

    else // NO HIT - BACKGROUND COLOR
    {
      col = 0.5 + 0.5 * cos(uv.xyx + vec3(0,2.3,3.5));
    }
    //TODO: TASK3
    //SCREEN SPACE EFFECTS
    vec3 grayCol = vec3((col.x + col.y + col.z) / 3.0);
    float sat = pow((fragCoord.x/iResolution.x), 2.8);
    col = grayCol + (col - grayCol) * sat;

   // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 randDisplacement() {
    return vec2(fract(sin(iTime*14237.14238)*23403.24234),
                fract(sin(iTime*35034.24809)*23473.42303));
}

vec2 gradient(float c) {
    float w1 = 1.0-mod(c, 0.02)*50.0;
    float bri;
    float sat = 1.0;
    if (w1<0.9) {
        bri = 1.0 - w1/0.9 + 0.2;
    }
    else {
        bri = (w1-0.9) / 0.1 + 0.2;
    }
    if (bri>1.0) {
        sat = 1.0 - (bri-1.0)/0.5;
    }
    return vec2(min(1.0, bri), sat);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv *= 0.7+0.3*sin(iTime*14.2354);
    uv.x *= iResolution.x / iResolution.y;
    float theta = iTime*10.0;
    mat2 rot = mat2 (cos(theta), sin(theta), -sin(theta), cos(theta));
    uv *= rot;
    uv += 0.4*randDisplacement();

    vec2 z = uv;
    vec2 c = vec2(-0.73+0.05*cos(30.5142*iTime+0.3),0.3+0.1*sin(10.0*iTime+0.9));

    float minDist = 100.0;
    float iterations = -1.0;
    for (float i = 0.0; i < 200.0; i++) {
        z = vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (abs(z.y)<minDist) {
            minDist = abs(z.y);
        }
        if (dot(z,z) > 256.0) {
            iterations = i;
            break;
        }
    }

    iterations = iterations + 2.0 - log(log(length(z)))/log(2.0);

    float hue = pow(iterations/50.0,0.4+0.2*sin(4.534243*iTime));
    hue += iTime*10.0;

    if (iterations >= 0.0) {
        vec2 brisat = gradient(hue);
		fragColor = vec4(hsv2rgb(vec3(hue,brisat.y,brisat.x)) ,1.0);
    }
    else {
        fragColor = vec4(min( vec3(1.0), (1.0-minDist*200.0)*vec3(1.0,1.2,2.0) ), 1.0);
    }
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

