// Reference:  https://thebookofshaders.com/11/?lan=fr
// noise
// Not so good, but ok 
float noise (float px, float pz) {
    vec2 st = vec2(px,pz);
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth

    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

vec3 getNormal( const vec3  p )
{
    return normalize( vec3( noise(p.x-EPSILON,p.z) - noise(p.x+EPSILON,p.z),
                            2.0*EPSILON,
                            noise(p.x,p.z-EPSILON) - noise(p.x,p.z+EPSILON) ) );
}


// Phong shading
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {

    vec3 N = getNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {

        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {

        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}


vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(50.0 * sin(iTime/2.0),
                          80.0,
                          50.0 * cos(iTime/2.0));

    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);


    return color;
}


bool castRay(vec3  ro,  vec3 rd, inout float resT)
{

    const float delt = 0.1;
    const float mint = 0.00;
    const float maxt = 20.0;
    for( float t = mint; t < maxt; t += delt )
    {
         vec3 p = ro + rd*t;
        if( p.y < 0.5*noise( p.x, p.z ) )
        {
            resT = t - 0.5*delt;
            return true;
        }
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0, 2, 0);
    float dist ;

    if (!castRay(eye, dir,dist)) {
        // Sky 
        vec3 col = 0.9*vec3(0.4,0.65,1.0) - dir.y*vec3(0.4,0.36,0.4);

        fragColor = vec4(col, 0.0);
		return;
    }

    // Terrain Color
    vec3 p = eye + dist * dir;
    vec3 K_a = vec3(0.2, 0.2, 0.2);
    vec3 K_d = vec3(0.7, 0.2, 0.2);
    vec3 K_s = vec3(0.0, 0.0, 0.0);
    float shininess = 0.0;   
    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

vec2 rot(vec2 p, float l){
	float s= sin(l), c= cos(l);
	return vec2(
		c*p.x - s*p.y,
		s*p.x + c*p.y
		);
}



