void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv= 2.0 * fragCoord / iResolution.xy - vec2(1.0,1.0);
	uv.x *= iResolution.x / iResolution.y;

	vec3 r;
	float generation= 0.2 * iTime;
	generation= floor(generation) + 0.5 * fract(generation);
	bool isChildFemale= mod(generation,2.0) < 1.0;
	float parentMove1= clamp( 20.0 * (mod(generation,1.0) - 0.26 ) , 0.0, 1.0 );
	float pairMove=    clamp( 15.0 * (mod(generation,1.0) - 0.30 ) , 0.0, 100.0 );
	float parentMove2= clamp( 15.0 * (mod(generation,1.0) - 0.35 ) , 0.0, 100.0 );

	if( false ){
	}else if(mod(generation,1.0) < 0.05 && ( length(rot(uv+vec2(0.15+0.6,0.13+0.2),0.5)) < 0.05 || length(rot(uv+vec2(0.15+0.6,0.13+0.2),0.5)-vec2(0,0.04)) < 0.05 ) ){
		/// infrant
		r= vec3(1);
	}else if( getWhite(uv-vec2(-0.8-parentMove2,-0.2) + parentMove1 * vec2(0.2,0.2), true, generation - 0.5 ) ){
		/// mum
		r= rc;
	}else if( getWhite(uv-vec2(-0.4-parentMove2,-0.2) + parentMove1 * vec2(-0.2,0.2), false, generation - 0.5 ) ){
		/// dad
		r= rc;
	}else if( getWhite(uv-vec2(-0.6 + clamp( 15.0 * (mod(generation,1.0) - 0.42 ) , 0.0, 1.0 )*(isChildFemale?-0.2:0.2),-0.2), isChildFemale, generation ) ){
		/// child
		r= rc;
	}else if( getWhite(uv-vec2(-0.6 + (isChildFemale?0.2:-0.2) - 10.0*clamp(0.4-mod(generation,1.0), 0.0, 1.0) , -0.2), !isChildFemale, generation ) ){
		/// pair
		r= rc;
	}else if( true && uv.x + uv.y < 1.8 && - uv.x + uv.y < 0.4 && 0.4 < uv.y && uv.y < 0.7  ){
		/// house roof
		r = vec3(.9,.1,.1);
	}else if( getWindow(uv-vec2(0.4,0.2)) ){
		/// window 1
		r = rc;
	}else if( getWindow(uv-vec2(1.0,0.2)) ){
		/// window 2
		r = rc;
	}else if( getWindow(uv-vec2(0.4,-0.25)) ){
		/// window 3
		r = rc;
	}else if( abs(uv.x-0.9) < 0.1 && abs(uv.y+0.3) < 0.2 ){
		/// door
		r = vec3(.5,.3,.1);
	}else if( abs(uv.x-0.7) < 0.6 && abs(uv.y) <= 0.5 ){
		/// house wall
		r = vec3(.7);
	}else if( + uv.x * uv.x * 0.04 +uv.y < -0.3 ){
		/// grass
		r = vec3(.3,.7,.2);
	}else{
		/// sky
		r = vec3(.5,.5,.9);
	}

	fragColor = vec4(r,0);
}

