float CombineSDF(vec3 p, inout int closest)
{
      vec3 pn = p*vec3(1.0,0.6,1.0);;
      pn =  Twist(pn+vec3(0,0,-0.3),sin(iTime)*0.5);//CreateRotationY(iTime)* vec4(p,0);
      float sd = SphereSDF(pn.xyz);  

      sd = max(BoxSDF(pn.xyz,vec3(0.95)),-sd); 
      float  torus = TorusSDF(CreateRotationY(iTime)*(p + CreateRotationY(iTime*0.3)*vec3(0.0,0.0,2.5)) ,0.9f,0.4f);
      sd = min(torus,sd);      
      float pd =  PlaneSDF(p,23.0); 
      float fin = min(sd,pd);
      closest = OBJECT;
      if(fin == pd)
      { closest = FLOOR_PLANE;}
      else if(fin == torus) 
      {closest = OBJECT2;}
      return fin;
}

vec3 GetNormal(vec3 p)
{
    int dummy = 0;
    return 
    normalize(vec3(
		CombineSDF(vec3(p.x + EPS, p.y, p.z), dummy) - CombineSDF(vec3(p.x - EPS, p.y, p.z), dummy),
		CombineSDF(vec3(p.x, p.y + EPS, p.z), dummy) - CombineSDF(vec3(p.x, p.y - EPS, p.z), dummy),
		CombineSDF(vec3(p.x, p.y, p.z + EPS), dummy) - CombineSDF(vec3(p.x, p.y, p.z - EPS), dummy)
		));
}

float Trace(Ray r,inout int hit)
{
    hit = 0;
    float t = 0.0f;

    for(int i =0; i < MAX_STEPS; ++i)
    {
       vec3 p = r.Origin + r.Direction * t;
       int closest = 0;     
       float sd = CombineSDF(p, closest);

       if(sd < EPS)
       {
          hit = closest;
          return t;
       }
       t += sd;
    }
    return 0.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Frag coord normalization 
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Make center of the screen be (0,0)
    uv = uv * 2.0 - 1.0; 
    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    //FOV
    float fovDist = tan(FOV_DEG  *0.5 * M_PI / 180.0); 
    uv *= fovDist;

    //TODO: TASK2
    //CHANGE CAMERA POSITION AND ORIENTATION HERE:

    Ray r;
    r.Direction = normalize(vec3(uv,1.0)*CreateRotationX(float(35.0/180.0*M_PI)));
    r.Origin = vec3(0,4.0,-5.0);

    int hit = NO_HIT;   // indicates type of the object hit
    float t = Trace(r,hit);  
    vec3 hitAt = r.Origin + r.Direction * t;
    vec3 N = GetNormal(hitAt);

    float diff,spec = 0.0;
    vec3 col = vec3(0); 
    float stm = sin(iTime);

    if(hit == OBJECT) // Block
    {        
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        col =  vec3(0.2,0.04,0.2)+ vec3(0.5,0.0,0.5) * diff + vec3(1.0) * spec;
    }
    else if(hit == OBJECT2) // Torus
    {
        Phong(LIGHT_DIR,N,-r.Direction,50.0,diff,spec);
        vec3 baseCol = vec3(clamp(cos(hitAt.y),0.0,1.0), clamp(sin(hitAt.y),0.0,1.0),0.0);
        col =  0.3 * baseCol + 0.7 * baseCol * diff + vec3(1.0)*spec; 
    } 
    else if(hit == FLOOR_PLANE) // Floor
    {
        float ar = iResolution.x/iResolution.y;
        vec2 planeUV = vec2(80.0+hitAt.x,hitAt.z)/200.0;
        vec3 uplines = vec3(pow(1.0-sin(hitAt.x / 3.0f), 0.5f));
        float ctm = float(cos(iTime));
        float sidetime = 0.4f+(stm/3.0f);
        float sidefloat = pow(1.0-sin(hitAt.z/5.0f), 2.0f)+0.3f + sidetime;
        vec3 sidelines = vec3(sidefloat, sidetime, sidefloat);
        col = texture(iChannel0, planeUV).xyz*uplines*sidelines*20.0/length(vec2(pow(sin(iTime), 2.0)*hitAt.x, pow(cos(iTime), 2.0)*hitAt.z));

        // ADD FLOOR PATTERNS

    }

    else // NO HIT - BACKGROUND COLOR
    {
      col = 0.5 + 0.5 * cos(uv.xyx + vec3(0,2.3,3.5));
    }
    //TODO: TASK3
    //SCREEN SPACE EFFECTS

   // Output to screen
    //fragColor = vec4(col, 1.0);
    float graphscalar = pow((fragCoord.x/iResolution.x),2.8);
    vec3 saturation = (1.0-graphscalar) * vec3(length(col));
    vec3 blackwhite = graphscalar * col;
    fragColor = vec4((saturation+blackwhite)/2.0,1.0);
}

// Atari logo - Raymarched by Del 25/11/2018

float atari(vec2 p)
{
    p*=0.15;
    vec2 b = vec2(0.08,0.5);
    vec2 v1 = abs(p)-b;
    float d1 = length(max(v1,vec2(0))) + min(max(v1.x,v1.y),0.0);

    p.x = -abs(p.x);
	p+=vec2(0.25,0.0);

    float c = smoothstep(0.0, 1.0, pow(clamp(-p.y, 0.0, 1.0), 1.2));
    p.x +=  c;
    b.x += c*0.1;
    v1 = abs(p)-b;
    float d2 = length(max(v1,vec2(0))) + min(max(v1.x,v1.y),0.0);
    return min(d1,d2)*8.0;
}


mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}
#define	TAU 6.28318


float map(vec3 p)
{
    float time2 = iTime-0.25;
    float twist = 0.5+sin(fract(time2*0.55)*TAU)*0.5;
    twist *= p.y * 0.125;
    p.xz *= rotate(twist+fract(time2*0.2)*TAU);

	float dist = atari(p.xy);

    float dep = 0.5;
    vec2 e = vec2( dist, abs(p.z) - dep );
    dist = min(max(e.x,e.y),0.0) + length(max(e,0.0));
    dist -= 0.225;		// rounding
    return dist;
}


// Tetrahedral normal, to save a couple of "map" calls. Courtesy of IQ.
vec3 normal( in vec3 p )
{
    // Note the slightly increased sampling distance, to alleviate
    // artifacts due to hit point inaccuracies.
    vec2 e = vec2(0.0025, -0.0025); 
    return normalize(
        e.xyy * map(p + e.xyy) + 
        e.yyx * map(p + e.yyx) + 
        e.yxy * map(p + e.yxy) + 
        e.xxx * map(p + e.xxx));
}

// Smooth HSV to RGB conversion 
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}
vec3 render(vec2 uv)
{
    float time1 = iTime-1.15;
	vec3 ro = vec3(0.0, 0.0, -13.0);
	vec3 rd = normalize(vec3(uv, 1.95));
	vec3 p = vec3(0.0);
	float t = 0.;
	for (int i = 0; i < 240; i++)
    {
		p = ro + rd * t;
		float d = map(p);
		if (d < .001 || t > 30.) break;
		t += d *0.5;
	}

    vec3 c = vec3(0.35,0.35,0.45);
    c*= 1.2-abs(uv.y);

    if (t<30.0)
    {
  	   	vec3 lightDir = normalize(vec3(10.0, 13.5, -13.0));

        vec3 nor = normal(p);

        float dif = max(dot(nor, lightDir), 0.0);

        float h = iTime*0.25-p.y*0.09;
        vec3 c1 = hsv2rgb_smooth(vec3(h,1.0,1.3));

        float tf = 0.1;
        c1 += reflect(vec3(p.x*tf, p.y*tf, 0.0), nor);
        c1 *= dif;


        vec3 ref = reflect(rd, nor);
        float spe = max(dot(ref, lightDir), 0.0);
        vec3 spec = vec3(2.0) * pow(spe, 16.);
        c1 = c1 + spec;
		vec3 c2 = c + spec;			// ghostly background + just spec

 		float bl = sin(fract(time1/12.0) * TAU);
        bl = smoothstep(0.0, 1.0, bl);
        c = mix(c1,c2,bl);

    }

    c *= 1.0 - 0.3*length(uv);
	return c;
}

