float intersect (Ray ray, Sphere sphere){
    vec3 toCamDir = ray.origin - sphere.center;

    float proj = dot(toCamDir, ray.direction);
    float d = proj * proj - dot(toCamDir, toCamDir) + sphere.radius * sphere.radius;

    if (d < 0.0) return ray.far;

	float dist = -proj - sqrt(d);
	if (dist < 0.0)  dist = -proj + sqrt(d);

	if (dist < ray.near) return ray.far;

	return dist;
} 
//Пересечение луча с плоскостью
float intersect(Ray ray, Plane plane) {

    float dist = dot(plane.normal, ray.direction);
	if (dist==0.) return ray.far;

	dist = dot(plane.center - ray.origin, plane.normal) / dist;

	if (dist < ray.near) return ray.far;

	return dist;
}
//Карта расстояний до объектов сцены
float map (in Ray ray, out Mapdata mapdata) {
