	float d;
	vec3 p; //Центр
	Plane plane;
	Sphere sphere;

	//Центральный шар
	p = vec3(0,0,0);
	sphere = Sphere(p, 3.);
	d = intersect(ray, sphere);
	if (mapdata.distance > d) {
		mapdata.distance 	= d;
		mapdata.sphere 		= sphere;
		mapdata.material 	= metal_reflect;
		mapdata.id 			= 1;
	}

	p = vec3(0,0,0);
	translate(p, vec3(0,7,7));
	rotate(p, vec3(1,0,0)*(u_time/2.));
	for (float i=0.; i<6.; i++) {
		//Материал шара
		Material material;
		if (mod(i,3.)==0.) material = red_mat;
		if (mod(i,3.)==1.) material = green_mat;
		if (mod(i,3.)==2.) material = blue_mat;
		rotate(p, vec3(0,1,0)*(PI/3.));
		sphere = Sphere(p, 2.);
		d = intersect(ray, sphere);
		if (mapdata.distance > d) {
			mapdata.distance 	= d;
			mapdata.sphere 		= sphere;
			mapdata.material 	= material;
			mapdata.id 			= 1;
		}
	}

	p = vec3(0,0,0);
	translate(p, vec3(7,-7,0));
	rotate(p, vec3(0,0,1)*(u_time/2.));
	for (float i=0.; i<6.; i++) {
		//Материал шара
		Material material;
		if (mod(i,3.)==0.) material = red_reflect;
		if (mod(i,3.)==1.) material = green_reflect;
		if (mod(i,3.)==2.) material = blue_reflect;
		rotate(p, vec3(0,1,0)*(PI/3.));
		sphere = Sphere(p, 2.);
		d = intersect(ray, sphere);
